INSERT INTO `locales_page_text` VALUES ('2654', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">军阶概述</H1><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRankAlliance\" align=\"left\"/><BR/>\r\n<BR/><BR/>\r\n<P align=\"right\">以下是</P><BR/>\r\n<P align=\"right\">联盟的</P><BR/>\r\n<P align=\"right\">军阶概述，</P><BR/>\r\n<P align=\"right\">其中包括</P><BR/>\r\n<P align=\"right\">部落</P><BR/>\r\n<P align=\"right\">种族熟知的</P><BR/>\r\n<P align=\"right\">信息。每一部分都</P><BR/>\r\n<P align=\"right\">包含两个</P><BR/>\r\n<P align=\"right\">章节，军官和士兵的军阶，都是按照由高到低的顺序排列的。联盟万岁!</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">軍階概述</H1><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRankAlliance\" align=\"left\"/><BR/>\r\n<BR/><BR/>\r\n<P align=\"right\">以下是</P><BR/>\r\n<P align=\"right\">聯盟的</P><BR/>\r\n<P align=\"right\">軍階概述，</P><BR/>\r\n<P align=\"right\">其中包括</P><BR/>\r\n<P align=\"right\">部落</P><BR/>\r\n<P align=\"right\">種族熟知的</P><BR/>\r\n<P align=\"right\">信息。每一部分都</P><BR/>\r\n<P align=\"right\">包含兩個</P><BR/>\r\n<P align=\"right\">章節，軍官和士兵的軍階，都是按照由高到低的順序排列的。聯盟萬歲!</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2655', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟士兵军阶</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">总元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">战场元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">指挥官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">少校</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML> ', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">總元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">戰場元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">指揮官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">少校</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML> ', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2656', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟士兵军阶</H1><BR/><BR/>\r\n<P align=\"center\">第二部分</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上尉骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中尉骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">骑士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">第二部分</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上尉騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中尉騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">騎士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2657', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落军官军阶</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官长</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">下士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落軍官軍階</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官長</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">下士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2658', null, null, null, '<HTML><BR/><BODY><BR/><H1 align=\"center\">部落士兵军阶</H1><BR/><BR/><P align=\"center\">Part 1</P><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/><P align=\"right\">高阶督军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/><P align=\"right\">督军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/><P align=\"right\">将军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/><P align=\"right\">中将</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/><P align=\"right\">勇士</P><BR/><BR/><BR/></BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">高階督軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">督軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">將軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中將</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">勇士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2659', null, null, null, '当你凝视着眼珠时，脑海中便浮现出战争的场景。$B$B似乎这场可怕的冲突发生在洛丹伦的某处。$B$B在尸横遍野的战场上，隐约可见一名女性，独自与浩如烟海的天灾军团作战。她显得那么绝望无助。$B$B从她手中施放的光芒瞬间便砍杀数百名不死生物。', '當你凝視著眼珠時，腦海中便浮現出戰爭的場景。$B$B似乎這場可怕的衝突發生在洛丹倫的某處。$B$B在屍橫遍野的戰場上，隱約可見一名女性，獨自與浩如煙海的天災軍團作戰。她顯得那麼絕望無助。$B$B從她手中施放的光芒瞬間便砍殺數百名不死生物。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2660', null, null, null, '一切似乎都是徒劳。很快女祭司周围就堆满了农夫的尸体……倒下的尸体又站起来了。', '一切似乎都是徒勞。很快女祭司周圍就堆滿了農夫的屍體……倒下的屍體又站起來了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2661', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟与部落的军阶</H1><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRankHorde\"/>align=\"left\"/><BR/>\r\n<BR/><BR/>\r\n<P align=\"right\">(兽人语)你好!在这本</P><BR/>\r\n<P align=\"right\">书中</P><BR/>\r\n<P align=\"right\">列出了</P><BR/>\r\n<P align=\"righ\">部落和</P><BR/>\r\n<P align=\"right\">弱小的联盟的</P><BR/>\r\n<P align=\"right\">军阶列表。</P><BR/>\r\n<P align=\"right\">首先</P><BR/>\r\n<P align=\"right\">是部落军阶，接下来是联盟的，</P><BR/>\r\n<P align=\"right\">其中囊括了军官和军阶列表。按等级的高低排列，等级最高的排在首位，等级低的列于下位。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟與部落的軍階</H1><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRankHorde\"/>align=\"left\"/><BR/>\r\n<BR/><BR/>\r\n<P align=\"right\">(獸人語)你好!在這本</P><BR/>\r\n<P align=\"right\">書中</P><BR/>\r\n<P align=\"right\">列出了</P><BR/>\r\n<P align=\"righ\">部落和</P><BR/>\r\n<P align=\"right\">弱小的聯盟的</P><BR/>\r\n<P align=\"right\">軍階列表。</P><BR/>\r\n<P align=\"right\">首先</P><BR/>\r\n<P align=\"right\">是部落軍階，接下來是聯盟的，</P><BR/>\r\n<P align=\"right\">其中囊括了軍官和軍階列表。按等級的高低排列，等級最高的排在首位，等級低的列於下位。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2662', null, null, null, '<HTML><BR/><BODY><BR/><H1 align=\"center\">部落军官军阶</H1><BR/><BR/><P align=\"center\">Part 1</P><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/><P align=\"right\">高阶督军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/><P align=\"right\">督军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/><P align=\"right\">将军</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/><P align=\"right\">中将</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/><P align=\"right\">勇士</P><BR/><BR/><BR/></BODY><BR/></HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落軍官軍階</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">高階督軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">督軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">將軍</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中將</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">勇士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2663', null, null, null, '<HTML><BR/><BODY><BR/><H1 align=\"center\">部落士兵军阶</H1><BR/><BR/><P align=\"center\">Part 2</P><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/><P align=\"right\">百夫长</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/><P align=\"right\">军团士兵</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/><P align=\"right\">血卫士</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/><P align=\"right\">石卫士</P><BR/><BR/><BR/></BODY><BR/></HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">Part 2</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">百夫長</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">軍團士兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">血衛士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">石衛士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2664', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落军官军阶</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">资深士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">侦察兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落軍官軍階</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">資深士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">偵察兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2665', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟士兵军阶</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">总元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">战场元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">元帅</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">指挥官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">少校</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">Part 1</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank14\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">總元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank13\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">戰場元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank12\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">元帥</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank11\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">指揮官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank10\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">少校</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2666', null, null, null, '一切似乎都是徒劳。很快女祭司周围就堆满了农夫的尸体……倒下的尸体又站起来了。', '一切似乎都是徒勞。很快女祭司周圍就堆滿了農夫的屍體……倒下的屍體又站起來了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2671', null, null, null, '迷人的西蒙妮：$B$B西蒙妮正在污染安戈洛环形山。不要被她的伪装所迷惑。小心翼翼地接近她，向她发起挑战。', '迷人的西蒙妮：$B$B\r\n西蒙妮正在污染安戈洛環形山。不要被她的偽裝所迷惑。小心翼翼地接近她，向她發起挑戰。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2672', null, null, null, '疯狂的克林弗兰：$B$B克林弗兰在燃烧平原上游荡。谨慎地接近他，迫使他现出原形。', '瘋狂的克林弗蘭：$B$B\r\n克林弗蘭在燃燒平原上遊蕩。謹慎地接近他，迫使他現出原形。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2673', null, null, null, '屠杀者索伦诺尔：$B$B屠杀者索伦诺尔就潜伏在希利苏斯的废土上。他伪装得很巧妙，而且非常危险。接近他，令他现出真正的面目。', '屠殺者索倫諾爾：$B$B\r\n屠殺者索倫諾爾就潛伏在希利蘇斯的廢土上。他偽裝得很巧妙，而且非常危險。接近他，令他現出真正的面目。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2674', null, null, null, '摧毁者阿托留斯：$B$B冬泉谷中的那些被阿托留斯接触过的生物全都被他毁灭了。找到他，杀死他。', '摧毀者阿托留斯：$B$B\r\n冬泉谷中的那些被阿托留斯接觸過的生物全都被他毀滅了。找到他，殺死他。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2675', null, null, null, '猎人，你要多加小心，那些恶魔都不会贸然行事。如果它们觉得自己没有绝对的胜算，就会选择逃跑。$B$B你一定要独自行动。', '獵人，你要多加小心，那些惡魔都不會貿然行事。如果它們覺得自己沒有絕對的勝算，就會選擇逃跑。$B$B\r\n你一定要獨自行動。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2676', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟军阶</H1><BR/><BR/>\r\n<P align=\"center\">军官</P><BR/><BR/>\r\n<P align=\"center\">总元帅</P><BR/>\r\n<P align=\"center\">战场元帅</P><BR/>\r\n<P align=\"center\">元帅</P><BR/>\r\n<P align=\"center\">指挥官</P><BR/>\r\n<P align=\"center\">少校</P><BR/>\r\n<P align=\"center\">一等骑士</P><BR/>\r\n<P align=\"center\">上尉骑士</P><BR/>\r\n<P align=\"center\">中尉骑士</P><BR/>\r\n<P align=\"center\">骑士</P><BR/><BR/>\r\n<P align=\"center\">士兵</P><BR/><BR/>\r\n<P align=\"center\">士官长</P><BR/>\r\n<P align=\"center\">上士</P><BR/>\r\n<P align=\"center\">士官</P><BR/>\r\n<P align=\"center\">下士</P><BR/>\r\n<P align=\"center\">士兵</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟軍階</H1><BR/><BR/>\r\n<P align=\"center\">軍官</P><BR/><BR/>\r\n<P align=\"center\">總元帥</P><BR/>\r\n<P align=\"center\">戰場元帥</P><BR/>\r\n<P align=\"center\">元帥</P><BR/>\r\n<P align=\"center\">指揮官</P><BR/>\r\n<P align=\"center\">少校</P><BR/>\r\n<P align=\"center\">一等騎士</P><BR/>\r\n<P align=\"center\">上尉騎士</P><BR/>\r\n<P align=\"center\">中尉騎士</P><BR/>\r\n<P align=\"center\">騎士</P><BR/><BR/>\r\n<P align=\"center\">士兵</P><BR/><BR/>\r\n<P align=\"center\">士官長</P><BR/>\r\n<P align=\"center\">上士</P><BR/>\r\n<P align=\"center\">士官</P><BR/>\r\n<P align=\"center\">下士</P><BR/>\r\n<P align=\"center\">士兵</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2677', null, null, null, '<HTML><BR/><BODY><BR/><H1 align=\"center\">部落军阶</H1><BR/><BR/><P align=\"center\">OFFICERS</P><BR/><BR/><P align=\"center\">高阶督军</P><BR/><P align=\"center\">督军</P><BR/><P align=\"center\">将军</P><BR/><P align=\"center\">中将</P><BR/><P align=\"center\">勇士</P><BR/><P align=\"center\">百夫长</P><BR/><P align=\"center\">军团士兵</P><BR/><P align=\"center\">血卫士</P><BR/><P align=\"center\">石卫士</P><BR/><BR/><P align=\"center\">士兵</P><BR/><BR/><P align=\"center\">一等士官</P><BR/><P align=\"center\">资深士官</P><BR/><P align=\"center\">士官</P><BR/><P align=\"center\">步兵</P><BR/><P align=\"center\">侦察兵</P><BR/></BODY><BR/></HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落軍階</H1><BR/><BR/>\r\n<P align=\"center\">OFFICERS</P><BR/><BR/>\r\n<P align=\"center\">高階督軍</P><BR/>\r\n<P align=\"center\">督軍</P><BR/>\r\n<P align=\"center\">將軍</P><BR/>\r\n<P align=\"center\">中將</P><BR/>\r\n<P align=\"center\">勇士</P><BR/>\r\n<P align=\"center\">百夫長</P><BR/>\r\n<P align=\"center\">軍團士兵</P><BR/>\r\n<P align=\"center\">血衛士</P><BR/>\r\n<P align=\"center\">石衛士</P><BR/><BR/>\r\n<P align=\"center\">士兵</P><BR/><BR/>\r\n<P align=\"center\">一等士官</P><BR/>\r\n<P align=\"center\">資深士官</P><BR/>\r\n<P align=\"center\">士官</P><BR/>\r\n<P align=\"center\">步兵</P><BR/>\r\n<P align=\"center\">偵察兵</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2691', null, null, null, '要束缚恐惧战马，你必须做三件事：$B$B制造强力召唤法阵。$B$B在法阵中打开通往克索诺斯的传送门，将恐惧战马引入我们的世界。$B$B击败恐惧战马并控制它的灵魂。$B$B下一页将详细描述要如何执行这些步骤。这可不是一件容易的事，但是你是有能力完成它。只要集中精神运用你的所有技能，我相信恐惧战马会属于你的。$B$B继续读下去吧。$B- 莫苏尔·召血者', '要束縛恐懼戰馬，你必須做三件事：$B$B\r\n製造強力召喚法陣。$B$B\r\n在法陣中打開通往克索諾斯的傳送門，將恐懼戰馬引入我們的世界。$B$B\r\n擊敗恐懼戰馬並控制它的靈魂。$B$B\r\n下一頁將詳細描述要如何執行這些步驟。這可不是一件容易的事，但是你是有能力完成它。只要集中精神運用你的所有技能，我相信恐懼戰馬會屬於你的。$B$B\r\n繼續讀下去吧。$B\r\n- 莫蘇爾·召血者', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2692', null, null, null, '仪式道具$B$B在开始任务之前，你必须拥有以下魔法道具：$B$B耶维尔的瓶子$B一块黑色磁石$B克索诺斯雕文$B我的仆人戈瑟奇会为你提供这些道具，但是你得付钱。如果你还没有集齐所有的道具，就不要试图开始仪式。每件道具都是必需的。$B$B在下一页，我将描述道具的使用方法。', '儀式道具$B$B\r\n在開始任務之前，你必須擁有以下魔法道具：$B$B\r\n耶維爾的瓶子$B\r\n一塊黑色磁石$B\r\n克索諾斯雕文$B\r\n我的僕人戈瑟奇會為你提供這些道具，但是你得付錢。如果你還沒有集齊所有的道具，就不要試圖開始儀式。每件道具都是必需的。$B$B\r\n在下一頁，我將描述道具的使用方法。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2693', null, null, null, '强力召唤法阵$B$B必须在魔法强大的地方创造出强力召唤法阵。在埃德萨拉斯的废墟深处，就有这样一块地方，也叫做厄运之槌。在埃德萨拉斯关押着拥有强大力量的生物，伊莫塔尔。创造法阵的地方就在关押他的监狱底座上。$B$B杀进厄运之槌吧，取回耶维尔的瓶子。', '強力召喚法陣$B$B\r\n必須在魔法強大的地方創造出強力召喚法陣。在埃德薩拉斯的廢墟深處，就有這樣一塊地方，也叫做厄運之槌。在埃德薩拉斯關押著擁有強大力量的生物，伊莫塔爾。創造法陣的地方就在關押他的監獄底座上。$B$B\r\n殺進厄運之槌吧，取回耶維爾的瓶子。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2694', null, null, null, '铃铛、车轮和蜡烛$B$B释放耶维尔之后，他将布置铃铛、车轮和蜡烛，为你构建法阵。仪式随即开始，你必须保持警惕，这些道具会引导出强大的魔法能量，而且很容易失效。在整个仪式失败之前，你必须快速使用黑色磁石来重新启动失效的道具。$B$B如果三件道具都失效的话，仪式也就失败了，你就不得不从头再来。', '鈴鐺、車輪和蠟燭$B$B\r\n釋放耶維爾之後，他將佈置鈴鐺、車輪和蠟燭，為你構建法陣。儀式隨即開始，你必須保持警惕，這些道具會引導出強大的魔法能量，而且很容易失效。在整個儀式失敗之前，你必須快速使用黑色磁石來重新啟動失效的道具。$B$B\r\n如果三件道具都失效的話，儀式也就失敗了，你就不得不從頭再來。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2695', null, null, null, '为了引导出仪式所需的能量，铃铛、车轮和蜡烛都具备独有的属性。$B$B达斯莫拉的铃铛，一旦铃响，可赋予术士活力和能量的法阵。$B$B黑暗战车之轮，一旦旋转，可保护法阵中的人物免受伤害。$B$B末日蜡烛，一旦燃烧，可对进入法阵的敌人造成可怕的伤害。$B$B因此在仪式中保持三种物品的正常运作是至关重要的。', '為了引導出儀式所需的能量，鈴鐺、車輪和蠟燭都具備獨有的屬性。$B$B\r\n達斯莫拉的鈴鐺，一旦鈴響，可賦予術士活力和能量的法陣。$B$B\r\n黑暗戰車之輪，一旦旋轉，可保護法陣中的人物免受傷害。$B$B\r\n末日蠟燭，一旦燃燒，可對進入法陣的敵人造成可怕的傷害。$B$B\r\n因此在儀式中保持三種物品的正常運作是至關重要的。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2696', null, null, null, '用于重新启动铃铛、车轮和蜡烛的黑色磁石需要灵魂碎片方可生效，这点你需要牢记。每次使用黑石磁石启动一件仪式道具时，都需要消耗一片灵魂碎片。因此，在开始仪式之前，你必须收集大量的灵魂碎片。', '用於重新啟動鈴鐺、車輪和蠟燭的黑色磁石需要靈魂碎片方可生效，這點你需要牢記。每次使用黑石磁石啟動一件儀式道具時，都需要消耗一片靈魂碎片。因此，在開始儀式之前，你必須收集大量的靈魂碎片。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2697', null, null, null, '完成仪式$B$B你可在法阵边缘查看魔法符文。当九个符文全部出现时，仪式便结束了，你将看见巨大的能量从新生的法阵中涌出。$B$B你可以在那里使用克索诺斯雕文，由此打开通往克索诺斯的传送门，引出一匹恐惧战马。$B$B打败恐惧战马，释放它的灵魂。奴役这个灵魂，你就可以随心所欲地召唤恐惧战马了。', '完成儀式$B$B\r\n你可在法陣邊緣查看魔法符文。當九個符文全部出現時，儀式便結束了，你將看見巨大的能量從新生的法陣中湧出。$B$B\r\n你可以在那裡使用克索諾斯雕文，由此打開通往克索諾斯的傳送門，引出一匹恐懼戰馬。$B$B\r\n打敗恐懼戰馬，釋放它的靈魂。奴役這個靈魂，你就可以隨心所欲地召喚恐懼戰馬了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2731', null, null, null, '<HTML><BODY><BR/><BR/><P align=\"left\">长眠于此的是霜狼氏族的第一任酋长，我们敬仰的部落酋长索尔的父亲-杜洛坦。他被那些想要让我们永远受到奴役的同胞背叛。杜洛坦为了我们的自由而献出了自己的生命，我们敬仰他，以及他留给我们的珍贵遗产，他伟大的儿子。</P><BR/><P align=\"left\">德雷克塔尔，霜狼氏族的先知 </P></BODY></HTML>', '<HTML><BODY><BR/><BR/><P align=\"left\">長眠於此的是霜狼氏族的第一任酋長，我們敬仰的部落酋長索爾的父親-杜洛坦。他被那些想要讓我們永遠受到奴役的同胞背叛。杜洛坦為了我們的自由而獻出了自己的生命，我們敬仰他，以及他留給我們的珍貴遺產，他偉大的兒子。</P><BR/><P align=\"left\">德雷克塔爾，霜狼氏族的先知 </P></BODY></HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2770', null, null, null, '', '<HTML> <BODY> <H1 align=\"center\">HORDE MILITARY RANKS</H1><BR/> <P align=\"center\">OFFICERS</P><BR/> <P align=\"center\">High Warlord</P> <P align=\"center\">Warlord</P> <P align=\"center\">General</P> <P align=\"center\">Lieutenant General</P> <P align=\"center\">Champion</P> <P align=\"center\">Centurion</P> <P align=\"center\">Legionnaire</P> <P align=\"center\">Blood Guard</P> <P align=\"center\">Stone Guard</P><BR/> <P align=\"center\">ENLISTED</P><BR/> <P align=\"center\">First Sergeant</P> <P align=\"center\">Senior Sergeant</P> <P align=\"center\">Sergeant</P> <P align=\"center\">Grunt</P> <P align=\"center\">Scout</P> </BODY> </HTML> ', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2771', null, null, null, '剥洋葱  关于瓦解雷矛部族的指南  - 德雷克塔尔着    本指南记录了大量战术策略知识。有学识的士兵总是依靠丰富的作战经验行事。本书将成为奥特兰克山谷的战场指南。', '剝洋蔥  關於瓦解雷矛部族的指南  - 德雷克塔爾著    本指南記錄了大量戰術策略知識。有學識的士兵總是依靠豐富的作戰經驗行事。本書將成為奧特蘭克山谷的戰場指南。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2772', null, null, null, '第一章：前线    瓦解雷矛军队跟剥洋葱很相似。你必须一层层地除去洋葱外皮，才能得到洋葱的嫩心。    主要由雷矛巡山人和联盟哨兵组成的前线阵营都归上尉管制。一旦前线受到攻击，巴琳达·斯通赫尔斯上尉将带领前线阵营加强防守。攻击上尉，前线阵营即可崩溃。', '第一章：前線    瓦解雷矛軍隊跟剝洋蔥很相似。你必須一層層地除去洋蔥外皮，才能得到洋蔥的嫩心。    主要由雷矛巡山人和聯盟哨兵組成的前線陣營都歸上尉管制。一旦前線受到攻擊，巴琳達·斯通赫爾斯上尉將帶領前線陣營加強防守。攻擊上尉，前線陣營即可崩潰。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2773', null, null, null, '第二章：双塔    别搞错了，范达尔·雷矛是个狡猾的敌人，他绝不是傻子。攻破前线之后，你还需要突破第二道防线。那就是控制着丹巴达尔之外的雷矛卫兵和雷矛巡逻兵的石炉和冰翼双塔。', '第二章：雙塔    別搞錯了，范達爾·雷矛是個狡猾的敵人，他絕不是傻子。攻破前線之後，你還需要突破第二道防線。那就是控制著丹巴達爾之外的雷矛衛兵和雷矛巡邏兵的石爐和冰翼雙塔。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2774', null, null, null, '士兵，小心，双塔之上戒备森严，范达尔的精英指挥官驻守在这儿。一旦你的军队突破防线，请确保塔中的指挥官已经……永远地沉默了。这将是瓦解雷矛部队的第二步。', '士兵，小心，雙塔之上戒備森嚴，范達爾的精英指揮官駐守在這兒。一旦你的軍隊突破防線，請確保塔中的指揮官已經……永遠地沉默了。這將是瓦解雷矛部隊的第二步。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2775', null, null, null, '第三章：四名指挥官    雷矛洋葱的第三层由四名指挥官组成。范达尔无非是模仿了我们霜狼的防御构造罢了。当然，他是不会承认这一点的……瞧，我离题了。    四名指挥官负责指挥守护丹巴达尔的所有雷矛卫兵。杀死他们，雷矛卫兵就会溃不成军了。    接下来便只剩最后一层了。', '第三章：四名指揮官    雷矛洋蔥的第三層由四名指揮官組成。范達爾無非是模仿了我們霜狼的防禦構造罷了。當然，他是不會承認這一點的……瞧，我離題了。    四名指揮官負責指揮守護丹巴達爾的所有雷矛衛兵。殺死他們，雷矛衛兵就會潰不成軍了。    接下來便只剩最後一層了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2776', null, null, null, '第四章：丹巴达尔兵营    毫无疑问地，你已经注意到雷矛的防御布局是为了防止强大的力量摧毁整个军队。是的，这正是我们自己的军队结构，我是不会改变自己的防御布局的。他们模仿我们的布局，我们为什么要改变呢？', '第四章：丹巴達爾兵營    毫無疑問地，你已經注意到雷矛的防禦佈局是為了防止強大的力量摧毀整個軍隊。是的，這正是我們自己的軍隊結構，我是不會改變自己的防禦佈局的。他們模仿我們的佈局，我們為什麼要改變呢？', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2777', null, null, null, '我讲到哪儿了？哦，是的，丹巴达尔兵营（位于丹巴达尔内）驻扎有范达尔最信任的精英雷矛防御者。摧毁双塔，消灭那些企图增援范达尔的军队。', '我講到哪兒了？哦，是的，丹巴達爾兵營（位於丹巴達爾內）駐紮有范達爾最信任的精英雷矛防禦者。摧毀雙塔，消滅那些企圖增援范達爾的軍隊。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2778', null, null, null, '尾声    至此，洋葱只剩下嫩心：孤身一人、毫无抵抗能力的范达尔·雷矛。在杀死他之前，我们必须按照军规要求他弃械投降。当你看到他时请记得提醒他这一点……否则就杀死他。', '尾聲    至此，洋蔥只剩下嫩心：孤身一人、毫無抵抗能力的范達爾·雷矛。在殺死他之前，我們必須按照軍規要求他棄械投降。當你看到他時請記得提醒他這一點……否則就殺死他。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2779', null, null, null, '霜狼洋葱：雷矛的光荣事迹$B：范达尔·雷矛着$B$B霜狼是什么?答案很简单：霜狼就是那些企图摧毁我们伟大的帝国的野蛮人。$B$B霜狼跟洋葱非常相似，霜狼设立了层层防御力量；一旦外层被剥离，就会露出嫩心，这样我们便可以就着蒜汁食用了。$B$B太美味了!', '霜狼洋蔥：雷矛的光榮事跡$B\r\n：范達爾·雷矛著$B$B\r\n霜狼是什麼?答案很簡單：霜狼就是那些企圖摧毀我們偉大的帝國的野蠻人。$B$B\r\n霜狼跟洋蔥非常相似，霜狼設立了層層防禦力量；一旦外層被剝離，就會露出嫩心，這樣我們便可以就著蒜汁食用了。$B$B\r\n太美味了!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2780', null, null, null, '第一章：前线$B$B霜狼战士构成了霜狼的前线防御部队。加尔范上尉在冰血要塞统领着所有霜狼战士。要摧毁这层防御，最有效的方法是占领冰血要塞。$B$B包围要塞，杀死加尔范上尉，前线将在顷刻之间崩溃。', '第一章：前線$B$B\r\n霜狼戰士構成了霜狼的前線防禦部隊。加爾范上尉在冰血要塞統領著所有霜狼戰士。要摧毀這層防禦，最有效的方法是佔領冰血要塞。$B$B\r\n包圍要塞，殺死加爾范上尉，前線將在頃刻之間崩潰。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2781', null, null, null, '第二章：冰雪和哨塔高地$B$B一旦首层防御被摧毁，“洋蓟”的第二层防御便暴露无遗。破坏前线附近的霜狼哨塔将削弱企图增援的军团和巡逻兵。$B$B哨塔部有重兵把守，塔内驻扎着德雷克塔尔的精英指挥官。必须杀死这些指挥官。这将加速瓦解霜狼防御力量的进程。', '第二章：冰雪和哨塔高地$B$B\r\n一旦首層防禦被摧毀，「洋薊」的第二層防禦便暴露無遺。破壞前線附近的霜狼哨塔將削弱企圖增援的軍團和巡邏兵。$B$B\r\n哨塔部有重兵把守，塔內駐紮著德雷克塔爾的精英指揮官。必須殺死這些指揮官。這將加速瓦解霜狼防禦力量的進程。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2782', null, null, null, '第三章：四名指挥官$B$B如果你遵循该指南行事，那么现在，你已经杀死了德雷克塔尔的两名指挥官。干得好，士兵!他们的信心正在瓦解。你必须杀死剩下的两名指挥官，完成命令。$B$B一旦四名指挥官阵亡，保卫霜狼要塞的霜狼军团将溃不成军，因为他们失去了战斗指挥的主心骨。现在是时候了!', '第三章：四名指揮官$B$B\r\n如果你遵循該指南行事，那麼現在，你已經殺死了德雷克塔爾的兩名指揮官。幹得好，士兵!他們的信心正在瓦解。你必須殺死剩下的兩名指揮官，完成命令。$B$B\r\n一旦四名指揮官陣亡，保衛霜狼要塞的霜狼軍團將潰不成軍，因為他們失去了戰鬥指揮的主心骨。現在是時候了!\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2783', null, null, null, '第四章：东部和西部霜狼哨塔$B$B现在你离胜利只有一步之遥啦；德雷克塔尔最信任和最强大的守卫军队：霜狼守卫者。$B$B霜狼守卫者驻守在东部和西部霜狼哨塔中。摧毁哨塔，看看那些四散逃跑的可耻的霜狼守卫者吧。', '第四章：東部和西部霜狼哨塔$B$B\r\n現在你離勝利只有一步之遙啦；德雷克塔爾最信任和最強大的守衛軍隊：霜狼守衛者。$B$B\r\n霜狼守衛者駐守在東部和西部霜狼哨塔中。摧毀哨塔，看看那些四散逃跑的可恥的霜狼守衛者吧。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2784', null, null, null, '尾声$B$B“将蒜汁黄油和蛋黄酱抹在洋蓟嫩心上，喝上一瓶陈年烈酒，这真是顿美餐啊。”$B$B德雷克塔尔身边只剩下两个愚蠢的随从啦。占领霜狼急救站，杀死德雷克塔尔。那时，这片土地就完全属于强大的雷矛啦。', '尾聲$B$B\r\n「將蒜汁黃油和蛋黃醬抹在洋薊嫩心上，喝上一瓶陳年烈酒，這真是頓美餐啊。」$B$B\r\n德雷克塔爾身邊只剩下兩個愚蠢的隨從啦。佔領霜狼急救站，殺死德雷克塔爾。那時，這片土地就完全屬於強大的雷矛啦。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2785', null, null, null, '我们请求你，赞达拉的赞札。$B$B祝福那些请求你的帮助的人吧，赞札。祝福那些跟赞达拉人民结盟的人。$B$B在这关键时刻帮助我们。赐予我们力量，打败敌人，再次消灭哈卡。', '我们请求你，赞达拉的赞札。$B$B祝福那些请求你的帮助的人吧，赞札。祝福那些跟赞达拉人民结盟的人。$B$B在这关键时刻帮助我们。赐予我们力量，打败敌人，再次消灭哈卡。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2786', null, null, null, '荆棘谷钓鱼大赛!$B时间：周日下午2点到4点。$B$B在荆棘谷的海岸边寻找可口鱼群!记得向可口鱼群中投放鱼钩，这样你就能钓到特殊的鱼儿，而不是荆棘谷常见的鱼。最快钓到40条可口鱼的人可将它们交到藏宝海湾的钓鱼大师布鲁吉尔处换取奖励!$B$B即使你不是最快的那个，也可以用它们换取奖金。如果你能钓到特殊的鱼儿，还可以获得神秘大奖!', '荊棘谷釣魚大賽!$B\r\n時間：週日下午2點到4點。$B$B\r\n在荊棘谷的海岸邊尋找可口魚群!記得向可口魚群中投放魚鉤，這樣你就能釣到特殊的魚兒，而不是荊棘谷常見的魚。最快釣到40條可口魚的人可將它們交到藏寶海灣的釣魚大師布魯吉爾處換取獎勵!$B$B\r\n即使你不是最快的那個，也可以用它們換取獎金。如果你能釣到特殊的魚兒，還可以獲得神秘大獎!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2787', null, null, null, '他的名字为黑暗的艾泽拉斯带来了光明。$B$B他的勇气推翻了痛苦的高墙。$B$B白银之手将继承骑士的事业，为世界造福。', '他的名字為黑暗的艾澤拉斯帶來了光明。$B$B他的勇氣推翻了痛苦的高牆。$B$B白銀之手將繼承騎士的事業，為世界造福。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2788', null, null, null, '他既不醉心于名望，$B$B亦不想以此获得统治权。$B$B相反，他选择为洛丹伦而战$B$B只是希望他的同胞不再卷入战事。', '他既不醉心於名望，$B$B亦不想以此獲得統治權。$B$B相反，他選擇為洛丹倫而戰$B$B只是希望他的同胞不再捲入戰事。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2789', null, null, null, '这位神圣的骑士的悲剧$B$B不会因他的逝去而终结。$B$B我们尊敬他，感谢他!$B$B乌瑟尔永远代表着荣耀和光明!', '這位神聖的騎士的悲劇$B$B不會因他的逝去而終結。$B$B我們尊敬他，感謝他!$B$B烏瑟爾永遠代表著榮耀和光明!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2790', null, null, null, '他的名字不会褪色。$B他的奉献精神将$B永远为后人指明方向。$B$B曾经的枷锁再也无法$B阻止我们表达对他的尊敬。', '他的名字不會褪色。$B他的奉獻精神將$B永遠為後人指明方向。$B$B曾經的枷鎖再也無法$B阻止我們表達對他的尊敬。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2791', null, null, null, '你能听到他的咆哮吗？$B一声惊醒部落的怒吼：$B胜利或是阵亡！$B$B我们必须牢记$B他在死亡面前爆发的力量。$B他的梦想终成现实。', '你能聽到他的咆哮嗎？$B一聲驚醒部落的怒吼：$B勝利或是陣亡！$B$B我們必須牢記$B他在死亡面前爆發的力量。$B他的夢想終成現實。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2792', null, null, null, '危机四伏。$B敌人想让我们$B$B再次受缚。$B$B在战斗时，让我们$B$B怀念葛罗·地狱吼$B他完成了自己的使命。$B永远的葛罗·地狱吼！', '危機四伏。$B敵人想讓我們$B$B再次受縛。$B$B在戰鬥時，讓我們$B$B懷念葛羅·地獄吼$B他完成了自己的使命。$B永遠的葛羅·地獄吼！', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2793', null, null, null, '从流沙之战到现在也将近一千年了。在这场战争中，希利苏斯沙漠的中心发生了一个重大的悲剧，而沉重的负担让范达尔·鹿盔苦恼不已。人们相信就是这些事件造成了大德鲁伊最后令人厌恶的性格。', '從流沙之戰到現在也將近一千年了。在這場戰爭中，希利蘇斯沙漠的中心發生了一個重大的悲劇，而沉重的負擔讓范達爾·鹿盔苦惱不已。人們相信就是這些事件造成了大德魯伊最後令人厭惡的性格。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2794', null, null, null, '一支古老，敏锐的种族－－其拉，想要争夺卡利姆多大陆的统治权。而自负、专制的鹿盔和他的夜精灵军队正式与它们展开了激战。', '一支古老，敏銳的種族－－其拉，想要爭奪卡利姆多大陸的統治權。而自負、專制的鹿盔和他的夜精靈軍隊正式與它們展開了激戰。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2795', null, null, null, '为了控制希利苏斯中大量出现的昆虫潮，不分男女老少都激烈地奋战。从安其拉的堡垒中，似乎永无止尽的异种蝎持续壮大着入侵的其拉军队。整个希利苏斯都被包围了。', '為了控制希利蘇斯中大量出現的昆蟲潮，不分男女老少都激烈地奮戰。從安其拉的堡壘中，似乎永無止盡的異種蠍持續壯大著入侵的其拉軍隊。整個希利蘇斯都被包圍了。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2796', null, null, null, '基于一个鹿盔自己都会后悔的决定，他将年轻的鹿盔，瓦尔斯坦派去防守夜精灵的前哨基地南风村。遗憾地，瓦尔斯坦在南风村败给安其拉的军队那天也失去了性命。', '基於一個鹿盔自己都會後悔的決定，他將年輕的鹿盔，瓦爾斯坦派去防守夜精靈的前哨基地南風村。遺憾地，瓦爾斯坦在南風村敗給安其拉的軍隊那天也失去了性命。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2797', null, null, null, '抱着无畏及坚定的决心，想复仇的范达尔·鹿盔继续计划着一切。希利苏斯的战败会导致夜精灵种族重大的损失，甚至可能会失去整个世界。$B$B战火延烧了好几个月。整个希利苏斯都被其拉军侵袭了。鹿盔和他的军队被逼回塔纳利斯。在大批军队被毁灭和无数无辜生命被屠杀之后，范达尔·鹿盔一直以来的自负被彻底地熄灭了。', '抱著無畏及堅定的決心，想復仇的范達爾·鹿盔繼續計劃著一切。希利蘇斯的戰敗會導致夜精靈種族重大的損失，甚至可能會失去整個世界。$B$B\r\n戰火延燒了好幾個月。整個希利蘇斯都被其拉軍侵襲了。鹿盔和他的軍隊被逼回塔納利斯。在大批軍隊被毀滅和無數無辜生命被屠殺之後，范達爾·鹿盔一直以來的自負被徹底地熄滅了。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2798', null, null, null, '他的骄傲被完全地粉碎，这是鹿盔第二次在人生中感到彻底的恐惧。于是，在塔纳利斯，鹿盔找到了安纳克罗斯－－诺兹多姆之子，并请求青铜龙军团的协助。', '他的驕傲被完全地粉碎，這是鹿盔第二次在人生中感到徹底的恐懼。於是，在塔納利斯，鹿盔找到了安納克羅斯－－諾茲多姆之子，並請求青銅龍軍團的協助。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2799', null, null, null, '一开始，安纳克罗斯拒绝让青铜龙军团卷入次要种族的事务之中。直到其拉莽撞地攻击了时光洞穴，安纳克罗斯才同意加入夜精灵的军队。', '一開始，安納克羅斯拒絕讓青銅龍軍團捲入次要種族的事務之中。直到其拉莽撞地攻擊了時光洞穴，安納克羅斯才同意加入夜精靈的軍隊。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2800', null, null, null, '安纳克罗斯很快的了解到即使是强大的青铜龙军团也无法阻挠其拉军队的入侵。每当他一口气杀掉五十只虫兵，就会有一百只等着替补它们的位置。随着夜精灵阵线越来越衰弱，这个威胁将会蔓延至更北方的菲拉斯荒野的迹象就越来越清楚的浮现，安纳克罗斯决定是时候该让守护巨龙之子们都意识到这个情况。', '安納克羅斯很快的瞭解到即使是強大的青銅龍軍團也無法阻撓其拉軍隊的入侵。每當他一口氣殺掉五十隻蟲兵，就會有一百隻等著替補它們的位置。隨著夜精靈陣線越來越衰弱，這個威脅將會蔓延至更北方的菲拉斯荒野的跡象就越來越清楚的浮現，安納克羅斯決定是時候該讓守護巨龍之子們都意識到這個情況。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2801', null, null, null, '他首先召唤了伊瑟拉的军团。翡翠梦境的麦琳瑟拉回应了他的召唤，接着雅立史卓莎和玛里苟斯的军团也被唤醒。', '他首先召喚了伊瑟拉的軍團。翡翠夢境的麥琳瑟拉回應了他的召喚，接著雅立史卓莎和瑪裡苟斯的軍團也被喚醒。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2802', null, null, null, '龙群在希利苏斯上空的最高处碰头：在安其拉堡垒上空的几千尺之上。$B$B安纳克罗斯是时间之王诺兹多姆之子。$B$B翡翠梦境则有麦琳瑟拉，自然之王伊瑟拉的孩子。$B$B凯雷斯特拉兹，生命之王雅立史卓莎的孩子，代表红龙军团。$B$B亚雷戈斯是魔法之王玛里苟斯的孩子，代表蓝龙军团。', '龍群在希利蘇斯上空的最高處碰頭：在安其拉堡壘上空的幾千尺之上。$B$B\r\n安納克羅斯是時間之王諾茲多姆之子。$B$B\r\n翡翠夢境則有麥琳瑟拉，自然之王伊瑟拉的孩子。$B$B\r\n凱雷斯特拉茲，生命之王雅立史卓莎的孩子，代表紅龍軍團。$B$B\r\n亞雷戈斯是魔法之王瑪裡苟斯的孩子，代表藍龍軍團。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2803', null, null, null, '巨龙们看着其拉虫人和异种虫的大军源源不断地从安其拉涌出。他们明白，没有谁能终止这场战争。在神庙中，还有数以万计的虫类士兵严阵以待。随着时间的流逝，它们的军队数量还在继续膨胀，它们全部都蠢蠢欲动，想要投身血腥的杀戮。', '巨龍們看著其拉蟲人和異種蟲的大軍源源不斷地從安其拉湧出。他們明白，沒有誰能終止這場戰爭。在神廟中，還有數以萬計的蟲類士兵嚴陣以待。隨著時間的流逝，它們的軍隊數量還在繼續膨脹，它們全部都蠢蠢欲動，想要投身血腥的殺戮。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2804', null, null, null, '亚雷戈斯是第一个发现奇怪能量的人。从安其拉南边四分之一处庞大的神庙里放射出一丝微暗的魔法，是他们从未自其拉虫上感受到的。他们尽全力尝试，却没有人可以穿过神庙找到这股能量的来源。只有巨大的力量才能这样完整地保护它自己。或许……有某样东西正在控制着这些昆虫。有某样东西甚至还正控制着其拉虫类。', '亞雷戈斯是第一個發現奇怪能量的人。從安其拉南邊四分之一處龐大的神廟裡放射出一絲微暗的魔法，是他們從未自其拉蟲上感受到的。他們盡全力嘗試，卻沒有人可以穿過神廟找到這股能量的來源。只有巨大的力量才能這樣完整地保護它自己。或許……有某樣東西正在控制著這些昆蟲。有某樣東西甚至還正控制著其拉蟲類。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2805', null, null, null, '龙群知道他们必须尽快做出回应，以免世界又要经历另一次史诗的大灾难。经过审慎的深思熟虑后，他们决定最好的行动方式就是阻止这些东西来源或其附近的军队。', '龍群知道他們必須盡快做出回應，以免世界又要經歷另一次史詩的大災難。經過審慎的深思熟慮後，他們決定最好的行動方式就是阻止這些東西來源或其附近的軍隊。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2806', null, null, null, '他们决定使用夜精灵和巨龙军团的本源建立一个巨大的屏障。一个可以遏制异种和安其拉城内的最高统治者，还可以防止未来的侵略的魔法屏障。安其拉将会因此成为一所监狱。', '他們決定使用夜精靈和巨龍軍團的本源建立一個巨大的屏障。一個可以遏制異種和安其拉城內的最高統治者，還可以防止未來的侵略的魔法屏障。安其拉將會因此成為一所監獄。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2807', null, null, null, '第1天$B我们的旅行结束。我们终于到达希利苏斯，也就是我们要建立联系的地方。我们来自许多地方，要一起为不朽的事业通力合作。$B$B空气中弥漫着一股兴奋。明天我们就要将所有的精力拿来建造与“他们”进行沟通的东西。$B$B我们将在明天建造一座风石!', '第1天$B\r\n我們的旅行結束。我們終於到達希利蘇斯，也就是我們要建立聯繫的地方。我們來自許多地方，要一起為不朽的事業通力合作。$B$B\r\n空氣中瀰漫著一股興奮。明天我們就要將所有的精力拿來建造與「他們」進行溝通的東西。$B$B\r\n我們將在明天建造一座風石!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2808', null, null, null, '第2天$B建造风石的位置引起了极大的争议。就连我们之中最聪明的人也无法百分之百确定，古老的预言里所指的确切位置究竟是哪里。$B$B我跟原来的北边族群站在同一阵线，而时间绝对会证明我们是对的。说能量线会贯穿这个位置的不利角度实在是愚蠢又无知。$B$B我们早已积极地在收集建构需要的材料。', '第2天$B\r\n建造風石的位置引起了極大的爭議。就連我們之中最聰明的人也無法百分之百確定，古老的預言裡所指的確切位置究竟是哪裡。$B$B\r\n我跟原來的北邊族群站在同一陣線，而時間絕對會證明我們是對的。說能量線會貫穿這個位置的不利角度實在是愚蠢又無知。$B$B\r\n我們早已積極地在收集建構需要的材料。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2809', null, null, null, '第15天$B我们的付出有了代价。我们是第一个完成风石建构的军营。如果其他那些笨蛋跟我们一起，我们就可以在一个星期之内完成这项工程!$B$B明天我们会开始召唤沃库仑大人的仪式。他要求第一个来测试系统，我们无法反驳他，因为他很容易生气。$B$B我希望他会对我们很满意。', '第15天$B\r\n我們的付出有了代價。我們是第一個完成風石建構的軍營。如果其他那些笨蛋跟我們一起，我們就可以在一個星期之內完成這項工程!$B$B\r\n明天我們會開始召喚沃庫侖大人的儀式。他要求第一個來測試系統，我們無法反駁他，因為他很容易生氣。$B$B\r\n我希望他會對我們很滿意。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2810', null, null, null, '暮光之锤紧急通告：$B$B寻找背叛者奥泰尔的过程进展的相当顺利。我们的侦察兵已缩小范围到西部荒野的洞穴群。$B$B奥泰尔的背叛不能被原谅，而他的逃脱显示了我们组织的安全性有极大的漏洞。$B$B任何被发现藏有奥泰尔消息的人，将会受到适当的处分。$B$B- 暮光之王艾沃兰', '暮光之錘緊急通告：$B$B\r\n尋找背叛者奧泰爾的過程進展的相當順利。我們的偵察兵已縮小範圍到西部荒野的洞穴群。$B$B\r\n奧泰爾的背叛不能被原諒，而他的逃脫顯示了我們組織的安全性有極大的漏洞。$B$B\r\n任何被發現藏有奧泰爾消息的人，將會受到適當的處分。$B$B\r\n- 暮光之王艾沃蘭', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2811', null, null, null, '暮光之锤紧急通告：$B$B就目前希利苏斯事务状况而言，所有真实信仰者文件的发送都必须有不低于四组的武装暮光之锤人员护航。$B$B任何被发现违反此新政策的成员将会立刻被惩罚。$B$B- 暮光之王德萨克', '暮光之錘緊急通告：$B$B\r\n就目前希利蘇斯事務狀況而言，所有真實信仰者文件的發送都必須有不低於四組的武裝暮光之錘人員護航。$B$B\r\n任何被發現違反此新政策的成員將會立刻被懲罰。$B$B\r\n- 暮光之王德薩克', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2812', null, null, null, '暮光之锤紧急通告：$B$B以下资讯描述和我们的深渊议会的主人们沟通的风石之使用方法。$B$B任何对风石没有意义的使用都将被处以最严厉的惩罚。$B$B- 暮光守护者哈弗斯', '暮光之錘緊急通告：$B$B\r\n以下資訊描述和我們的深淵議會的主人們溝通的風石之使用方法。$B$B\r\n任何對風石沒有意義的使用都將被處以最嚴厲的懲罰。$B$B\r\n- 暮光守護者哈弗斯', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2813', null, null, null, '深渊圣殿骑士$B$B身为一个新加入者，你可以与深渊圣殿骑士建立联系以进行例行仪式。记得永远在使用风石时穿着适宜的服装，以免启动我们的防御魔法。$B$B当预料之外的紧急事件发生时，若没有任何监督者在现场的话，你被允许可以召唤深渊圣殿骑士。$B$B想知道更详细的召唤仪式可以参考上个月的《真实信仰者》。', '深淵聖殿騎士$B$B\r\n身為一個新加入者，你可以與深淵聖殿騎士建立聯繫以進行例行儀式。記得永遠在使用風石時穿著適宜的服裝，以免啟動我們的防禦魔法。$B$B\r\n當預料之外的緊急事件發生時，若沒有任何監督者在現場的話，你被允許可以召喚深淵聖殿騎士。$B$B\r\n想知道更詳細的召喚儀式可以參考上個月的《真實信仰者》。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2814', null, null, null, '深渊公爵$B$B惟有那些持有身份勋章的侍僧能与深渊公爵建立联系，只有身份勋章的持有者本人才能使用它。$B$B如果暮光之锤的成员未经许可擅自与深渊公爵联系或使用身份勋章，我将亲自惩罚违规者。', '深淵公爵$B$B\r\n惟有那些持有身份勳章的侍僧能與深淵公爵建立聯繫，只有身份勳章的持有者本人才能使用它。$B$B\r\n如果暮光之錘的成員未經許可擅自與深淵公爵聯繫或使用身份勳章，我將親自懲罰違規者。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2815', null, null, null, '高阶深渊议会的首领$B$B只有暮光之王能与斯卡德诺克斯王子、大元帅维拉希斯、卡苏姆男爵或斯古恩男爵联系。$B$B任何情况下，低于暮光之王等级的任何暮光之锤成员都不能与高阶议会建立联系。若发现任何外来人员使用风石，他将立即被化为灰烬。', '高階深淵議會的首領$B$B\r\n只有暮光之王能與斯卡德諾克斯王子、大元帥維拉希斯、卡蘇姆男爵或斯古恩男爵聯繫。$B$B\r\n任何情況下，低於暮光之王等級的任何暮光之錘成員都不能與高階議會建立聯繫。若發現任何外來人員使用風石，他將立即被化為灰燼。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2816', null, null, null, '教派的现状，第92卷$B$B衷心地祝福你，我的兄弟，教派目前的状况对我们来说是最最重要的。我们是一支军团，我们的目标越来越接近实现。$B$B新召集的成员正在不断聚集。赞美我们主的歌声出现在黑海岸，他们充满幻想地在灼热峡谷鞠躬，他们信奉主人派往黑暗深渊的仆人，阿库麦尔。事实上，我的兄弟和姐妹，我们所有具有这种真正信仰的人，将会遍布艾泽拉斯!', '教派的現狀，第92卷$B$B\r\n衷心地祝福你，我的兄弟，教派目前的狀況對我們來說是最最重要的。我們是一支軍團，我們的目標越來越接近實現。$B$B\r\n新召集的成員正在不斷聚集。讚美我們主的歌聲出現在黑海岸，他們充滿幻想地在灼熱峽谷鞠躬，他們信奉主人派往黑暗深淵的僕人，阿庫麥爾。事實上，我的兄弟和姐妹，我們所有具有這種真正信仰的人，將會遍佈艾澤拉斯!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2817', null, null, null, '教派的现状，第127卷$B$B这真是一个令人高兴的一天!我们在希利苏斯接受特训，而且最终通过了这次特训!$B$B报告说我们最北面营地的毁灭将会挑战不忠实者之心，但是这次屠杀不会被视为一次悲剧。决不!', '教派的現狀，第127卷$B$B\r\n這真是一個令人高興的一天!我們在希利蘇斯接受特訓，而且最終通過了這次特訓!$B$B\r\n報告說我們最北面營地的毀滅將會挑戰不忠實者之心，但是這次屠殺不會被視為一次悲劇。決不!\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2818', null, null, null, '被一名高阶深渊议会的成员溅洒我们的鲜血，这是一种无上的祝福!我们必须羡慕我们倒下的朋友，他们的灵魂正安息在一头最荣耀的野兽的胃中!$B$B赞美上古之神!赞美他们的仆人!赞美暮光之锤!', '被一名高階深淵議會的成員濺灑我們的鮮血，這是一種無上的祝福!我們必須羨慕我們倒下的朋友，他們的靈魂正安息在一頭最榮耀的野獸的胃中!$B$B\r\n讚美上古之神!讚美他們的僕人!讚美暮光之錘!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2819', null, null, null, '教派的现状，第233卷$B$B机会再次降临，我的兄弟姐妹!塞纳里奥议会，这个邪恶的自然爱好者组织现在派人来对抗我们。他们一定想要阻止我们来召唤红色的引诱石。我们决不允许他们这么做!$B$B红色的石头对于我们新的联盟来说非常重要。我们绝对不能让那些塞纳里奥议会的蠢货挡路。团结起来，我忠诚的朋友。团结起来!', '教派的現狀，第233卷$B$B\r\n機會再次降臨，我的兄弟姐妹!塞納裡奧議會，這個邪惡的自然愛好者組織現在派人來對抗我們。他們一定想要阻止我們來召喚紅色的引誘石。我們決不允許他們這麼做!$B$B\r\n紅色的石頭對於我們新的聯盟來說非常重要。我們絕對不能讓那些塞納裡奧議會的蠢貨擋路。團結起來，我忠誠的朋友。團結起來!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2820', null, null, null, '我们怎么知道他们爱我们?$B$B有些人可能要问，上古之神和他们的手下怎么会爱我们?他们难道不是经常杀戮我们吗?如此恶毒的生物的心中怎么会有爱?$B$B对于这个问题只有一个答案，简单得都不需要说出来?', '我們怎麼知道他們愛我們?$B$B\r\n有些人可能要問，上古之神和他們的手下怎麼會愛我們?他們難道不是經常殺戮我們嗎?如此惡毒的生物的心中怎麼會有愛?$B$B\r\n對於這個問題只有一個答案，簡單得都不需要說出來?\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2821', null, null, null, '信仰。信仰是所有给予我们臣服的快感的源泉。信仰可以让我们在希利苏斯最寒冷的夜晚感到温暖。信仰让我们在末日将临的时候感受我们主的仁慈。$B$B信仰将会拯救我们。', '信仰。信仰是所有給予我們臣服的快感的源泉。信仰可以讓我們在希利蘇斯最寒冷的夜晚感到溫暖。信仰讓我們在末日將臨的時候感受我們主的仁慈。$B$B\r\n信仰將會拯救我們。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2822', null, null, null, '魔法账本', '魔法賬本', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2828', null, null, null, '<HTML>\r\n<BODY>\r\n<IMG src=\"图形接口21037_crudemap_256\"/>\r\n</BODY>\r\n</HTML>', '<HTML>\r\n<BODY>\r\n<IMG src=\"Interface\\Pictures\\21037_crudemap_256\"/>\r\n</BODY>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2829', null, null, null, '亲爱的日记，$B$B今天，我的头号敌人 - 纳里安，企图派他的密友在他处以死刑的场地欺骗我。他的死刑。你相信吗?$B$B我历经千辛万苦才想出这个恶魔般的计划，要把他从那个该死的茅屋里引诱出来而他却这样做?算了……我太生气，不想写了。下次再说。', '親愛的日記，$B$B\r\n今天，我的頭號敵人 - 納裡安，企圖派他的密友在他處以死刑的場地欺騙我。他的死刑。你相信嗎?$B$B我歷經千辛萬苦才想出這個惡魔般的計劃，要把他從那個該死的茅屋裡引誘出來而他卻這樣做?算了……我太生氣，不想寫了。下次再說。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2830', null, null, null, '亲爱的日记，$B$B我在愤怒中离开了藏身之处。我真的很生气。我决定重新设计奴仆们的制服以反映我的不安。我现在觉得好多了但是有个东西不见了。我马上就回来……', '親愛的日記，$B$B\r\n我在憤怒中離開了藏身之處。我真的很生氣。我決定重新設計奴僕們的制服以反映我的不安。我現在覺得好多了但是有個東西不見了。我馬上就回來……\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2831', null, null, null, '亲爱的日记，$B$B好了，我现在很好。我把一个佣人打到哭的跟个小女孩一样。$B$B嗯……我现在觉得有点糟。等一下!该死的，我是个邪恶的天才!邪恶，你懂吗?我对自己真是太失望了。下次再说。', '親愛的日記，$B$B\r\n好了，我現在很好。我把一個傭人打到哭的跟個小女孩一樣。$B$B嗯……我現在覺得有點糟。等一下!該死的，我是個邪惡的天才!邪惡，你懂嗎?我對自己真是太失望了。下次再說。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2832', null, null, null, '亲爱的日记，$B$B我给了他一个拥抱并告诉他要继续努力工作。我现在觉得好多了。这是我们之间的秘密，日记。$B$B我上次说到哪了?喔，对，纳里安!哼!!!我恨那个家伙。毕竟，就是纳里安摧毁了所有我们在修补诺姆瑞根学院当学生时的测验曲线。$B$B他们会把我赶出学校吗?就我看来，诺姆瑞根已经得到它该有的教训了!$B$B今天就写到这里。好好睡，日记!', '親愛的日記，$B$B\r\n我給了他一個擁抱並告訴他要繼續努力工作。我現在覺得好多了。這是我們之間的秘密，日記。$B$B我上次說到哪了?喔，對，納裡安!哼!!!我恨那個傢伙。畢竟，就是納裡安摧毀了所有我們在修補諾姆瑞根學院當學生時的測驗曲線。$B$B他們會把我趕出學校嗎?就我看來，諾姆瑞根已經得到它該有的教訓了!$B$B今天就寫到這裡。好好睡，日記!\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2833', null, null, null, '亲爱的日记，$B$B我一定是老了。我完全忘记我跟你说了纳里安和他的诡计的事。总之，当我看到扮成纳里安的诱饵，我真的很生气所以就命令二号把那个笨蛋给杀了!$B$B我进入飞行器里开始撕毁他宝贵、愚蠢、无知的龙语书。真是爱炫耀!谁会读龙语啊?我真是痛恨他!', '親愛的日記，$B$B\r\n我一定是老了。我完全忘記我跟你說了納裡安和他的詭計的事。總之，當我看到扮成納裡安的誘餌，我真的很生氣所以就命令二號把那個笨蛋給殺了!$B$B我進入飛行器裡開始撕毀他寶貴、愚蠢、無知的龍語書。真是愛炫耀!誰會讀龍語啊?我真是痛恨他!\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2834', null, null, null, '亲爱的日记，$B$B抱歉，我睡着了。然后我就一边愤怒的开着飞行器，一边把那愚蠢的书一章章丢出去。我看到我下方的暗语峡谷里有个恶魔，便决定要将第一章变成魔法的纸龙卷风，把它的身体碎成好几百万片!这是解放。魔法 + 纸张 =乐趣。$B$B这让我非常开心所以我决定飞到东部王国。我的第一站是熔火之心。纳里安总是一直讲那边的事!“…熔火之心怎样怎样，拉格纳罗斯如何如何…”', '親愛的日記，$B$B\r\n抱歉，我睡著了。然後我就一邊憤怒的開著飛行器，一邊把那愚蠢的書一章章丟出去。我看到我下方的暗語峽谷裡有個惡魔，便決定要將第一章變成魔法的紙龍捲風，把它的身體碎成好幾百萬片!這是解放。魔法 + 紙張 =樂趣。$B$B這讓我非常開心所以我決定飛到東部王國。我的第一站是熔火之心。納裡安總是一直講那邊的事!「…熔火之心怎樣怎樣，拉格納羅斯如何如何…」\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2835', null, null, null, '你知道吗?如果他那么喜欢它，或许他会去熔火之心试着把他的书拼凑起来，然后从一堆灰烬中让那些章节再复活过来!哈!$B$B当我还在邻近地区的时候，我发现有一些在奈法利斯实验室的技术人员可能会用到这个龙语的东西。把炸弹拿开!', '你知道嗎?如果他那麼喜歡它，或許他會去熔火之心試著把他的書拼湊起來，然後從一堆灰燼中讓那些章節再復活過來!哈!$B$B當我還在鄰近地區的時候，我發現有一些在奈法利斯實驗室的技術人員可能會用到這個龍語的東西。把炸彈拿開!\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2836', null, null, null, '已经开始变成一种运动了。我真的撕这该死的书撕到上瘾了!$B$B接下来就简单了。$B$B幽暗城，恶臭腐烂死人的家，可能有着我吃过最棒的辣椒，这样是一章。暴风城，起司的首都，另一章。日记，你有把暴风城的起司拿来放在幽暗城的辣椒上过吗?嘿?这应该听起来有点警惕作用。', '已經開始變成一種運動了。我真的撕這該死的書撕到上癮了!$B$B接下來就簡單了。$B$B幽暗城，惡臭腐爛死人的家，可能有著我吃過最棒的辣椒，這樣是一章。暴風城，起司的首都，另一章。日記，你有把暴風城的起司拿來放在幽暗城的辣椒上過嗎?嘿?這應該聽起來有點警惕作用。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2837', null, null, null, '我开始对这个地方感到厌倦，所以我把飞行器掉头往回家的路走，但是在那之前我还有两个地方要去。我载了卡札克和他的恶魔们一程，到腐烂之痕(他最讨厌我这样做)然后又去了女伯爵奥妮克希亚的烟囱。$B$B累了。去睡了。', '我開始對這個地方感到厭倦，所以我把飛行器掉頭往回家的路走，但是在那之前我還有兩個地方要去。我載了卡札克和他的惡魔們一程，到腐爛之痕(他最討厭我這樣做)然後又去了女伯爵奧妮克希亞的煙囪。$B$B累了。去睡了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2838', null, null, null, '敬启者，$B$B我真爱我们玩的这些游戏。$B$B我只能猜想，如果你正在阅读这封信，你一定是因为奇迹或神的介入才击败了我。很荒谬，我知道，但是更奇怪的事都在这个世界发生过。', '敬啟者，$B$B\r\n我真愛我們玩的這些遊戲。$B$B\r\n我只能猜想，如果你正在閱讀這封信，你一定是因為奇跡或神的介入才擊敗了我。很荒謬，我知道，但是更奇怪的事都在這個世界發生過。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2839', null, null, null, '伴随生命中所有事物，这个哀伤传说中的悲剧、混乱和邪恶，都有着光荣、银色的内衬。当你杀了我，因为你的动作是如此的缓慢无序，让我有足够的时间先摧毁你拯救世界唯一的希望再写下这封信。粉碎的红水晶粉末是你的权杖碎片唯一留下的东西。\n', '伴隨生命中所有事物，這個哀傷傳說中的悲劇、混亂和邪惡，都有著光榮、銀色的內襯。當你殺了我，因為你的動作是如此的緩慢無序，讓我有足夠的時間先摧毀你拯救世界唯一的希望再寫下這封信。粉碎的紅水晶粉末是你的權杖碎片唯一留下的東西。\n\r\n\r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2840', null, null, null, '享用你在我贵重的收藏品中发现的复仇和怒风装备吧。相信我说的：我很高兴做了我父亲没有做的事……$B$B$B由衷的敬上，$B$B奈法利斯$B\"奈法利安\"', '享用你在我貴重的收藏品中發現的復仇和怒風裝備吧。相信我說的：我很高興做了我父親沒有做的事……$B$B\r\n$B\r\n由衷的敬上，$B$B\r\n奈法利斯$B\r\n\"奈法利安\"', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2841', null, null, null, '谨代表熏木牧场，再次感谢你的调查。我们不确定梅特森在谁的手里，但两个都去调查也许会是个不错的开始！$B$B时间就是金钱，朋友，我们可以解决这个案子的时间又少得非常宝贵。快点加把劲去解救它，因为这些团体都不是容易对付的。$B$B总之，接下来的2页就是我们收到的勒索信。祝你好运……', '謹代表熏木牧場，再次感謝你的調查。我們不確定梅特森在誰的手裡，但兩個都去調查也許會是個不錯的開始！$B$B時間就是金錢，朋友，我們可以解決這個案子的時間又少得非常寶貴。快點加把勁去解救它，因為這些團體都不是容易對付的。$B$B總之，接下來的2頁就是我們收到的勒索信。祝你好運……\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2842', null, null, null, '如果你们想要麋鹿活着回来，你们就得赶快准备好赎金。南海海盗不会好心到让你们延迟付款，我们也知道对“你们”的象征意义有多么珍贵。$B$B带1000金到塔纳利斯的落帆海湾。任何企图拯救这只麋鹿的行为，都会让梅特森变成桌上的菜肴。$B$B哈哈……$B$B南海海盗。', '如果你們想要麋鹿活著回來，你們就得趕快準備好贖金。南海海盜不會好心到讓你們延遲付款，我們也知道對「你們」的象徵意義有多麼珍貴。$B$B帶1000金到塔納利斯的落帆海灣。任何企圖拯救這只麋鹿的行為，都會讓梅特森變成桌上的菜餚。$B$B哈哈……$B$B南海海盜。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2843', null, null, null, '你们那珍贵的宠物在其热无比的灼热峡谷表现的不太好……你们最好聪明点，不要延迟完成我们的要求。$B$B你们必须带给我们5颗星红宝石和总数700金－－将两者置于同一个不加注记的素面包裹里。在一周内把包裹放在通往石坝小径的门前。$B$B若没有达到我们的要求，梅特森就会变成黑铁矮人冬幕节的主菜。$B$B嗯……麋鹿……', '你們那珍貴的寵物在其熱無比的灼熱峽谷表現的不太好……你們最好聰明點，不要延遲完成我們的要求。$B$B你們必須帶給我們5顆星紅寶石和總數700金－－將兩者置於同一個不加注記的素面包裹裡。在一周內把包裹放在通往石壩小徑的門前。$B$B若沒有達到我們的要求，梅特森就會變成黑鐵矮人冬幕節的主菜。$B$B嗯……麋鹿……\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2844', null, null, null, '他那些猎犬来的比老鹰还要快，$B$B然后他吹了声口哨，大吼，依序喊着它们的名字；“$B$B现在，毛瑟！现在，奇尔顿！现在，乔登跟卡普兰！$B$B上，纳格！上，帕度！上，古德曼跟梅特森！$B$B到门廊的顶端！到墙顶！$B$B现在冲过去！冲过去！冲过去啊”', '他那些猎犬来的比老鹰还要快，$B$B然后他吹了声口哨，大吼，依序喊着它们的名字；“$B$B现在，毛瑟！现在，奇尔顿！现在，乔登跟卡普兰！$B$B上，纳格！上，帕度！上，古德曼跟梅特森！$B$B到门廊的顶端！到墙顶！$B$B现在冲过去！冲过去！冲过去啊”', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2846', null, null, null, '我衷心的期望透过我的努力研究可以发现一种魔术能量的补充来源，而它对我的人民而言是安全的。太阳之井消失后，我们必须想办法让生活继续，不向秘法魔法屈服。 $B$B我相信辛多雷的未来能再次成为大家钦羡亮丽的榜样! ', '我衷心的期望透過我的努力研究可以發現一種魔術能量的補充來源，而它對我的人民而言是安全的。太陽之井消失後，我們必須想辦法讓生活繼續，不向秘法魔法屈服。 \r\n\r\n我相信辛多雷的未來能再次成為大家欽羨亮麗的榜樣! \r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2847', null, null, null, '好运还没降临。我导入的法术一定要传导通过尖塔，不过我的确有一些有趣又吸引人的点子。我派我最聪明的学徒追随调查的路径，看看能发现什么。', '好運還沒降臨。我導入的法術一定要傳導通過尖塔，不過我的確有一些有趣又吸引人的點子。我派我最聰明的學徒追隨調查的路徑，看看能發現什麼。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2848', null, null, null, '我记笔记已经好一阵子了，但还是没啥进展希望。我昨天在逐日岛听到其中一位学生法兰德伦说他没有谨记良师忠告，而向痛苦屈服。 $B$B我得加把劲、加倍努力。 ', ' 我記筆記已經好一陣子了，但還是沒啥進展希望。我昨天在逐日島聽到其中一位學生法蘭德倫說他沒有謹記良師忠告，而向痛苦屈服。 \r\n\r\n我得加把勁、加倍努力。 ', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2849', null, null, null, '没事。我不会放弃任何一丝希望。我们包围了南方，而此刻辛多雷无法在这个魔法保护区放肆乱来。 $B$B假如我设计的全新方法成功的话，我就能顺利滤除邪恶、腐败法术中的「杂质」，我已经把法术力的资料藏起来。我得持续谨慎小心。', '沒事。我不會放棄任何一絲希望。我們包圍了南方，而此刻辛多雷無法在這個魔法保護區放肆亂來。 \r\n\r\n假如我設計的全新方法成功的話，我就能順利濾除邪惡、腐敗法術中的「雜質」，我已經把法術力的資料藏起來。我得持續謹慎小心。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2850', null, null, null, '太神奇了!我们成功了，我们所研究的魔法精髓数量虽然不是很多，我要停摆目前大部分学徒的实验，然后一起着手进行崭新、有前瞻希望的研究工作。 $B$B有了好运加持，我们应该要精炼研究过程，一石二鸟-我们全体要备足精力，以及迎接即将来临的恶魔法术难题。', '太神奇了!我們成功了，我們所研究的魔法精髓數量雖然不是很多，我要停擺目前大部分學徒的實驗，然後一起著手進行嶄新、有前瞻希望的研究工作。 \r\n\r\n有了好運加持，我們應該要精煉研究過程，一石二鳥-我們全體要備足精力，以及迎接即將來臨的惡魔法術難題。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2851', null, null, null, '不!当新实验程序开始时，我的主要学徒:泰勒塞翁，突然无预警的在我眼前萎缩，屈服成这种状态着实折磨了我的兄弟。我极力想制止，但他就这样离我远远而去。除了让他从苦痛中幸免外，我也没别的选择了。 $B$B而这是研究与发明的代价，只不过这代价实在是太沉重了。', '不!當新實驗程序開始時，我的主要學徒:泰勒塞翁，突然無預警的在我眼前萎縮，屈服成這種狀態著實折磨了我的兄弟。我極力想制止，但他就這樣離我遠遠而去。除了讓他從苦痛中倖免外，我也沒別的選擇了。 \r\n\r\n而這是研究與發明的代價，只不過這代價實在是太沉重了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2852', null, null, null, '我不能再容忍了…又有2个学徒向邪恶屈服。我们已经很小心翼翼了…但我不知道是哪里出差错。 $B$B我得放弃这些调查行动，从零开始。 ', '我不能再容忍了…又有2個學徒向邪惡屈服。我們已經很小心翼翼了…但我不知道是哪裡出差錯。 \r\n\r\n我得放棄這些調查行動，從零開始。 \r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2853', null, null, null, '我来迟了。第3个学徒已不知不觉和其他人建立起邪恶的勾当。我必须容忍现状，但当务之急是避免让其他未受影响的学徒同流合污。 $B$B我要让所有人都知道我在远行者居所行周期安息日。这样一来，我就可以好好构思解决难题的办法。 $B$B我一定要想出正确的判断来弥补这个可怕的错误。', '我來遲了。第3個學徒已不知不覺和其他人建立起邪惡的勾當。我必須容忍現狀，但當務之急是避免讓其他未受影響的學徒同流合污。 \r\n\r\n我要讓所有人都知道我在遠行者居所行週期安息日。這樣一來，我就可以好好構思解決難題的辦法。 \r\n\r\n我一定要想出正確的判斷來彌補這個可怕的錯誤。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2854', null, null, null, '<HTML><BR/><BODY><BR/><H1 align=\"center\">部落士兵军阶</H1><BR/><BR/><P align=\"center\">第二部分</P><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/><P align=\"right\">百夫长</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/><P align=\"right\">军团士兵</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/><P align=\"right\">血卫士</P><BR/><BR/><BR/><IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/><P align=\"right\">石卫士</P><BR/><BR/><BR/></BODY><BR/></HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">第二部分</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">百夫長</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">軍團士兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">血衛士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">石衛士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2855', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落军官军阶</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">资深士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">侦察兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">部落軍官軍階</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">資深士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">步兵</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">偵察兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2856', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟士兵军阶</H1><BR/><BR/>\r\n<P align=\"center\">第二部分</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上尉骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中尉骑士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">骑士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟士兵軍階</H1><BR/><BR/>\r\n<P align=\"center\">第二部分</P><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank09\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">一等騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank08\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上尉騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank07\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">中尉騎士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank06\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">騎士</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2857', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">联盟军官军阶</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官长</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">下士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">聯盟軍官軍階</H1><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank05\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官長</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank04\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">上士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank03\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士官</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank02\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">下士</P><BR/><BR/><BR/>\r\n<IMG src=\"InterfacePvPRankBadgesPvPRank01\" align=\"left\"/><BR/><BR/>\r\n<P align=\"right\">士兵</P><BR/><BR/><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2858', null, null, null, '安东尼·雷·斯塔克\r\n\r\n\r\n\r\n\r\n1961 - 2005\r\n', '<HTML>\n <BODY>\n <H1 align=\"center\">\n Anthony Ray Stark\n </H1>\n <H2 align=\"center\">\n 1961 - 2005\n </H2>\n </BODY>\n </HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2859', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">祈愿火盆: 使用者操作手册</H1><BR/>\r\n<H3 align=\"center\">如何找到闹鬼地点</H3><BR/>\r\n<BR/><BR/>\r\n<H3 align=\"center\">介绍</H3><BR/>\r\n<BR/><BR/>\r\n<P>这些页面介绍，包括那些卷入瓦拉萨克护符事件的堕落腐败的鬼魂、灵魂之所在地点，虽然你救了我之前的同伴，据我推测，曾扭曲他们灵魂的恶魔厌恶回音，仍盘悬回荡在那些闹鬼之处。这就是心肠恶毒的瓦拉萨克灵魂的力量。</P><BR/>\r\n<BR/><BR/>\r\n<P>此外，火盆还可以召唤瓦拉萨克，不过，凡事还是得小心谨慎一些。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">祈願火盆: 使用者操作手冊</H1><BR/>\r\n<H3 align=\"center\">如何找到鬧鬼地點</H3><BR/>\r\n<BR/><BR/>\r\n<H3 align=\"center\">介紹</H3><BR/>\r\n<BR/><BR/>\r\n<P>這些頁面介紹，包括那些捲入瓦拉薩克護符事件的墮落腐敗的鬼魂、靈魂之所在地點，雖然你救了我之前的同伴，據我推測，曾扭曲他們靈魂的惡魔厭惡回音，仍盤懸迴盪在那些鬧鬼之處。這就是心腸惡毒的瓦拉薩克靈魂的力量。</P><BR/>\r\n<BR/><BR/>\r\n<P>此外，火盆還可以召喚瓦拉薩克，不過，凡事還是得小心謹慎一些。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2860', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">介绍，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>火盆的功能就像召唤火盆一样。只要把它放在五个闹鬼的地方之一，它就会召唤出亡者的灵魂。</P><BR/>\r\n<BR/><BR/>\r\n<P>另外告诉你，我知道你会有兴趣，挑衅旗帜也能在黑石深渊竞技场有类似的功用。到了那里，只要有机会就替我教训瑟尔伦和他的暴徒们。</P><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>如果你有兴趣了解更多就继续读下去。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">介紹，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>火盆的功能就像召喚火盆一樣。只要把它放在五個鬧鬼的地方之一，它就會召喚出亡者的靈魂。</P><BR/>\r\n<BR/><BR/>\r\n<P>另外告訴你，我知道你會有興趣，挑釁旗幟也能在黑石深淵競技場有類似的功用。到了那裡，只要有機會就替我教訓瑟爾倫和他的暴徒們。</P><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>如果你有興趣瞭解更多就繼續讀下去。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2861', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">依萨利恩</H1><BR/>\r\n<H3 align=\"center\">月亮的夜精灵女祭司</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>当依萨利恩决定离开公司，她是为了去厄运之槌朝圣，那个古老的夜精灵城市，埃德萨拉斯。在那里，有一个图书馆仍保留了丰富的知识，是少数夜精灵保存下来的圣所。她很想去那里找到命运的答案，或许还有护符块的事。我们该知道她也是法琳·树形者的好朋友。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">依薩利恩</H1><BR/>\r\n<H3 align=\"center\">月亮的夜精靈女祭司</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>當依薩利恩決定離開公司，她是為了去厄運之槌朝聖，那個古老的夜精靈城市，埃德薩拉斯。在那裡，有一個圖書館仍保留了豐富的知識，是少數夜精靈保存下來的聖所。她很想去那裡找到命運的答案，或許還有護符塊的事。我們該知道她也是法琳·樹形者的好朋友。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2862', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">依萨利恩，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>不幸地，当她和她的角鹰兽同伴，苍穹，正进入城里的时候，被奥兹恩和他的爪牙攻击。她倒在那里，再也没有爬起来了。依萨利恩从未解开任何瓦萨拉克在护符里施的邪恶魔法秘密，或许这是很好的事。</P><BR/>\r\n<BR/><BR/>\r\n<P>奥兹恩住的露天房间就是闹鬼的地点之一。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">依薩利恩，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>不幸地，當她和她的角鷹獸同伴，蒼穹，正進入城裡的時候，被奧茲恩和他的爪牙攻擊。她倒在那裡，再也沒有爬起來了。依薩利恩從未解開任何瓦薩拉克在護符裡施的邪惡魔法秘密，或許這是很好的事。</P><BR/>\r\n<BR/><BR/>\r\n<P>奧茲恩住的露天房間就是鬧鬼的地點之一。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2863', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">莫尔·灰蹄</H1><BR/>\r\n<H3 align=\"center\">牛头人德鲁伊和第一个倒下的人</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>莫尔·灰蹄是隐刃里相当勇敢且值得信赖的同伴。他成为瓦萨拉克的邪恶爪牙下第一个牺牲的人实在令人惋惜。</P><BR/>\r\n<BR/><BR/>\r\n<P>当我们逃出瓦萨拉克的魔掌时，我们正被他的鬼魂刺客和捕猎者追赶。那场黑石塔的战斗十分混乱，只能这么说。不幸地，莫尔在路上错乱了他的脚步，虽然我猜他是被推倒的。总之，他跌进了塔的下层。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">莫爾·灰蹄</H1><BR/>\r\n<H3 align=\"center\">牛頭人德魯伊和第一個倒下的人</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>莫爾·灰蹄是隱刃裡相當勇敢且值得信賴的同伴。他成為瓦薩拉克的邪惡爪牙下第一個犧牲的人實在令人惋惜。</P><BR/>\r\n<BR/><BR/>\r\n<P>當我們逃出瓦薩拉克的魔掌時，我們正被他的鬼魂刺客和捕獵者追趕。那場黑石塔的戰鬥十分混亂，只能這麼說。不幸地，莫爾在路上錯亂了他的腳步，雖然我猜他是被推倒的。總之，他跌進了塔的下層。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2864', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">莫尔·灰蹄，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>后来，我们发现莫尔并未跌死。但是我的好友最后还是没有好下场；他被塔萨洛尔的食人妖掳为战俘。不久，就在他被指挥官沃恩折磨的时候，一群瓦萨拉克的刺客跳了出来结束了这个任务。</P><BR/>\r\n<BR/><BR/>\r\n<P>如果你想召唤出莫尔·灰蹄的灵魂，指挥官沃恩的房间就是闹鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">莫爾·灰蹄，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>後來，我們發現莫爾並未跌死。但是我的好友最後還是沒有好下場；他被塔薩洛爾的食人妖擄為戰俘。不久，就在他被指揮官沃恩折磨的時候，一群瓦薩拉克的刺客跳了出來結束了這個任務。</P><BR/>\r\n<BR/><BR/>\r\n<P>如果你想召喚出莫爾·灰蹄的靈魂，指揮官沃恩的房間就是鬧鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2865', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">贾林和索索斯</H1><BR/>\r\n<H3 align=\"center\">欲成为血色十字军的姊弟</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>贾林和索索斯是我所认识最偏执的两个人。但是他们战斗的技能是相当杰出的，他们是用钱买来的，也是我推断隐刃领导者，安希恩会留住他们的原因。</P><BR/>\r\n<BR/><BR/>\r\n<P>这对姊弟最后终于找到令他们迷恋的血色十字军的教导和哲学。他们在我们的旅途中总是展现出极大的热诚来歼灭天灾军团，我们也怀疑他们的家人与那个组织有关连。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">賈林和索索斯</H1><BR/>\r\n<H3 align=\"center\">欲成為血色十字軍的姊弟</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>賈林和索索斯是我所認識最偏執的兩個人。但是他們戰鬥的技能是相當傑出的，他們是用錢買來的，也是我推斷隱刃領導者，安希恩會留住他們的原因。</P><BR/>\r\n<BR/><BR/>\r\n<P>這對姊弟最後終於找到令他們迷戀的血色十字軍的教導和哲學。他們在我們的旅途中總是展現出極大的熱誠來殲滅天災軍團，我們也懷疑他們的家人與那個組織有關連。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2866', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">贾林及索索斯，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>他们发现自己面临的试炼是加入十字军必经的过程。他们特别有兴趣的位置是骑士身分；那是需要更艰难的考验的。后来发现索索斯其实没有通过他最后一项考验。贾林当然不会，她公然挑战，坚持让他加入她的骑士身分。据我们所了解，大十字军战士达索汉当场就在房间里把他们都杀了。</P><BR/>\r\n<BR/><BR/>\r\n<P>因此斯坦索姆的血色十字军堡垒里，赤色王座就是另一个闹鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">賈林及索索斯，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>他們發現自己面臨的試煉是加入十字軍必經的過程。他們特別有興趣的位置是騎士身份；那是需要更艱難的考驗的。後來發現索索斯其實沒有通過他最後一項考驗。賈林當然不會，她公然挑戰，堅持讓他加入她的騎士身份。據我們所瞭解，大十字軍戰士達索漢當場就在房間裡把他們都殺了。</P><BR/>\r\n<BR/><BR/>\r\n<P>因此斯坦索姆的血色十字軍堡壘裡，赤色王座就是另一個鬧鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2867', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">科尔莫克</H1><BR/>\r\n<H3 align=\"center\">双头巨魔巫师</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>科尔莫克有什么好说的？他是个有着多样魔法的双头巨魔，不是个很差劲的人。我们第一次带他进入坑洼时他是个囚犯，那是个在燃烧平原接下的任务。但是后来我们发现，他对帮助我们脱离棘手的状况并没有什么益处，然后他就成为公司最早的法师之一。</P><BR/>\r\n<BR/><BR/>\r\n<P>他奇怪的地方是他的两个头暀?着完全不同的个性。一个总是很开心，有一点傻气，另一个很狡猾算计，而且眼神很像死去的人。满吓人的。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">科爾莫克</H1><BR/>\r\n<H3 align=\"center\">雙頭巨魔巫師</H3><BR/>\r\n<BR/><BR/>\r\n<BR/><BR/>\r\n<P>科爾莫克有什麼好說的？他是個有著多樣魔法的雙頭巨魔，不是個很差勁的人。我們第一次帶他進入坑窪時他是個囚犯，那是個在燃燒平原接下的任務。但是後來我們發現，他對幫助我們脫離棘手的狀況並沒有什麼益處，然後他就成為公司最早的法師之一。</P><BR/>\r\n<BR/><BR/>\r\n<P>他奇怪的地方是他的兩個頭暀?著完全不同的個性。一個總是很開心，有一點傻氣，另一個很狡猾算計，而且眼神很像死去的人。滿嚇人的。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2868', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">科尔莫克，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>后来，因为我们长途跋涉，科尔莫克持续暴露于巫术的魔法下。不幸的是那个可怕的东西竟然有饲养亡者灵魂的喜好，在其中获得邪恶的喜悦，尤其在召唤赎罪岛被放逐的灵魂时。</P><BR/>\r\n<BR/><BR/>\r\n<P>在公司解散后，科尔莫克参加了通灵学院的进修课程。鬼魂刺客发现要进入学校实在是太容易了，便在他的新主人，莱斯·霜语面前杀了他。</P><BR/>\r\n<BR/><BR/>\r\n<P>这让莱斯·霜语的房间变成闹鬼的地方之一，也是火盆可以成功利用的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">科爾莫克，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>後來，因為我們長途跋涉，科爾莫克持續暴露於巫術的魔法下。不幸的是那個可怕的東西竟然有飼養亡者靈魂的喜好，在其中獲得邪惡的喜悅，尤其在召喚贖罪島被放逐的靈魂時。</P><BR/>\r\n<BR/><BR/>\r\n<P>在公司解散後，科爾莫克參加了通靈學院的進修課程。鬼魂刺客發現要進入學校實在是太容易了，便在他的新主人，萊斯·霜語面前殺了他。</P><BR/>\r\n<BR/><BR/>\r\n<P>這讓萊斯·霜語的房間變成鬧鬼的地方之一，也是火盆可以成功利用的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2869', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦萨拉克</H1><BR/>\r\n<H3 align=\"center\">龙语贵族和之前的黑石塔将军</H3><BR/>\r\n<BR/><BR/>\r\n<P>在达基萨斯将军之前，有瓦萨拉克，一个拥有强大力量的龙族术士。哨塔的领导权交替是常见的事，对我们而言不幸的是，我们正好处于最后一批的权力转换。</P><BR/>\r\n<BR/><BR/>\r\n<P>我们受一个藏宝海湾的哥布林走私者术士委任，他叫格雷姆尼克，去偷瓦萨拉克的魔法书。我们不知道他是想研读那本书还是把它卖掉(或者两者都有)，不过现在也不重要了。重要的是我们进入黑石塔上层，到瓦萨拉克的巢穴杀了他。乐趣现在才开始。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦薩拉克</H1><BR/>\r\n<H3 align=\"center\">龍語貴族和之前的黑石塔將軍</H3><BR/>\r\n<BR/><BR/>\r\n<P>在達基薩斯將軍之前，有瓦薩拉克，一個擁有強大力量的龍族術士。哨塔的領導權交替是常見的事，對我們而言不幸的是，我們正好處於最後一批的權力轉換。</P><BR/>\r\n<BR/><BR/>\r\n<P>我們受一個藏寶海灣的哥布林走私者術士委任，他叫格雷姆尼克，去偷瓦薩拉克的魔法書。我們不知道他是想研讀那本書還是把它賣掉(或者兩者都有)，不過現在也不重要了。重要的是我們進入黑石塔上層，到瓦薩拉克的巢穴殺了他。樂趣現在才開始。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2870', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦萨拉克，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>我们不了解瓦萨拉克操纵灵魂的魔法有多强大，否则当初就不会接下这个任务了。但是我们已经接了，而且看来也是我们接的最后一个任务了。</P><BR/>\r\n<BR/><BR/>\r\n<P>杀了瓦萨拉克，拿走他的魔法书，科尔莫克一定感应到他的护符有点不对劲。他坚持把它视为战利品，很快地成为一个热烈的争论，他们甚至为了它大打出手。我们当时不知道护符里藏有瓦萨拉克的灵魂；是后来才知道的，也让我们很痛苦。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦薩拉克，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>我們不瞭解瓦薩拉克操縱靈魂的魔法有多強大，否則當初就不會接下這個任務了。但是我們已經接了，而且看來也是我們接的最後一個任務了。</P><BR/>\r\n<BR/><BR/>\r\n<P>殺了瓦薩拉克，拿走他的魔法書，科爾莫克一定感應到他的護符有點不對勁。他堅持把它視為戰利品，很快地成為一個熱烈的爭論，他們甚至為了它大打出手。我們當時不知道護符裡藏有瓦薩拉克的靈魂；是後來才知道的，也讓我們很痛苦。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2871', null, null, null, '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦萨拉克，继续……</H1><BR/>\r\n<BR/><BR/>\r\n<P>分裂护符，我们从新生的瓦萨拉克鬼魂，和他的鬼魂刺客军队那里逃了出来。我们抢夺护符的声响引起了塔里剩下的防卫者注意！</P><BR/>\r\n<BR/><BR/>\r\n<P>我们几乎所有人都逃出黑石塔并以为自己安全了。但是，不管有多远，瓦萨拉克都会派他的爪牙来追我们。隐刃在那次任务后很快就四分五裂了，然后我们就成了俎上鱼肉。</P><BR/>\r\n<BR/><BR/>\r\n<P>你一定要知道，瓦萨拉克的住所就是现在黑石塔里比斯巨兽的房间，最后一个闹鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', '<HTML><BR/>\r\n<BODY><BR/>\r\n<H1 align=\"center\">瓦薩拉克，繼續……</H1><BR/>\r\n<BR/><BR/>\r\n<P>分裂護符，我們從新生的瓦薩拉克鬼魂，和他的鬼魂刺客軍隊那裡逃了出來。我們搶奪護符的聲響引起了塔裡剩下的防衛者注意！</P><BR/>\r\n<BR/><BR/>\r\n<P>我們幾乎所有人都逃出黑石塔並以為自己安全了。但是，不管有多遠，瓦薩拉克都會派他的爪牙來追我們。隱刃在那次任務後很快就四分五裂了，然後我們就成了俎上魚肉。</P><BR/>\r\n<BR/><BR/>\r\n<P>你一定要知道，瓦薩拉克的住所就是現在黑石塔裡比斯巨獸的房間，最後一個鬧鬼的地方。</P><BR/>\r\n</BODY><BR/>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2872', null, null, null, '致所有强壮的男人和女人们：$B$B天灾军团已经回来了而我们务必要将一切琐碎的争吵放在一边，并面对这个威胁。银色黎明会赞赏任何卷起袖子，加入对抗天灾军团威胁的人。$B$B所有要保卫我们家园的人都会聚集在东瘟疫之地的圣光之愿礼拜堂。团结一致，我们就能获得胜利。$B$B名册保管者$B银色黎明', '致所有強壯的男人和女人們：$B$B\r\n天災軍團已經回來了而我們務必要將一切瑣碎的爭吵放在一邊，並面對這個威脅。銀色黎明會讚賞任何捲起袖子，加入對抗天災軍團威脅的人。$B$B\r\n所有要保衛我們家園的人都會聚集在東瘟疫之地的聖光之願禮拜堂。團結一致，我們就能獲得勝利。$B$B\r\n名冊保管者$B\r\n銀色黎明', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2878', null, null, null, '我的埃米莉,$B$B短短数天前, 我们打破了阵营在这光被遗弃的地方, 按照国王的命令返回国内. 虽然我对周围环境黯淡,但我的心是圣洁的, 我高寒跋涉的在岸边走着，就想到了这些, 无限的痛苦,你的肩膀使我感到一丝安慰.$B$B我们沿着海岸线来寻找自己的船, 再当次返回时，却只剩下被烧焦的果壳; 我们无法离开, 并已别无选择,于是就提起勇气走进了这个深不可测的荒原深处.$B$B我去到别一个世界后来保佑你,埃米莉... 并且现在... 我愿与洛丹伦与你而留下来.$B$B我每次醒来都会第一时间看到你,你是我在这片冻土的温暖，我的心爱，没有人可以从我这里带走你.$B$B麦克斯韦', '我的埃米莉,$B$B短短数天前, 我们打破了阵营在这光被遗弃的地方, 按照国王的命令返回国内. 虽然我对周围环境黯淡,但我的心是圣洁的, 我高寒跋涉的在岸边走着，就想到了这些, 无限的痛苦,你的肩膀使我感到一丝安慰.$B$B我们沿着海岸线来寻找自己的船, 再当次返回时，却只剩下被烧焦的果壳; 我们无法离开, 并已别无选择,于是就提起勇气走进了这个深不可测的荒原深处.$B$B我去到别一个世界后来保佑你,埃米莉... 并且现在... 我愿与洛丹伦与你而留下来.$B$B我每次醒来都会第一时间看到你,你是我在这片冻土的温暖，我的心爱，没有人可以从我这里带走你.$B$B麦克斯韦', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2880', null, null, null, '鲁宾，$b$b我知道你可能永远都不会看到我写的这封信，只是我不能闲着，听着壁炉谷的城墙不断发出撞击声。不死族就在我们村子外面，不停袭击我们，而我们收到命令要保护镇民直到援军前来。$b$b我的腿在上一次的行动中断了，所以我坐在这里，一无是处，如果我们的防线被攻破了，我的剑就插在腰间。没有空闲开玩笑...只有打斗和死亡的声音。空气中弥漫着恐惧。$b$b阿尔萨斯王子在这里，和人们一同在前线打仗。如果他不在，我们很久以前就该死了。他对这片土地和人民的热爱是有感染力的; 我很荣幸能为他工作，也会一直跟随他直到老死。$b$b战争越演越烈；不管腿有没有断，我都不能坐在这里。我们需要每一个人。我希望你看到这些话的时候情况能乐观些。$b$b你的朋友，$b里格瑞$b$b', '魯賓，$b$b我知道你可能永遠都不會看到我寫的這封信，只是我不能閒著，聽著壁爐谷的城牆不斷發出撞擊聲。不死族就在我們村子外面，不停襲擊我們，而我們收到命令要保護鎮民直到援軍前來。$b$b我的腿在上一次的行動中斷了，所以我坐在這裡，一無是處，如果我們的防線被攻破了，我的劍就插在腰間。沒有空閒開玩笑...只有打鬥和死亡的聲音。空氣中瀰漫著恐懼。$b$b阿爾薩斯王子在這裡，和人們一同在前線打仗。如果他不在，我們很久以前就該死了。他對這片土地和人民的熱愛是有感染力的; 我很榮幸能為他工作，也會一直跟隨他直到老死。$b$b戰爭越演越烈；不管腿有沒有斷，我都不能坐在這裡。我們需要每一個人。我希望你看到這些話的時候情況能樂觀些。$b$b你的朋友，$b裡格瑞$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2881', null, null, null, '一封被弄皱的信', '$b$bHeliana,$b$bI, Vargus, yet live, despite your wishes to the contrary. I despise traipsing about the countryside in this ridiculous armor.$b$bOur latest orders have us heading towards Andorhal after grain or some such nonsense. Why should I care about the food stores in Andorhal, of all places? This entire notion of being in the army is ludicrous.$b$bRest assured, Aunt Heliana, that I will return for your inheritance; even if I become sorely wounded and am left without food, pure spite will drive me ever onward.$b$bVargus$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2882', null, null, null, '一封潦草的便笺', '一封潦草的便笺', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2883', null, null, null, '萨姆拉,$B$B我们向北去我们的主阿尔塞斯，越向北，我的心就会日益沉重。虽然他曾经辉煌，但是我现在感觉到他年轻时的战士那种精神已经渐渐消退。他的热忱通过一细想遮蔽，在我不可能占卦…他灵魂深处的某一恶梦 $B$B我们很快在冰冻废墟登陆。 虽然许多他的人在寒冷和战役的腐烂兽环境中不断生病， 阿尔塞斯告诉我们他的目的是在这寒冷之地将扭转争斗浪潮趋势。 但是我并没有安慰他的话，他们...严峻的笑容…挂在他的卷曲的嘴唇上的冷冻使我变冷，并远远超过任何可能暴雪的感觉.$B$B请为我们祈祷, 萨姆拉, 同时为我们的世界祈祷,$B$B托加尔', '萨姆拉,$B$B我们向北去我们的主阿尔塞斯，越向北，我的心就会日益沉重。虽然他曾经辉煌，但是我现在感觉到他年轻时的战士那种精神已经渐渐消退。他的热忱通过一细想遮蔽，在我不可能占卦…他灵魂深处的某一恶梦 $B$B我们很快在冰冻废墟登陆。 虽然许多他的人在寒冷和战役的腐烂兽环境中不断生病， 阿尔塞斯告诉我们他的目的是在这寒冷之地将扭转争斗浪潮趋势。 但是我并没有安慰他的话，他们...严峻的笑容…挂在他的卷曲的嘴唇上的冷冻使我变冷，并远远超过任何可能暴雪的感觉.$B$B请为我们祈祷, 萨姆拉, 同时为我们的世界祈祷,$B$B托加尔', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2884', null, null, null, '亲爱的帕米拉，$b$b明天我们就要站上安多哈尔的战场，我怕这将是我写给你的最后一封信了，我的爱。那些不死族丝毫不见疲累，而我们只不过是凡人罢了。我们抵挡不了它们。但不必烦恼，因为我们虽然必定会死去，我们的希望仍然坚定。圣光将会战胜一切！$b$b我最亲爱的，虽然那些黑暗恐怖的家伙猛攻我们的墙垣撞击我们的心灵，最后浮现在我脑海的不会是死亡，我很安慰知道这点。我会想念着你，而我终将体验宁静。$b$b克里斯多夫', '親愛的帕米拉，$b$b明天我們就要站上安多哈爾的戰場，我怕這將是我寫給你的最後一封信了，我的愛。那些不死族絲毫不見疲累，而我們只不過是凡人罷了。我們抵擋不了它們。但不必煩惱，因為我們雖然必定會死去，我們的希望仍然堅定。聖光將會戰勝一切！$b$b我最親愛的，雖然那些黑暗恐怖的傢伙猛攻我們的牆垣撞擊我們的心靈，最後浮現在我腦海的不會是死亡，我很安慰知道這點。我會想念著你，而我終將體驗寧靜。$b$b克裡斯多夫\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2885', null, null, null, '嗨，$n。经过调查之后，我从那张便笺发现了一些讯息。$b$b关于詹姆士，恐怕，我只能找到一点点。不过我确实找到一些亚蜜利亚家人的事。$b$b虽然她或她的孩子们都没从战争中生还，但她的哥哥还活着，一个叫做亚历山大·考德尔的人; 你可以在铁炉堡荒弃的洞穴找到他，研究着燃烧军团危险的魔法。$b$b她还有一个亲密的表姊，一个叫迪兹·斯诺班的女人，她在奥格瑞玛筹划着阿拉希盆地的战役。$b$b你应该在下次前往那里时去拜访他们其中一人。我相信他们会很乐意听听逝去的亚蜜利亚的事。$b$b', '嗨，$n。經過調查之後，我從那張便箋發現了一些訊息。$b$b關於詹姆士，恐怕，我只能找到一點點。不過我確實找到一些亞蜜利亞家人的事。$b$b雖然她或她的孩子們都沒從戰爭中生還，但她的哥哥還活著，一個叫做亞歷山大·考德爾的人; 你可以在鐵爐堡荒棄的洞穴找到他，研究著燃燒軍團危險的魔法。$b$b她還有一個親密的表姊，一個叫迪茲·斯諾班的女人，她在奧格瑞瑪籌劃著阿拉希盆地的戰役。$b$b你應該在下次前往那裡時去拜訪他們其中一人。我相信他們會很樂意聽聽逝去的亞蜜利亞的事。$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2886', null, null, null, '你好, $N. 我以为你可能有兴趣的结果，我的研究.$B$B结果, 鲁宾和leagrem都还活着-以此告诉-他们的家族。即使他们从此已经不能再做士兵了，能看到他们中的任何一个都是一件很值得的事.$B$B你能看到鲁宾的妻子, 乔安娜怀特霍尔, 在暗幽城的战士区, 大部分时间她都在那里. 我不知道她会不会接受这个消息, 思想.$B$B关于Leagrem, 他的伯母是Stormwind的孤儿院女总管, 一个令人敬佩的位置.$B$B生活中一帆风顺, 我祝愿你一切顺利.', '你好, $N. 我以为你可能有兴趣的结果，我的研究.$B$B结果, 鲁宾和leagrem都还活着-以此告诉-他们的家族。即使他们从此已经不能再做士兵了，能看到他们中的任何一个都是一件很值得的事.$B$B你能看到鲁宾的妻子, 乔安娜怀特霍尔, 在暗幽城的战士区, 大部分时间她都在那里. 我不知道她会不会接受这个消息, 思想.$B$B关于Leagrem, 他的伯母是Stormwind的孤儿院女总管, 一个令人敬佩的位置.$B$B生活中一帆风顺, 我祝愿你一切顺利.', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2905', 'Here is a jar of ashes. These are the ashes of my sanity, my passion, and my drive. All, utterly destroyed by themselves. May all those who look upon these desolated lands of Hellfire remember this fallen peon. He shed blood for the Alliance, and sacrificed for the Horde only to be driven utterly mad by the wicked and soulless ones who devoured what he held most dear. As they feast from his toils, may they suffer his wrath. Maybe not in this world, but in every world hereafter. It is my declaration, my solem oath, and my everlasting promise. I will avenge my suffering.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2887', null, null, null, '$N, 我希望你能顺利的找到这封信，在一翻开掘以后,我发现了一些信息,恢复的笔记资料.$B$B萨姆拉的父亲带领被遗忘者和巨魔的人为了自己的信仰朝圣雷霆崖。他的名字就是英里威尔士语的，你能找到他与他的同胞不死生物在泳池的愿景.$B$B托加尔同时遗下他的兄弟埃莉萨。她是一个孤僻的人，我一直到现在才明白，她花了几天，她在达纳苏斯的月泉神殿.$B$B我敦促您参观一下,我想你能做到的. $N.他们可能永远无法得知托加尔或萨姆拉的命运。', '$N, I hope this letter finds you well. After some digging, I\'ve discovered some information on those who wrote that note you recovered.$B$BSamla\'s father leads the Forsaken and Trolls who make the pilgrimage to Thunder Bluff in the ways of his faith. His name is Miles Welsh, and you can find him with his fellow undead in the Pools of Vision.$B$BTorgal, meanwhile, is survived by his cousin, Elissa Dumas. She is a reclusive sort from what I\'ve come to understand; she spends her days in the Temple of the Moon in Darnassus.$B$BI urge you to visit whomever you can, $N. They may never have learned of Torgal or Samla\'s fate.', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2888', null, null, null, '保管者将你发现的信给大家传阅了，$n，我想你可能想要了解最新的情况。$b$b最后一位仅存的“瓦古斯”家族成员住在塞纳里奥城堡，希利苏斯的沙漠里。他以卡隆．海金斯的名字自居。$b$b你应该在下次旅行至荒地时和他谈谈。他或许会对我们发现的事情有兴趣。$b$b', '保管者將你發現的信給大家傳閱了，$n，我想你可能想要瞭解最新的情況。$b$b最後一位僅存的「瓦古斯」家族成員住在塞納裡奧城堡，希利蘇斯的沙漠裡。他以卡隆．海金斯的名字自居。$b$b你應該在下次旅行至荒地時和他談談。他或許會對我們發現的事情有興趣。$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2889', null, null, null, '你好。我终于找到时间仔细研究你在天灾军团发现的公文。虽然花了点时间，但我相信我找到了一名死者的亲戚。$b$b玛克斯韦尔的姊姊，一个叫萨瑞娜·克罗姆温德的女人，在藏宝海湾经营一家小型武器店。我不确定她和她弟弟的关系亲不亲密，但如果你预先知道你将前往该处，你就该安排一次会面。这类事情能有结果在现今是珍贵且稀有的。$b$b', '你好。我終於找到時間仔細研究你在天災軍團發現的公文。雖然花了點時間，但我相信我找到了一名死者的親戚。$b$b瑪克斯韋爾的姊姊，一個叫薩瑞娜·克羅姆溫德的女人，在藏寶海灣經營一家小型武器店。我不確定她和她弟弟的關係親不親密，但如果你預先知道你將前往該處，你就該安排一次會面。這類事情能有結果在現今是珍貴且稀有的。$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2890', null, null, null, '我终于发现一些先前与你打斗的士兵们的消息。线索很难追，因为他们的家谱没有正确的记载所有资讯，但我最后仍找到一个血亲。$b$b你相信圣光之愿礼拜堂的护理者奥林事实上是克里斯多夫的儿子吗?听到父亲的消息他应会比较放心。如果你再次至瘟疫之地为银色黎明服务，拜托你，与他谈谈。这会是件善行。$b$b', '我終於發現一些先前與你打鬥的士兵們的消息。線索很難追，因為他們的家譜沒有正確的記載所有資訊，但我最後仍找到一個血親。$b$b你相信聖光之願禮拜堂的護理者奧林事實上是克裡斯多夫的兒子嗎?聽到父親的消息他應會比較放心。如果你再次至瘟疫之地為銀色黎明服務，拜託你，與他談談。這會是件善行。$b$b', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2891', null, null, null, '纪念联盟最高指挥官安度因·洛萨，一个为了捍卫他的国王、他的臣民和他的家园而不惜牺牲一切的英雄……$B$B让我们的敌人记住我们的名字，让我们的伙伴为我们的牺牲而感到荣耀，我们是洛萨之子。$B$B- 图拉扬将军', '紀念聯盟最高指揮官安度因·洛薩，一個為了捍衛他的國王、他的臣民和他的家園而不惜犧牲一切的英雄……\r\n\r\n讓我們的敵人記住我們的名字，讓我們的夥伴為我們的犧牲而感到榮耀，我們是洛薩之子。\r\n\r\n- 圖拉揚將軍', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2892', null, null, null, '这里长眠着一个勇敢的苦工。他试图抵抗，但只来得及放了个屁。', '這裡長眠著一個勇敢的苦工。他試圖抵抗，但只來得及放了個屁。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2906', null, null, null, '侦察岛屿区域并返回报告。$B$B切记千万别被抓住！$B$B不要让我失望，卡蒂瑞丝！', '偵察島嶼區域並返回報告。$B$B切記千萬別被抓住！$B$B不要讓我失望，卡蒂瑞絲！', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2907', null, null, null, '草图显示沙德拉洛和它的三座神庙，有一些“x”记号的，都与被偷窃的蛋下落有关。记号出现在东边神庙的南方尽头，和东边神庙的东北边一间小茅屋很近，靠近中央湖东边的一个圣坛，在靠近“放逐”字样的南方神庙斜坡上。$B$B羽须的便笺暗示他正在计划传送消息给弗斯塔德·蛮锤，希望他能派遣一批战士去拯救那些蛋。', '這份草圖用X記號標出了沙德拉洛和其他三座神殿以及被偷走的獅鷲卵的所在地。這些記號分佈在東部神殿的南邊、東部神殿東北邊的一座小房子、接近位於中央湖東邊的聖壇，以及南部神殿的斜面，靠近「流亡」字樣的地方。$B$B羽須的日記寫著他打算將該信息傳遞給弗斯塔德·蠻錘，並希望他能派出一批戰士來奪回被偷走的獅鷲卵。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2909', null, null, null, '迪亚索鲁斯， $B$B我相信这封信最终一定会到你手上。这些恶魔真是无脑。他们脑袋里想的只有他们的爪子，鞭子，或是用他们的头钉刺穿什么东西。我不敢使用任何一个地狱火否则一切都会被烧成灰烬! $B$B很令人伤心，我怀念那些奴役兽人的日子。至少他们有一半的脑袋还会听从命令。或许有机会从附近再找一些新的奴隶来?', '迪亞索魯斯， \r\n\r\n我相信這封信最終一定會到你手上。這些惡魔真是無腦。他們腦袋裡想的只有他們的爪子，鞭子，或是用他們的頭釘刺穿什麼東西。我不敢使用任何一個地獄火否則一切都會被燒成灰燼! \r\n\r\n很令人傷心，我懷念那些奴役獸人的日子。至少他們有一半的腦袋還會聽從命令。或許有機會從附近再找一些新的奴隸來?', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2910', null, null, null, '有关夜精灵协助德莱尼的报告是真的。他们想在北边萨提纳尔附近的林歌神殿建立一个新的运作基地。我们应该协商一下，在这可悲的世界下一次月圆前联合我们的军队做一次攻击。如果我们现在制服他们，他们才会记得「部落」这个词真正的意义为何! $B$B戈甘诺恩$B$B致意$B$B备注-不要再用血墨水写情书给我的鞭笞者!夜精灵处女最近很短缺', '有關夜精靈協助德萊尼的報告是真的。他們想在北邊薩提納爾附近的林歌神殿建立一個新的運作基地。我們應該協商一下，在這可悲的世界下一次月圓前聯合我們的軍隊做一次攻擊。如果我們現在制服他們，他們才會記得「部落」這個詞真正的意義為何! \r\n\r\n戈甘諾恩\r\n\r\n致意\r\n\r\n備註-不要再用血墨水寫情書給我的鞭笞者!夜精靈處女最近很短缺', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2911', null, null, null, '迪亚索鲁斯： 我相信这封信可以为你指明方向。这些蠢笨的下等恶魔除了考虑如何砍下敌人的脑袋外不会多花脑子去想别的，战场上他们却常常自己先被砍掉脑袋。我根本不敢利用他们来为我们战斗。 可悲的是，我却一直希望有那么一天，我们可以控制一些兽人，至少他们愿在任何地方为任何强大的领袖服务。我们是不是应该在这附近补充一些兽人奴隶？', '\r\nDiathorus,\r\n\r\nI trust that this letter will eventually make its way to you.  These demons are mindless.  All they think about is their nails, their whips or goring something with their head spikes.  And I dare not use one of the infernals lest it be burned to ashes!\r\n\r\nSadly, I long for the days when we could use the orcs.  At least they had half a brain and could follow orders.  Perhaps there is a chance to take some new thralls from nearby?', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2912', null, null, null, '是时候夺回一些次要种族了，我想。西北边的碎木岗哨是个很诱人的目标。我们应该协商一下，在这可悲的世界下一次月圆前联合我们的军队做一次攻击。如果我们现在制服他们，他们才会记得「部落」这个词真正的意义为何! $B$B戈甘诺恩$B$B致意$B$B备注-不要再用血墨水写情书给我的鞭笞者!夜精灵处女最近很短缺。', '\r\nThe time has come to retake a few of the lesser races, I think.  Splintertree Post to the northwest makes a tempting target.  We should confer on combining our forces for an attack before the next full moon of this pathetic world.  If we subjugate them now they will come to remember what the word \'Horde\' truly used to mean!\r\n\r\nRegards,\r\n\r\nGorgannon\r\n\r\np.s. - Stop using up all of the blood ink on love letters to my lashers!  Night elf virgins are in short supply these days.', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2913', null, null, null, '虽然几乎被击败$B他的勇气仍然坚强$B拔剑出鞘$B命令地狱人群向前冲锋$B$B因为那些暴力的日子$B不要箭，也不要剑$B寻找，不在战斗中也不在冲突里，$B这是矮人领主的方法', '雖然幾乎被擊敗$B他的勇氣仍然堅強$B拔劍出鞘$B命令地獄人群向前衝鋒$B$B因為那些暴力的日子$B不要箭，也不要劍$B尋找，不在戰鬥中也不在衝突裡，$B這是矮人領主的方法', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2914', null, null, null, '……但她已经知道了。在她将自己的灵魂一点一滴的交换出去后，她已经认命的过着贱民的生活了。复仇已经值得了，或是她这样想。$B$B但现在有个机会让她重拾人性，所有的陌生人要交换的就是她的信任。$B$B“让我来”，卡崔恩说。“我没什么好怕的。”', '……但她已經知道了。在她將自己的靈魂一點一滴的交換出去後，她已經認命的過著賤民的生活了。復仇已經值得了，或是她這樣想。$B$B但現在有個機會讓她重拾人性，所有的陌生人要交換的就是她的信任。$B$B「讓我來」，卡崔恩說。「我沒什麼好怕的。」', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2916', null, null, null, '维斯路登，$B$B由于黑暗之门再次开启，荣耀堡和萨尔玛都从艾泽拉斯接获了强大的援军。我看过报告暗示他们正计划要攻击堡垒，而我们也一定要对付燃烧军团。这是无法令人接受的!$B$B不要让血熔炉的运作冒险!在那里制造出来的地狱兽人就是我们成功的关键。加强堡垒的防御并立刻分派更多周边守卫!$B$B相信我，你不会想让我亲自去拜访你的。$B$B-伊利丹', '維斯路登，$B$B\r\n由於黑暗之門再次開啟，榮耀堡和薩爾瑪都從艾澤拉斯接獲了強大的援軍。我看過報告暗示他們正計劃要攻擊堡壘，而我們也一定要對付燃燒軍團。這是無法令人接受的!$B$B\r\n不要讓血熔爐的運作冒險!在那裡製造出來的地獄獸人就是我們成功的關鍵。加強堡壘的防禦並立刻分派更多周邊守衛!$B$B\r\n相信我，你不會想讓我親自去拜訪你的。$B$B\r\n-伊利丹', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2917', null, null, null, '维斯路登，$B$B由于黑暗之门再次开启，荣耀堡和萨尔玛都从艾泽拉斯接获了强大的援军。我看过报告暗示他们正计划要攻击堡垒，而我们也一定要对付燃烧军团。这是无法令人接受的!$B$B不要让血熔炉的运作冒险!在那里制造出来的地狱兽人就是我们成功的关键。加强堡垒的防御并立刻分派更多周边守卫!$B$B相信我，你不会想让我亲自去拜访你的。$B$B-伊利丹', '維斯路登，$B$B\r\n由於黑暗之門再次開啟，榮耀堡和薩爾瑪都從艾澤拉斯接獲了強大的援軍。我看過報告暗示他們正計劃要攻擊堡壘，而我們也一定要對付燃燒軍團。這是無法令人接受的!$B$B\r\n不要讓血熔爐的運作冒險!在那裡製造出來的地獄獸人就是我們成功的關鍵。加強堡壘的防禦並立刻分派更多周邊守衛!$B$B\r\n相信我，你不會想讓我親自去拜訪你的。$B$B\r\n-伊利丹', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2918', null, null, null, '我做了这件事。我不记得我有做，但也没有别的可能了。$B$B我以前犯过罪。我甚至还杀过一个无辜的人。但这次不同。$B$B我改变了。$B$B这些谋杀不是我的选择。有一只野兽住在我心里。$B$B我会被追捕和回避。我要躲起来。逃跑。埋葬尸体。$B$B当我真正了解是什么在困扰着我时，我开始到外面走动。$B$B我感觉不到罪恶。', '我做了這件事。我不記得我有做，但也沒有別的可能了。$B$B我以前犯過罪。我甚至還殺過一個無辜的人。但這次不同。$B$B我改變了。$B$B這些謀殺不是我的選擇。有一隻野獸住在我心裡。$B$B我會被追捕和迴避。我要躲起來。逃跑。埋葬屍體。$B$B當我真正瞭解是什麼在困擾著我時，我開始到外面走動。$B$B我感覺不到罪惡。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2919', null, null, null, '仆人，$B$B值得计划的几年已经过去了。德莱尼和他们可悲的领导者又再次从主人的手里逃走了!更重要的是，他们偷走了我的飞船还让它在艾泽拉斯坠毁!$B$B虽然主人的埃雷达尔密探失败了，但她能将艾克索达的地点传送给我们。很幸运，她的身份对德莱尼而言仍是个谜，我们还是可以利用她来达到我们的目的。', '僕人，$B$B\r\n值得計劃的幾年已經過去了。德萊尼和他們可悲的領導者又再次從主人的手裡逃走了!更重要的是，他們偷走了我的飛船還讓它在艾澤拉斯墜毀!$B$B\r\n雖然主人的埃雷達爾密探失敗了，但她能將埃索達的地點傳送給我們。很幸運，她的身份對德萊尼而言仍是個謎，我們還是可以利用她來達到我們的目的。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2920', null, null, null, '联络我们的间谍并准备全面性突击德莱尼。杀光他们并取回我的飞船。你是艾泽拉斯上唯一可以修补那些畜生对艾克索达造成的损坏的人，又是我唯一的连系。$B$B不要让我失望，地精。如果你对财富的渴望干扰了我的命令，你来世就等着祷告吧。如果你失败了我会让你痛不欲生，你会祈求死亡快点到来将你从痛苦中释放。$B$B-逐日者之王', '聯絡我們的間諜並準備全面性突擊德萊尼。殺光他們並取回我的飛船。你是艾澤拉斯上唯一可以修補那些畜生對埃索達造成的損壞的人，又是我唯一的連繫。$B$B\r\n不要讓我失望，地精。如果你對財富的渴望干擾了我的命令，你來世就等著禱告吧。如果你失敗了我會讓你痛不欲生，你會祈求死亡快點到來將你從痛苦中釋放。$B$B\r\n-逐日者之王', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2923', null, null, null, '部落受人尊敬的领导者，$B$B我 - 洛瑟玛·塞隆，奎尔萨拉斯地区统治者 - 非常荣幸的宣布我们所知道的达克汗·崔希尔，天灾军团的指挥官阵亡了。$B$B达克汗在我们土地上的立足点是由辛多雷种族的评论家在先前无数次的协商中所建立的。我们希望这个事件能平抚所有你心里可能有的异议。$B$B<署名>洛瑟玛·塞隆', '部落受人尊敬的領導者，$B$B我 - 洛瑟瑪·塞隆，奎爾薩拉斯地區統治者 - 非常榮幸的宣佈我們所知道的達克汗·崔希爾，天災軍團的指揮官陣亡了。$B$B達克汗在我們土地上的立足點是由辛多雷種族的評論家在先前無數次的協商中所建立的。我們希望這個事件能平撫所有你心裡可能有的異議。$B$B<署名>洛瑟瑪·塞隆\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2924', null, null, null, '部落受人尊敬的领导者，$B$B我 - 洛瑟玛·塞隆，奎尔萨拉斯地区统治者 - 非常荣幸的宣布我们所知道的达克汗·崔希尔，天灾军团的指挥官阵亡了。$B$B达克汗在我们土地上的立足点是由辛多雷种族的评论家在先前无数次的协商中所建立的。我们希望这个事件能平抚所有你心里可能有的异议。$B$B<署名>洛瑟玛·塞隆', '部落受人尊敬的領導者，$B$B我 - 洛索瑪·塞隆，奎爾薩拉斯地區統治者 - 非常榮幸的宣佈我們所知道的達克汗·崔希爾，天譴軍團的指揮官陣亡了。$B$B達克汗在我們土地上的立足點是由辛多雷種族的評論家在先前無數次的協商中所建立的。我們希望這個事件能平撫所有你心裡可能有的異議。$B$B<署名>洛索瑪·塞隆\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2925', null, null, null, '空间飞船维修傻瓜指南，作者：加姆斯·凯斯$B$B<这本书的书页是用某种闪闪发光的材料制作的，上面的高科技图表详细描述了维修空间飞船的技术细节。这本小册子的开篇题目是“矢量线圈破裂的应对及避免措施”。>', '空間飛船維修傻瓜指南，作者：加姆斯·凱斯\r\n\r\n<這本書的書頁是用某種閃閃發光的材料製作的，上面的高科技圖表詳細描述了維修空間飛船的技術細節。這本小冊子的開篇題目是「矢量線圈破裂的應對及避免措施」。>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2926', null, null, null, '$B$B备注 - 我们的大师常收到外域捎来消息回覆萨尔的问题。答案是，当然，是的。', '$B$B備註 - 我們的大師常收到外域捎來消息回覆薩爾的問題。答案是，當然，是的。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2930', null, null, null, '$N，$B$B血铸三叉矛的创造需要许多稀有又易变的材料，才能制作出最佳手工的可怕武器。我会将材料清单从最容易到最难取得的顺序排列。$B$B别忘记你也一定要在材料被接受，开始铸造之前取得你的血骑士徽记。', '$N，$B$B\r\n血鑄三叉矛的創造需要許多稀有又易變的材料，才能製作出最佳手工的可怕武器。我會將材料清單從最容易到最難取得的順序排列。$B$B\r\n別忘記你也一定要在材料被接受，開始鑄造之前取得你的血騎士徽記。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2931', null, null, null, '愤怒之血 - 为了恰当地锻炼武器的金属，它一定要在愤怒之血里被冷却。虽然大部份恶魔的血液都有实质力量，但普通的血却不足以铸造你的三叉矛。$B$B谨慎询问奥格瑞玛的术士之后对寻找鲜血的补给有了帮助。怒焰裂谷里的燃刃祭司拥有一种充满鲜血的宝珠，那就是我们要制造你武器所需的东西。我听说那颗宝珠由一个自称祈求者耶戈什的兽人守卫着。$B$B小心行动，所有和恶魔力量的往来都要注意。握有这样强大的物体时你永远也不知道会发生什么事。', '憤怒之血 - 為了恰當地鍛煉武器的金屬，它一定要在憤怒之血裡被冷卻。雖然大部份惡魔的血液都有實質力量，但普通的血卻不足以鑄造你的三叉矛。$B$B\r\n謹慎詢問奧格瑞瑪的術士之後對尋找鮮血的補給有了幫助。怒焰裂谷裡的燃刃祭司擁有一種充滿鮮血的寶珠，那就是我們要製造你武器所需的東西。我聽說那顆寶珠由一個自稱祈求者耶戈什的獸人守衛著。$B$B\r\n小心行動，所有和惡魔力量的往來都要注意。握有這樣強大的物體時你永遠也不知道會發生什麼事。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2932', null, null, null, '一箱血铸锭 - 只有一箱，钢铁是很好的金属，但由它铸造的武器则不值得注意。血铸锭代表一种极好的金属，而它的制作过程一直是个秘密。传说任何敢触碰并使用这个金属的铁匠都被诅咒了，但我们的工匠已证明这是个谬论。$B$B这个金属的名声增加了它的稀有度，但被遗忘者却声称在影牙城堡可能有补给。一个叫做蓝登·史帝威尔技艺高超的铁匠非常好奇，便走私了一箱血铸锭。没人知道他把东西藏在哪里，但我们的消息告诉我们那名铁匠现在被囚禁在城堡的地下城里。用尽所有方法取得那箱血铸锭。', '一箱血鑄錠 - 只有一箱，鋼鐵是很好的金屬，但由它鑄造的武器則不值得注意。血鑄錠代表一種極好的金屬，而它的製作過程一直是個秘密。傳說任何敢觸碰並使用這個金屬的鐵匠都被詛咒了，但我們的工匠已證明這是個謬論。$B$B\r\n這個金屬的名聲增加了它的稀有度，但被遺忘者卻聲稱在影牙城堡可能有補給。一個叫做藍登·史帝威爾技藝高超的鐵匠非常好奇，便走私了一箱血鑄錠。沒人知道他把東西藏在哪裡，但我們的消息告訴我們那名鐵匠現在被囚禁在城堡的地下城裡。用盡所有方法取得那箱血鑄錠。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2933', null, null, null, '一颗被污染的科尔宝石 - 博学者的墓碑记载着这些宝石曾被一些施法者用来保留他们的魔法能量，让他们更致命。虽然纯净的宝石可以安全地被中级施法者使用，但它们对我们的目的而言仍嫌太弱。$B$B在黑暗深渊的洞穴中，纳迦施法者将科尔宝石注入他们自己的黑暗力量，增强它们的力量，以便在你的三叉矛中完美地被使用。在你弄到需要的宝石之前先无情的杀了他们。', '一顆被污染的科爾寶石 - 博學者的墓碑記載著這些寶石曾被一些施法者用來保留他們的魔法能量，讓他們更致命。雖然純淨的寶石可以安全地被中級施法者使用，但它們對我們的目的而言仍嫌太弱。$B$B\r\n在黑暗深淵的洞穴中，納迦施法者將科爾寶石注入他們自己的黑暗力量，增強它們的力量，以便在你的三叉矛中完美地被使用。在你弄到需要的寶石之前先無情的殺了他們。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2934', null, null, null, '你将戴上的徽记最后一次是被晨星家族中一个勇敢无惧的血骑士所配戴。他典范的奉献是你在军队中生活的好预兆。$B$B血骑士晨星对死亡之域的天灾军团要塞率领了一次大胆，但不成功的攻击。他的尸体就在他死去的地方安息，与我们最强劲的敌手锁在战斗中。找到他，$N，并将他的徽记带来给我。你的成功会为他，为你，为所有真正的血骑士带来荣耀。', '你將戴上的徽記最後一次是被晨星家族中一個勇敢無懼的血騎士所配戴。他典範的奉獻是你在軍隊中生活的好預兆。$B$B\r\n血騎士晨星對死亡之域的天災軍團要塞率領了一次大膽，但不成功的攻擊。他的屍體就在他死去的地方安息，與我們最強勁的敵手鎖在戰鬥中。找到他，$N，並將他的徽記帶來給我。你的成功會為他，為你，為所有真正的血騎士帶來榮耀。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2936', null, null, null, '你发现了神殿的位置!进一步的检查之后，你感觉到一股更强大的陌生力量控制了岛屿。你觉得站在神殿旁有点不舒服……甚至有点心乱。 $B$B神殿旁的青铜色公告牌上写着: $B$B「这是达斯雷玛神殿，对最高尚的精灵们致上最高的敬意。让所有景仰过这个纪念碑的人都会记得他对抗天谴军团的牺牲及对我们继续生存的事业贡献。所有让奎尔萨拉斯繁荣的人们也都感谢他。」', '你發現了神殿的位置!進一步的檢查之後，你感覺到一股更強大的陌生力量控制了島嶼。你覺得站在神殿旁有點不舒服……甚至有點心亂。 \r\n\r\n神殿旁的青銅色公告牌上寫著: \r\n\r\n「這是達斯雷瑪神殿，對最高尚的精靈們致上最高的敬意。讓所有景仰過這個紀念碑的人都會記得他對抗天譴軍團的犧牲及對我們繼續生存的事業貢獻。所有讓奎爾薩拉斯繁榮的人們也都感謝他。」', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2937', null, null, null, '我看着他们无情的鞭打他。他们残酷的中尉一举一动就好像残暴是正常的事一样。经过野蛮的鞭打之后，我可以感觉我已经崩溃了。 $B$B看着复仇者萨鲁安忍受他们的殴打，我开始哭泣。并不是因为害怕……而是因为悲伤。看到三头政治的德莱尼被这样对待。要是我能打破我的束缚就好了。要是可以的话……', '我看著他們無情的鞭打他。他們殘酷的中尉一舉一動就好像殘暴是正常的事一樣。經過野蠻的鞭打之後，我可以感覺我已經崩潰了。 $B$B看著復仇者薩魯安忍受他們的毆打，我開始哭泣。並不是因為害怕……而是因為悲傷。看到三頭政治的德萊尼被這樣對待。要是我能打破我的束縛就好了。要是可以的話……', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2938', null, null, null, '过了很长一段时间，守备官失去了知觉。他已遭受太多的严刑拷打。那个残忍的家伙——他的名字似乎是叫玛提斯——竟然往守备官那早已伤痕累累的身体上泼洒腐水。但撒鲁安并没有清醒过来。', '過了很長一段時間，守備官失去了知覺。他已遭受太多的嚴刑拷打。那個殘忍的傢伙——他的名字似乎是叫瑪提斯——竟然往守備官那早已傷痕纍纍的身體上潑灑腐水。但撒魯安並沒有清醒過來。\r\r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2939', null, null, null, '我想大声尖叫，却只能发出微微的声音。因为刚才的反抗行动，我很快就被打倒在地。当我躺在地上时，看到玛提斯在撒鲁安的尸体旁来回走动，显得非常焦虑。他曾说过如果撒鲁安死于他的折磨，那个希隆娜丝会活活要了他的命。', '我想大聲尖叫，卻只能發出微微的聲音。因為剛才的反抗行動，我很快就被打倒在地。當我躺在地上時，看到瑪提斯在撒魯安的屍體旁來回走動，顯得非常焦慮。他曾說過如果撒魯安死於他的折磨，那個希隆娜絲會活活要了他的命。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2940', null, null, null, '希隆娜丝计划把撒鲁安……$B$B<由于作者死去，故事突然中断了。接下去的记录是一片空白。>', '希隆娜絲計劃把撒魯安……\r\n\r\n<由於作者死去，故事突然中斷了。接下去的記錄是一片空白。>\r\r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2941', null, null, null, '给魔导师阿斯塔洛·血誓的物品\r\n莉亚德琳大人： 我们抓获的生物所造成的一些麻烦使得许多重要的设备遭到了毁坏。修复它们需要很长一段时间，而我的人力和物力资源已经全部投入其中了。我已向大法师罗曼斯报告了这件事，但他还没有答复我。我担心如果缺少如下这些材料的话，我们就不得不改变我们的计划，而您应该知道这对你们的血骑士来说意味着什么。 如果您能帮助我，我会为您列出所需材料的名字。骑士团所提供的任何帮助对我们都很重要。 魔导师阿斯塔洛·血誓', '女公爵·莉雅淳，$B$B\r\n最近被俘虜的生物製造了一些麻煩，讓我們一些最重要的裝備遭到損壞。恢復一切的工作讓我的博學者和資源都大大的減少。我已將事件報告呈交給大博學者洛摩斯，但他回覆的速度很慢。我擔心沒有下列的物品我們會被迫縮減運作，而你知道那對你的騎士們而言代表著什麼。$B$B\r\n如果你可以有任何的幫助，我已在下一頁列好了材料清單。我們會感謝你的軍隊所能提供的任何協助。$B$B\r\n博學者阿斯塔樂·血誓', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2942', null, null, null, '*40块符文布$B$B*6条奥金锭$B$B*10根太阳草$B$B*5个黑暗符文$B$B*150金', '*40塊符文布$B$B\r\n*6條奧金錠$B$B\r\n*10根太陽草$B$B\r\n*5個黑暗符文$B$B\r\n*150金', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2943', null, null, null, '太阳之王的公告$B$B炎鹰部队——我亲爱而忠诚的孩子们——我为你们而骄傲。你们在艾泽拉斯世界中执行的任务对于我和主人来说都是至关重要的。$B$B再过不久，风暴要塞的增援就将通过太阳之门到达秘血岛。$B$B我们将夺取埃索达，干掉所有蓝皮肤的杂种。维伦将会为他的罪行付出代价。$B$B- 太阳之王，凯尔萨斯·逐日者', '太陽之王的公告\r\n\r\n炎鷹部隊——我親愛而忠誠的孩子們——我為你們而驕傲。你們在艾澤拉斯世界中執行的任務對於我和主人來說都是至關重要的。\r\n\r\n再過不久，風暴要塞的增援就將通過太陽之門到達秘血島。\r\n\r\n我們將奪取埃索達，幹掉所有藍皮膚的雜種。維倫將會為他的罪行付出代價。\r\n\r\n- 太陽之王，凱爾薩斯·逐日者', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2944', null, null, null, '<HTML><BODY><IMG src=\"图形接口24475_gordawg_256\"/></BODY></HTML>', '<HTML>\n <BODY>\n <IMG src=\"InterfacePictures24475_gordawg_256\"/>\n </BODY>\n </HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2975', null, null, null, '蠢货，$B$B去找食物。蓝色的味道很好。兰特瑞索说找到蓝色的就行。把食物带回去给拉普。他吃了后蠢货再吃。没问题吧？动作快一些，否则拉普会把你吃掉的。$B$B- 拉普', '蠢貨，\r\n\r\n去找食物。藍色的味道很好。蘭特瑞索說找到藍色的就行。把食物帶回去給拉普。他吃了後蠢貨再吃。沒問題吧？動作快一些，否則拉普會把你吃掉的。\r\n\r\n- 拉普', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2976', null, null, null, '(1)酒鬼拉利克：巨魔。他从未离开小酒店。很坏。很丑。很胖。很坏。又很胖。$B$B[下一页]', '(1)酒鬼拉利克：巨魔。他從未離開小酒店。很壞。很醜。很胖。很壞。又很胖。$B$B\r\n[下一頁]', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2977', null, null, null, '(2)库库许：这个从赞格沼泽来的讨厌小孢子人在赞格沼泽和泰洛卡的边界有一个蘑菇园。在“那是什么味道”的游戏中输了很多金子给沙萨拉毕恩。想用叫做空茎的蠢蘑菇付给沙萨拉毕恩。跟他说这跟黄金一样好!哈!$B$B[下一页]', '(2)庫庫許：這個從贊加沼澤來的討厭小孢子人在贊加沼澤和泰羅卡的邊界有一個蘑菇村。在「那是什麼味道」的遊戲中輸了很多金子給沙薩拉畢恩。想用叫做空莖的蠢蘑菇付給沙薩拉畢恩。跟他說這跟黃金一樣好!哈!$B$B\r\n[下一頁]', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2978', null, null, null, '(3)服伦：阿拉卡。输给沙萨拉毕恩许多金子。鸟人因为没有付钱就被沙萨拉毕恩杀死了。现在鬼魂鸟人在白骨荒野的西南方走着。', '(3)服倫：阿拉卡。輸給沙薩拉畢恩許多金子。鳥人因為沒有付錢就被沙薩拉畢恩殺死了。現在鬼魂鳥人在白骨荒野的西南方走著。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2979', null, null, null, '奥莱姆贝根茎是泰洛卡森林里一种普遍的根茎植物。到构成泰洛卡森林的奥莱姆贝树丛附近搜寻向上翻起的根茎。$B$B沼泽莓只生长在赞格沼泽。只能在安格拉斯要塞找到它，就在沼泽的西北区。小心那些守卫珍贵莓子的巨魔。$B$B泰拉蕊藻叶或许是这清单上最容易取得的草药。你可以在泰拉蕊盆地的卡拉达尔南方找到藻叶。它们生长在水边。$B$B清单上最后一种草药是剑刃山脉特有的植物。你可以在赞格沼泽北边找到剑刃山脉。龙脊生长在守卫着剑刃山脉峡谷的锯齿状岩石地形尽头。等你见到了剑刃山脉就会知道我在说什么了。那个岩石地形就像是一排排的尖锐牙齿。', '奧萊姆貝根莖是泰羅卡森林裡一種普遍的根莖植物。到構成泰羅卡森林的奧萊姆貝樹叢附近搜尋向上翻起的根莖。$B$B\r\n沼澤莓只生長在贊加沼澤。只能在安格拉斯要塞找到它，就在沼澤的西北區。小心那些守衛珍貴莓子的巨魔。$B$B\r\n泰拉蕊藻葉或許是這清單上最容易取得的草藥。你可以在泰拉蕊盆地的加拉達爾南方找到藻葉。它們生長在水邊。$B$B\r\n清單上最後一種草藥是刀鋒山脈特有的植物。你可以在贊加沼澤北邊找到刀鋒山脈。龍脊生長在守衛著刀鋒山脈峽谷的鋸齒狀岩石地形盡頭。等你見到了刀鋒山脈就會知道我在說什麼了。那個岩石地形就像是一排排的尖銳牙齒。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2981', null, null, null, '仆人. $B$B这些兽人正在成为一个不小的烦扰。我希望你能亲自看看它们说些什么，我们的部队正在黑暗之门大规模攻击，他们的所有能量转化奠定废物，以萨尔玛的名誉。$B$B这是阿拉修斯的命令.', '仆人. $B$B这些兽人正在成为一个不小的烦扰。我希望你能亲自看看它们说些什么，我们的部队正在黑暗之门大规模攻击，他们的所有能量转化奠定废物，以萨尔玛的名誉。$B$B这是阿拉修斯的命令.', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2982', null, null, null, '仆人. $B$B这些兽人正在成为一个不小的烦扰。我希望你能亲自看看它们说些什么，我们的部队正在黑暗之门大规模攻击，他们的所有能量转化奠定废物，以萨尔玛的名誉。$B$B这是阿拉修斯的命令.', '仆人. $B$B这些兽人正在成为一个不小的烦扰。我希望你能亲自看看它们说些什么，我们的部队正在黑暗之门大规模攻击，他们的所有能量转化奠定废物，以萨尔玛的名誉。$B$B这是阿拉修斯的命令.', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2983', null, null, null, '要让饮用者看见彩色幻象的亡灵，就一定要收集到一些特定的材料。$B$B首先，西纳葛兰的山地古罗眼球，靠近燃烧军团被诅咒的炼冶场，战锤山丘南方。$B$B在西纳葛兰也可以找到巨型风翼猫头鹰。只有完美的品种才行!$B$B最后，在纳葛兰西边和西南边有老迈的裂蹄油。$B$B收集这些物品再回来找我!动作快!$B$B-奈崔恩', '要讓飲用者看見彩色幻象的亡靈，就一定要收集到一些特定的材料。$B$B\r\n首先，西納葛蘭的山地古羅眼球，靠近燃燒軍團被詛咒的煉冶場，戰錘山丘南方。$B$B\r\n在西納葛蘭也可以找到巨型風翼貓頭鷹。只有完美的品種才行!$B$B\r\n最後，在納葛蘭西邊和西南邊有老邁的裂蹄油。$B$B\r\n收集這些物品再回來找我!動作快!$B$B\r\n-奈崔恩', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2984', null, null, null, '<翻过数页你根本无法理解的“科学论文”后，最后一页末有几行字迹潦草的记录……>$B$B对不起，老大。看起来虚空风暴即将要爆炸，这场爆炸会席卷外域的大部分地区。$B$B我不能完全肯定，但我想这应该和血精灵在法力熔炉所做的一切有关。$B$B幸好您建造了一艘火箭！$B$B- 哥白尼$B$B又及，我能与您一起离开吗？在您经过扭曲虚空的时候一定会需要一个虚空学家的！', '<翻過數頁你根本無法理解的「科學論文」後，最後一頁末有幾行字跡潦草的記錄……>\r\n\r\n對不起，老大。看起來虛空風暴即將要爆炸，這場爆炸會席捲外域的大部分地區。\r\n\r\n我不能完全肯定，但我想這應該和血精靈在法力熔爐所做的一切有關。\r\n\r\n幸好您建造了一艘火箭！\r\n\r\n- 哥白尼\r\n\r\n又及，我能與您一起離開嗎？在您經過扭曲虛空的時候一定會需要一個虛空學家的！', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2985', null, null, null, '将军，$B$B我们正守住命运阶梯的战线，但军团派出了一波又一波的人马来攻击我们军队。他们在附近一定有个集结地是我们侦察兵尚未找到的。虽然这里的联盟军队是站在我们的阵线，我担心军团的众多数量会超出我们的防御。尽量伸出你的援手，否则传送门瘫痪 - 我们就要永远被困在这个梦魇世界了。$B$B欧莱恩中将', '將軍，$B$B我們正守住命運階梯的戰線，但軍團派出了一波又一波的人馬來攻擊我們軍隊。他們在附近一定有個集結地是我們偵察兵尚未找到的。雖然這裡的聯盟軍隊是站在我們的陣線，我擔心軍團的眾多數量會超出我們的防禦。盡量伸出你的援手，否則傳送門癱瘓 - 我們就要永遠被困在這個夢魘世界了。$B$B歐萊恩中將', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2986', null, null, null, '当纳尼滑上来时，这位沧桑的战士给了她一个严厉的眼神。“我想你待在这里是为了搜集杀死鱼人的奖励吧?”$B$B她的眼神移到了他身边闪亮的阔剑。“马库斯，这要看奖励是什么。”她玩弄着她的头发，假装没有注意到他对她的存在感到不自在。“我可能并不想要这个奖励。”', '當納尼滑上來時，這位滄桑的戰士給了她一個嚴厲的眼神。「我想你待在這裡是為了搜集殺死魚人的獎勵吧?」$B$B\r\n她的眼神移到了他身邊閃亮的闊劍。「馬庫斯，這要看獎勵是什麼。」她玩弄著她的頭髮，假裝沒有注意到他對她的存在感到不自在。「我可能並不想要這個獎勵。」', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2987', null, null, null, '马库斯走向她，恐惧与愤怒的情绪交织地站着。“这个奖励是没得商量的!”他稍微停顿，整理一下紧张的心情，然后逼近她娇小的身躯。他们的双唇火热地交缠着，激情的气息满溢，融化了她的冰霜护甲法术。$B$B“所以，这个任务有下一个步骤吗?”她的双眼闪着兴奋的光芒，挑逗着他。$B$B<这样的情节持续了数百页，完全没有任何进一步的剧情。>', '馬庫斯走向她，恐懼與憤怒的情緒交織地站著。「這個獎勵是沒得商量的!」他稍微停頓，整理一下緊張的心情，然後逼近她嬌小的身軀。他們的雙唇火熱地交纏著，激情的氣息滿溢，融化了她的冰霜護甲法術。$B$B\r\n「所以，這個任務有下一個步驟嗎?」她的雙眼閃著興奮的光芒，挑逗著他。$B$B\r\n<這樣的情節持續了數百頁，完全沒有任何進一步的劇情。>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2988', null, null, null, '这个奇怪的物体看起来有点像是一个工程学的装置，但应该是你之前从未见过的装置。$B$B从它的外表上看，不管你觉得它是什么东西，它一定是已经被使用过的。$B$B从附近的尸体来看，你不由得认为它就是这一切惨剧的罪魁祸首。', '這個奇怪的物體看起來有點像是一個工程學的裝置，但應該是你之前從未見過的裝置。\r\n\r\n從它的外表上看，不管你覺得它是什麼東西，它一定是已經被使用過的。\r\n\r\n從附近的屍體來看，你不由得認為它就是這一切慘劇的罪魁禍首。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2989', null, null, null, '指挥官，$B$B这个密码要被送到祖鲁希德那里。在进一步的通知之前，这个物品必须在他的保管之下。$B$B-伊利丹王怒风', '指揮官，$B$B\r\n這個密碼要被送到祖魯希德那裡。在進一步的通知之前，這個物品必須在他的保管之下。$B$B\r\n-伊利丹王怒風', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2990', null, null, null, '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">上层精灵的挽歌</H1>\r\n<BR/>\r\n<IMG src=\"InterfaceFlavorImagesBloodElfLogo-small\"/>\r\n</BODY>\r\n</HTML>', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">上層精靈的輓歌</H1>\r\n<BR/>\r\n<IMG src=\"InterfaceFlavorImagesBloodElfLogo-small\"/>\r\n</BODY>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2991', null, null, null, '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>简略翻译版……</P>\r\n<BR/>\r\n<P>以光芒之名，以太阳的光芒之名，</P>\r\n<P>血之后裔们啊，</P>\r\n<P>敌人正长驱直入。</P>\r\n<P>血之后裔们啊，</P>\r\n<P>以光芒之名，</P>\r\n<P>消亡的血之后裔啊，</P>\r\n<P>敌人正长驱直入。</P>\r\n<P>啊，血之后裔们啊，</P>\r\n<P>以太阳的光芒之名。</P>\r\n<P>消亡的血之后裔们啊，</P>\r\n<P>他们正长驱直入。</P>\r\n<P>啊，血之后裔们啊，</P>\r\n<P>以太阳的光芒之名。</P>\r\n<P>太阳的光芒。</P>\r\n</BODY>\r\n</HTML>\r\n', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>簡略翻譯版……</P>\r\n<BR/>\r\n<P>以光芒之名，以太陽的光芒之名，</P>\r\n<P>血之後裔們啊，</P>\r\n<P>敵人正長驅直入。</P>\r\n<P>血之後裔們啊，</P>\r\n<P>以光芒之名，</P>\r\n<P>消亡的血之後裔啊，</P>\r\n<P>敵人正長驅直入。</P>\r\n<P>啊，血之後裔們啊，</P>\r\n<P>以太陽的光芒之名。</P>\r\n<P>消亡的血之後裔們啊，</P>\r\n<P>他們正長驅直入。</P>\r\n<P>啊，血之後裔們啊，</P>\r\n<P>以太陽的光芒之名。</P>\r\n<P>太陽的光芒。</P>\r\n</BODY>\r\n</HTML>\r\n\r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2992', null, null, null, '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>萨拉斯语版本……</P>\r\n<BR/>\r\n<P>Anar\'alah, anar\'alah belore,</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah,</P>\r\n<P>Shindu sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah belore.</P>\r\n<P>Shindu sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah belore,</P>\r\n<P>Belore.</P>\r\n</BODY>\r\n</HTML>', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>薩拉斯語版本……</P>\r\n<BR/>\r\n<P>Anar\'alah, anar\'alah belore,</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah,</P>\r\n<P>Shindu sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah belore.</P>\r\n<P>Shindu sin\'dorei,</P>\r\n<P>Shindu fallah na.</P>\r\n<P>Sin\'dorei,</P>\r\n<P>Anar\'alah belore,</P>\r\n<P>Belore.</P>\r\n</BODY>\r\n</HTML>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2993', null, null, null, '亲爱的大臣，$B$B把暗影之月的巨人们带到峡谷的任务现在已经落在了你的肩上，运用你血精灵的魅力，使他们用我的方式看事情。如果你无法做到这一点，就把他们杀得鸡犬不留……$B$B伊利丹·怒风', '親愛的大臣，$B$B\r\n把暗影之月的巨人們帶到峽谷的任務現在已經落在了你的肩上，運用你血精靈的魅力，使他們用我的方式看事情。如果你無法做到這一點，就把他們殺得雞犬不留……$B$B\r\n伊利丹·怒風', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2994', null, null, null, '无论如何，圣骑士的行为必须与圣光对应，圣光是我们力量的来源。我们其中的一员为了神而奋战，并不代表我们为了神性而奋战，我们一切的行动都是为了努力追求至善。', '無論如何，聖騎士的行為必須與聖光對應，聖光是我們力量的來源。我們其中的一員為了神而奮戰，並不代表我們為了神性而奮戰，我們一切的行動都是為了努力追求至善。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2995', null, null, null, '就算在这个艰困的时局中被号召前来打击邪恶，你必须永远记住，帮助别人才会真正地让你与众不同。同情心，耐心与勇气就是圣骑士作战时的最大力量。$B$B清楚地了解这个道理，永远也不要忘记。', '就算在這個艱困的時局中被號召前來打擊邪惡，你必須永遠記住，幫助別人才會真正地讓你與眾不同。同情心，耐心與勇氣就是聖騎士作戰時的最大力量。$B$B清楚地瞭解這個道理，永遠也不要忘記。\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2996', null, null, null, '免责声明。$B$B试飞员对使用风动电容器（以下简称“设备”）的过程中可能引发的粉碎性骨折、触电身亡、重要器官损伤、被当地野兽吞食、时间和/或空间幻觉等致伤、致残或者死亡事件表示完全理解。试飞员自愿进入设备，并自愿承担上述风险。$B$B试飞员同意，如果$g他:她;不幸罹难，将免除并永远放弃对塔利·萨普纳波及其家人提起诉讼的权利；并同意将$g他:她;的遗体捐献给萨普纳波进行研究。$B$B签名：$B___$n', '免責聲明。\r\n\r\n試飛員對使用風動電容器（以下簡稱「設備」）的過程中可能引發的粉碎性骨折、觸電身亡、重要器官損傷、被當地野獸吞食、時間和/或空間幻覺等致傷、致殘或者死亡事件表示完全理解。試飛員自願進入設備，並自願承擔上述風險。\r\n\r\n試飛員同意，如果$g他:她;不幸罹難，將免除並永遠放棄對塔利·薩普納波及其家人提起訴訟的權利；並同意將$g他:她;的遺體捐獻給薩普納波進行研究。\r\n\r\n簽名：\r\n___$n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2997', null, null, null, '您好，学有所成的安拓维奇：$B$B我希望你能在萨尔玛收到这封信，并且对你的研究有所帮助。虽然我还是希望能回到幽暗城，因为那边有全面性的工具与仆人可以协助我，但是我在断脊氏族的关系已经足以应付我的实验。$B$B啊，死者的挽歌。$B$B顺道一提，我有一些有趣的消息---$N从地狱兽人身体上采集的血液出现了恶魔腐化的浓缩体。这几乎与原来艾泽拉斯上受到恶魔腐化的地狱兽人完全相同……玛诺洛斯的腐化之血。$B$B玛诺洛斯的影响不可能来到外域，这个地狱领主已经死了好几年了，但是，我担心有另外一个地狱领主正在此地肆虐。一个有着玛诺洛斯力量的地狱领主……一个可以腐化本地兽人的存在。$B$B我会继续我对血之谷鲜血的研究，但是我极需你去追捕那个目标。我害怕这个目标将会引导你深入地狱火堡垒。$B$B你勤奋好学的，$B药剂师艾柏瑞克', '您好，學有所成的安拓維奇：$B$B\r\n我希望你能在薩爾瑪收到這封信，並且對你的研究有所幫助。雖然我還是希望能回到幽暗城，因為那邊有全面性的工具與僕人可以協助我，但是我在斷脊氏族的關係已經足以應付我的實驗。$B$B\r\n啊，死者的輓歌。$B$B\r\n順道一提，我有一些有趣的消息---$N從地獄獸人身體上採集的血液出現了惡魔腐化的濃縮體。這幾乎與原來艾澤拉斯上受到惡魔腐化的地獄獸人完全相同……瑪諾洛斯的腐化之血。$B$B\r\n瑪諾洛斯的影響不可能來到外域，這個地獄領主已經死了好幾年了，但是，我擔心有另外一個地獄領主正在此地肆虐。一個有著瑪諾洛斯力量的地獄領主……一個可以腐化本地獸人的存在。$B$B\r\n我會繼續我對血之谷鮮血的研究，但是我極需你去追捕那個目標。我害怕這個目標將會引導你深入地獄火堡壘。$B$B\r\n你勤奮好學的，$B\r\n藥劑師艾柏瑞克', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2998', null, null, null, '$N，$B$B诅咒密码事实上是一组十分强大的咒文。它代表着古老的混沌力量，我们的世界那边的多起重大惨剧都与这组密码有关。是的，这也就是说，它并非是德拉诺所独有的。$B$B这个燃烧着的火焰符号代表凯尔萨斯，他渊博的古文知识可以完全理解这一切。$B$B你的发现已经惊动了纳鲁，阿达尔邀请你前往沙塔斯城与他会面。请你立刻到这里来，我们可以在圣光广场会面。$B$B你谦恭的朋友，卡德加', '$N，\r\n\r\n詛咒密碼事實上是一組十分強大的咒文。它代表著古老的混沌力量，我們的世界那邊的多起重大慘劇都與這組密碼有關。是的，這也就是說，它並非是德拉諾所獨有的。$B$B這個燃燒著的火焰符號代表凱爾薩斯，他淵博的古文知識可以完全理解這一切。$B$B你的發現已經驚動了納魯，阿達爾邀請你前往沙塔斯城與他會面。請你立刻到這裡來，我們可以在聖光廣場會面。\r\n\r\n你謙恭的朋友，\r\n\r\n卡德加', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2999', null, null, null, '安拓维奇，$B$B首先，让我们来说说你上一个派来的人，$n，$g他:她;在工作上的表现已经远超过我的预期了。我已经占据了$g他:她;的身体，并且用$g他:她;的手来写这封信。这对我来说是多大的享受啊，我希望有一天我能有幸占据你的身体，强大的巫医!$B$B所以，你要求我所做的报告如下：在地狱火堡垒内肆虐的恶魔比我过去遭遇过的强大许多。我猜想在第三次大战时著名的地狱领主玛诺洛斯就掌握了同样的力量，但拥有这股力量的生物屈指可数。然而，我感觉到有一股禁锢之风围绕着这个恶魔。如果它做出了违背其意愿的事情，我一点都不会感到惊讶，也许他是被囚禁或是在更强大的力量控制之下。$B$B时间会证明一切的…………', '安拓維奇，$B$B\r\n首先，讓我們來說說你上一個派來的人，$n，$g他:她;在工作上的表現已經遠超過我的預期了。我已經佔據了$g他:她;的身體，並且用$g他:她;的手來寫這封信。這對我來說是多大的享受啊，我希望有一天我能有幸佔據你的身體，強大的巫醫!$B$B\r\n所以，你要求我所做的報告如下：在地獄火堡壘內肆虐的惡魔比我過去遭遇過的強大許多。我猜想在第三次大戰時著名的地獄領主瑪諾洛斯就掌握了同樣的力量，但擁有這股力量的生物屈指可數。然而，我感覺到有一股禁錮之風圍繞著這個惡魔。如果它做出了違背其意願的事情，我一點都不會感到驚訝，也許他是被囚禁或是在更強大的力量控制之下。$B$B\r\n時間會證明一切的…………', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3000', null, null, null, '在地狱火堡垒的高墙内，我也感觉到命运的移转，就像他们说的一样，时间会证明一切。$B$B你将会在此见识到恶魔的面容，来自艾泽拉斯的外来者，那里的繁茂生机，对于我来说就像是充满圣光与生命的地狱。如果你能允许我用比喻的话，你会明白在地狱火堡垒中的邪恶生物，只不过是巨大冰山的一角。$B$B喔，在我面前的是一场多么荣耀的战斗啊!也许只有我可以品尝到即将被执行的痛苦与恐怖!$B$B呃，我应该结束我们之间乏味的对话，并且让你慢慢想清楚以下的文字，亲爱的药剂师：你的身体可能是死的，安拓维奇，但是你的灵魂仍然十分完整，在你的防守发生失误，并且让我有机会接近你的那天，我应该啃食你的灵魂，并且在极佳的开胃菜搭配下，尽情的享用!如果必须的话，我会等到审判的第四天，但是，你会是我的!$B$B晚安,$B-D', '在地獄火堡壘的高牆內，我也感覺到命運的移轉，就像他們說的一樣，時間會證明一切。$B$B\r\n你將會在此見識到惡魔的面容，來自艾澤拉斯的外來者，那裡的繁茂生機，對於我來說就像是充滿聖光與生命的地獄。如果你能允許我用比喻的話，你會明白在地獄火堡壘中的邪惡生物，只不過是巨大冰山的一角。$B$B\r\n喔，在我面前的是一場多麼榮耀的戰鬥啊!也許只有我可以品嚐到即將被執行的痛苦與恐怖!$B$B\r\n呃，我應該結束我們之間乏味的對話，並且讓你慢慢想清楚以下的文字，親愛的藥劑師：你的身體可能是死的，安拓維奇，但是你的靈魂仍然十分完整，在你的防守發生失誤，並且讓我有機會接近你的那天，我應該啃食你的靈魂，並且在極佳的開胃菜搭配下，盡情的享用!如果必須的話，我會等到審判的第四天，但是，你會是我的!$B$B\r\n晚安,$B\r\n-D', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3001', null, null, null, '<在这张严重烧毁的页面上已经没有任何可以辨识的字迹了。>', '<在這張嚴重燒燬的頁面上已經沒有任何可以辨識的字跡了。>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3002', null, null, null, '自从你离开永恒树林后，我继续进行乌鸦圣典的研究，并发现一些有助于你打败乌鸦神的资讯。 $B$B解放这些灵魂已经将他们跟你绑束在一起，因此我认为他们可以自由的在对抗乌鸦神的战斗中提供他们的协助。 $B$B当你第一次解放灵魂时，他们将会处于休眠状态，因为他们几世纪以来都是如此。使用你的持续性疗愈法术，你可以以能量灌注他们，给予他们生命并在战斗中得到他们的协助。当魔法消退，他们将回复休眠状态。 $B$B接下来就是一系列我对每个鸟灵的注记:', '自從你離開永恆樹林後，我繼續進行烏鴉聖典的研究，並發現一些有助於你打敗烏鴉神的資訊。 \r\n\r\n解放這些靈魂已經將他們跟你綁束在一起，因此我認為他們可以自由的在對抗烏鴉神的戰鬥中提供他們的協助。 \r\n\r\n當你第一次解放靈魂時，他們將會處於休眠狀態，因為他們幾世紀以來都是如此。使用你的持續性療愈法術，你可以以能量灌注他們，給予他們生命並在戰鬥中得到他們的協助。當魔法消退，他們將回復休眠狀態。 \r\n\r\n接下來就是一系列我對每個鳥靈的注記:', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3003', null, null, null, '猎隼灵魂$B$B猎隼灵魂充满了惊人的速度跟敏捷，在阿拉卡所关注的猎人技术当中受到高度的重视。如果你以持续性疗愈法术给予灵魂能量，它将赋予你于法术施放及近身战斗方面有一定程度的速度。', '獵隼靈魂\r\n\r\n獵隼靈魂充滿了驚人的速度跟敏捷，在阿拉卡所關注的獵人技術當中受到高度的重視。如果你以持續性療愈法術給予靈魂能量，它將賦予你於法術施放及近身戰鬥方面有一定程度的速度。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3004', null, null, null, '战鹰灵魂$B$B阿拉卡传统将战鹰灵魂配置得不似它的猎隼兄弟一样迅速，不过它所掌管却是狩猎中最高超的技术。如果你使用持续性疗愈法术使他从休眠中苏醒，战鹰灵魂将会使用它的技术来对抗你的敌人。 $B$B。', '戰鷹靈魂\r\n\r\n阿拉卡傳統將戰鷹靈魂配置得不似它的獵隼兄弟一樣迅速，不過它所掌管卻是狩獵中最高超的技術。如果你使用持續性療愈法術使他從休眠中甦醒，戰鷹靈魂將會使用它的技術來對抗你的敵人。 \r\n\r\n。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3005', null, null, null, '猎鹰灵魂$B$B石板上谈到很多有关猎鹰的事，好像他曾经是乌鸦的盟友。一个远古阿拉卡的复仇符号，猎鹰灵魂常常被罪恶的受害者召唤。使用持续性疗愈法术来唤醒这个灵魂将会使你能够分享它的力量，毁坏那些伤害你的人。', '獵鷹靈魂\r\n\r\n石板上談到很多有關獵鷹的事，好像他曾經是烏鴉的盟友。一個遠古阿拉卡的復仇符號，獵鷹靈魂常常被罪惡的受害者召喚。使用持續性療愈法術來喚醒這個靈魂將會使你能夠分享它的力量，毀壞那些傷害你的人。\r\n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3006', null, null, null, '以我们伟大主人之名……伊利丹大人万岁！$B$B莫格霍尔：$B$B我现在将这个废物派遣给你，让他在你那里继续为伊利丹大人效力。他没有成为死亡骑士的才能，不过，或许具备学习你那些能力的潜质。殴打他，羞辱他，去得到他的服从吧。让他成为一名真正的伊利达雷战士。$B$B假如他失败了的话……那么一切都归咎于他自身的懦弱与愚蠢……那时候直接抛弃他好了。把他扔进深渊！$B$B死亡骑士领主，塔隆·血魔', '以我們偉大主人之名……伊利丹大人萬歲！\r\n\r\n莫格霍爾：\r\n\r\n我現在將這個廢物派遣給你，讓他在你那裡繼續為伊利丹大人效力。他沒有成為死亡騎士的才能，不過，或許具備學習你那些能力的潛質。毆打他，羞辱他，去得到他的服從吧。讓他成為一名真正的伊利達雷戰士。\r\n\r\n假如他失敗了的話……那麼一切都歸咎於他自身的懦弱與愚蠢……那時候直接拋棄他好了。把他扔進深淵！\r\n\r\n死亡騎士領主，塔隆·血魔', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3007', null, null, null, '不要绝望。你的特殊巨魔就在那里的某个地方。', '不要絕望。你的特殊巨魔就在那裡的某個地方。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3008', null, null, null, '扔掉你的利剑，你的重锤，你的长枪。水晶告诉我们，要不杀，要和平。', '放下你的劍，你的槌，和你的矛。水晶告訴我們給和平一次機會。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3009', null, null, null, '你不用怕一个满脑子啤酒的人!', '你不用怕一個滿腦子啤酒的人!', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3010', null, null, null, '三个臭皮匠胜过一个诸葛亮!然后你就会永远都有朋友。', '三個臭皮匠勝過一個諸葛亮!然後你就會永遠都有朋友。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3011', null, null, null, '智慧有很多种表现，但它永远源自水晶。', '智慧有很多種表現，但它永遠源自水晶。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3012', null, null, null, '正面点思考!一个人的垃圾会是另一个人的珍宝。', '正面點思考!一個人的垃圾會是另一個人的珍寶。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3013', null, null, null, '你明天会遇到一个特别的人，所以记住:和蔼可亲会比拳打脚踢为你带来更多朋友。', '你明天會遇到一個特別的人，所以記住:和藹可親會比拳打腳踢為你帶來更多朋友。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3014', null, null, null, '幸运号码: 13 9 17 4 n', '幸運號碼: 13 9 17 4 n', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3015', null, null, null, '你对舞蹈的热爱令所有人面带微笑。微笑。', '你跳舞的動作讓大家臉上都浮現一抹微笑。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3016', null, null, null, '你很特别，不管你的朋友，亲戚，同事，路上的陌生人，或是你的良心是怎么告诉你的', '你很特別，不管你的朋友，親戚，同事，路上的陌生人，或是你的良心是怎麼告訴你的', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3017', null, null, null, '当所有人都失败的时候，就是你大展身手的时候。', '當所有人都失敗的時候，就是你大展身手的時候。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3018', null, null, null, '酒醉就离天国不远了。', '酒醉就離天國不遠了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3019', null, null, null, '警告：内脏并非安全可靠之食品。', '警告:內臟不適人類食用。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3020', null, null, null, '你被天空中最棒的金属之手选中!现在快去工作。', '你被天空中最棒的金屬之手選中!現在快去工作。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3021', null, null, null, '让沉睡的古罗死去。', '讓沉睡的古羅死去。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3022', null, null, null, '这则讯息是欧格利拉开发委员会带给你的。加基森制造', '這則訊息是歐格利拉開發委員會帶給你的。加基森製造', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3023', null, null, null, '生命的意义是......(剩下的讯息意外地被吃掉了。)', '生命的意義是......(剩下的訊息意外地被吃掉了。)', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3024', null, null, null, '抱歉，这次你不是赢家。请再玩一次', '抱歉，這次你不是贏家。請再玩一次', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3025', null, null, null, '你今天会树敌，并立刻杀了它。', '你今天會樹敵，並立刻殺了它。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3027', null, null, null, '寻找生命中更崇高的一条道路。在那条路上，你可以避开一些鄙恶之人', '尋找生命中更崇高的一條道路。在那條路上，你可以避開一些鄙惡之人', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3028', null, null, null, '以我们伟大主人之名……伊利丹大人万岁！$B$B这些灵魂火炮的一般零件都是直接由各类工匠直接制造出来的。比如，技师和矿工能供应魔钢锭、氪金能量核心和精金框架给你。$B$B但如果要想得到完美的奥术精华，就要去泰罗卡森林了。在森林中央那处部落据点裂石堡的上方有一面湖泊——尤鲁恩湖，你可以在那里找到一位名叫萨提斯的鸦人，他被放逐了，躲避在那里。$B$B去和他谈谈，然后把你的需要告诉他。记得说明是我派你去的，因为他欠我不少东西，这么说肯定对你有所帮助。$B$B之后，按照萨提斯说的去做，你就一定能得到精华。$B$B- 巴塔斯', '以我們偉大主人之名……伊利丹大人萬歲！\r\n\r\n這些靈魂火炮的一般零件都是直接由各類工匠直接製造出來的。比如，技師和礦工能供應魔鋼錠、氪金能量核心和精金框架給你。\r\n\r\n但如果要想得到完美的奧術精華，就要去泰羅卡森林了。在森林中央那處部落據點裂石堡的上方有一面湖泊——尤魯恩湖，你可以在那裡找到一位名叫薩提斯的鴉人，他被放逐了，躲避在那裡。\r\n\r\n去和他談談，然後把你的需要告訴他。記得說明是我派你去的，因為他欠我不少東西，這麼說肯定對你有所幫助。\r\n\r\n之後，按照薩提斯說的去做，你就一定能得到精華。\r\n\r\n- 巴塔斯', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3029', null, null, null, '在我的记忆中，利爪之王艾吉斯总是随身携带着泰罗克的羽毛。他将这羽毛视为自身王权的象征，或许还用来向他的追随者显示他就是泰罗克的转世。$B$B而我的兄弟黑暗编织者塞斯则一直戴着泰罗克的面具，领导着大厅内的塞泰克仪式。$B$B至于泰罗克的传说，则一直保存在利爪之王艾吉斯自己的房间旁边的一处宝库里，放置在数个符石组成的法阵中央。', '在我的記憶中，利爪之王艾吉斯總是隨身攜帶著泰羅克的羽毛。他將這羽毛視為自身王權的象徵，或許還用來向他的追隨者顯示他就是泰羅克的轉世。$B$B而我的兄弟黑暗編織者塞斯則一直戴著泰羅克的面具，領導著大廳內的塞泰克儀式。$B$B至於泰羅克的傳說，則一直保存在利爪之王艾吉斯自己的房間旁邊的一處寶庫裡，放置在數個符石組成的法陣中央。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3030', null, null, null, '玛诺洛斯的影响范围没有那么大，绝对不可能达到外域。再说了，那只巨兽早就已经死了。所以，我认为这里恐怕还有其他的深渊领主，而且至少是玛诺洛斯那一级别的深渊领主……强大到足以腐蚀这里的兽人。$B$B我会继续研究我手中的血环氏族血样，不过，我也建议你继续追究调查下去。根据我的推测，线索应该会将你领向地狱火堡垒的深处。$B$B你睿智的顾问，$B药剂师阿尔布雷克', '瑪諾洛斯的影響範圍沒有那麼大，絕對不可能達到外域。再說了，那隻巨獸早就已經死了。所以，我認為這裡恐怕還有其他的深淵領主，而且至少是瑪諾洛斯那一級別的深淵領主……強大到足以腐蝕這裡的獸人。\r\n\r\n我會繼續研究我手中的血環氏族血樣，不過，我也建議你繼續追究調查下去。根據我的推測，線索應該會將你領向地獄火堡壘的深處。\r\n\r\n你睿智的顧問，\r\n藥劑師阿爾佈雷克', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3031', null, null, null, '听好了，巴德！ 如果你再不付钱，这就是我给你送去的最后一个蠢货了！ 不许再拖款了！ 格里弗塔', '听好了，巴德！ 如果你再不付钱，这就是我给你送去的最后一个蠢货了！ 不许再拖款了！ 格里弗塔', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3032', null, null, null, '不知道是否满意，抱歉- UDB团队', '不知道是否满意，抱歉- UDB团队', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3033', null, null, null, '长者，我已经调遣了一支相当规模的军队来协助你，但你必须小心行事。当前，你的首要任务是阻止联盟渣滓继续侵犯我们的土地。有一群胆大妄为的人类竟然在贫瘠之地边界处修建起了一幢大型建筑物。 让这帮家伙明白，他们是不受欢迎的入侵者，统统滚回塞拉摩岛去。最后，我警告你：别让蕨墙村的食人魔知道你的计划。那帮野蛮的家伙连最基本的判断力都没有。 - 阿加希姆', '长者，我已经调遣了一支相当规模的军队来协助你，但你必须小心行事。当前，你的首要任务是阻止联盟渣滓继续侵犯我们的土地。有一群胆大妄为的人类竟然在贫瘠之地边界处修建起了一幢大型建筑物。 让这帮家伙明白，他们是不受欢迎的入侵者，统统滚回塞拉摩岛去。最后，我警告你：别让蕨墙村的食人魔知道你的计划。那帮野蛮的家伙连最基本的判断力都没有。 - 阿加希姆', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3034', null, null, null, '我亲眼看到了其中一个最惊恐的生物泰瑟尔。 我第一次只看见了野兽，并且我一直在祈祷自己能幸存下来？$B$B这是黑暗和风雨如磐的黑夜。 通过旋转的海浪和大雨， 瑟里摩尔的灯塔的导航台已经几乎看不见了。 我们尽力的控制着航向。 突然，在波浪之间的野兽在我们的船前面爆炸! ', '歡迎你使用大芒果漢化數據庫。\r\n\r\n本漢化成果的作者有：\r\nCOCELY\r\nFIVER\r\n英倫\r\n快樂雞蛋王子\r\n空穴來楓\r\nSKY\r\nTiwe\r\n\r\n\r\n有問題請聯繫：\r\ncocely@gmail.com', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3035', null, null, null, '强大鳔浮生物攻入进来，它们翻腾着螺栓冰冷的水将我们的船围住。那些可怕的大白鲨占领船头，并开始撕裂船体。船员和货物被残暴地击入海中，船的残余下方的波浪飞溅着巨浪。$B$B最后我只记得当时飞机从破碎的船体下消失，被黑色水击翻并沉入巨浪之中。世界一片黑暗，我似乎感觉到我的最后一息。当我在瑟里摩尔的疗养院醒来，我知道当时有人已经讲诉了我们的故事。$B', '强大鳔浮生物攻入进来，它们翻腾着螺栓冰冷的水将我们的船围住。那些可怕的大白鲨占领船头，并开始撕裂船体。船员和货物被残暴地击入海中，船的残余下方的波浪飞溅着巨浪。$B$B最后我只记得当时飞机从破碎的船体下消失，被黑色水击翻并沉入巨浪之中。世界一片黑暗，我似乎感觉到我的最后一息。当我在瑟里摩尔的疗养院醒来，我知道当时有人已经讲诉了我们的故事。$B', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3037', null, null, null, '孩子：约珥和吉娜。妻子：苏珊娜', '孩子：约珥和吉娜。妻子：苏珊娜', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3039', null, null, null, '不要站立在我的坟墓啜泣，我没有沉睡在那里。我如千丝的风略过整个诺斯然德的明亮和洁白的雪。 我如丝丝细雨，飘过金色粮食的韦斯特福尔的领域。飘过早晨静寂的 Stranglethorn郁郁葱葱的绿色丛林。我在响亮而隆重的鼓声和雷鸣般的掌声中飘过整个纳格兰。我如闪烁的星星，沉睡在达纳苏斯的歌声里。我如鸟儿在诉说着每一个可爱的故事。不要站立在我的坟墓啜泣，我不在那里。 我没有死。 ', '不要站立在我的坟墓啜泣，我没有沉睡在那里。我如千丝的风略过整个诺斯然德的明亮和洁白的雪。 我如丝丝细雨，飘过金色粮食的韦斯特福尔的领域。飘过早晨静寂的 Stranglethorn郁郁葱葱的绿色丛林。我在响亮而隆重的鼓声和雷鸣般的掌声中飘过整个纳格兰。我如闪烁的星星，沉睡在达纳苏斯的歌声里。我如鸟儿在诉说着每一个可爱的故事。不要站立在我的坟墓啜泣，我不在那里。 我没有死。 ', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3044', null, null, null, '早班，第七次报钟\r\n发现塞德瑞克又躲在酒窖里。惩罚之。', '早班，第七次报钟\r\n发现塞德瑞克又躲在酒窖里。惩罚之。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3076', null, null, null, '<在这个严重烧毁的页面上没有可以辨识的字。>', '<在这个严重烧毁的页面上没有可以辨识的字。>', null, null, null);
INSERT INTO `locales_page_text` VALUES ('1992', 'As the fires of the Second War died down, the Alliance took aggressive steps to contain the orcish threat. A number of large internment camps, meant to house the captive orcs, were constructed in southern Lordaeron. Guarded by both the paladins and the veteran soldiers of the Alliance, the camps proved to be a great success.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1993', 'Though the captive orcs were tense and anxious to do battle once more, the various camp wardens, based at the old prison-fortress of Durnholde, kept the peace and maintained a strong semblance of order.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1994', 'However, on the hellish world of Draenor, a new orcish army prepared to strike at the unsuspecting Alliance. Ner\'zhul, the former mentor of Gul\'dan, rallied the remaining orc clans under his dark banner. Aided by the Shadowmoon clan, the old shaman planned to open a number of portals on Draenor that would lead the Horde to new, unspoiled worlds.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1995', 'To power his new portals, he needed a number of enchanted artifacts from Azeroth. To procure them, Ner\'zhul reopened the Dark Portal and sent his ravenous servants charging through it.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1996', 'The new Horde, led by veteran chieftains such as Grom Hellscream and Kilrogg Deadeye (of the Bleeding Hollow clan), surprised the Alliance defense forces and rampaged through the countryside. Under Ner\'zhul\'s surgical command, the orcs quickly rounded up the artifacts that they needed and fled back to the safety of Draenor.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1997', 'King Terenas of Lordaeron, convinced that the orcs were preparing a new invasion of Azeroth, assembled his most trusted lieutenants. He ordered General Turalyon and the archmage, Khadgar, to lead an expedition through the Dark Portal to put an end to the orcish threat once and for all. Turalyon and Khadgar\'s forces marched into Draenor and repeatedly clashed with Ner\'zhul\'s clans upon the ravaged Hellfire Peninsula.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1998', 'Even with the aid of the high elf Alleria Windrunner, the dwarf Kurdran Wildhammer, and the veteran soldier Danath Trollbane, Khadgar was unable to prevent Ner\'zhul from opening his portals to other worlds.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1999', 'Ner\'zhul finally opened his portals to other worlds, but he did not foresee the terrible price he would pay. The portals\' tremendous energies began to tear the very fabric of Draenor apart. As Turalyon\'s forces fought desperately to return home to Azeroth, the world of Draenor began to buckle in upon itself. Grom Hellscream and Kilrogg Deadeye, realizing that Ner\'zhul\'s mad plans would doom their entire race, rallied the remaining orcs and escaped back to the relative safety of Azeroth.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2000', 'On Draenor, Turalyon and Khadgar agreed to make the ultimate sacrifice by destroying the Dark Portal from their side. Though it would cost their lives, and the lives of their companions, they knew that it was the only way to ensure Azeroth\'s survival. Even as Hellscream and Deadeye hacked their way through the human ranks in a desperate bid for freedom, the Dark Portal exploded behind them. For them, and the remaining orcs on Azeroth, there would be no going back.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3065', 'Loyal servants of the elemental lords, OUR TIME IS NOW.\n\nToo long we have languished in the shadows, biding our time, serving our masters, seeking the end we know must come. Now, as the flames of Ragnaros\' Appeasement burn brightly through the night, we have in our grasp the tools to incite war and chaos on a cataclysmic scale!\n\nThe Firelord is imprisoned in our world. He is not at his full power, but his might is formidable. Given an equally formidable opponent in this realm, the resulting clash would begin the great elemental war that will bring about the end we have sought.', null, null, '元素领主忠诚的仆从们，时机已经成熟！\r\n\r\n多年来，我们陪伴着主人蛰伏在黑暗中，怀着坚定的信念，耐心地期盼着这一天的到来。如今，拉格纳罗斯的烈焰照亮了静谧的夜晚，就让我们引爆战争的导火索，将世界推向混乱的颠峰吧！\r\n\r\n被禁锢的火焰领主虽然无法释放全部的力量，但他的威力依然不容小觑。如果能找来一名旗鼓相当的对手，这场冲突势必会引发大规模的元素战争，引领我们取得最终的胜利。', '元素领主忠诚的仆从们，时机已经成熟！\r\n\r\n多年来，我们陪伴着主人蛰伏在黑暗中，怀着坚定的信念，耐心地期盼着这一天的到来。如今，拉格纳罗斯的烈焰照亮了静谧的夜晚，就让我们引爆战争的导火索，将世界推向混乱的颠峰吧！\r\n\r\n被禁锢的火焰领主虽然无法释放全部的力量，但他的威力依然不容小觑。如果能找来一名旗鼓相当的对手，这场冲突势必会引发大规模的元素战争，引领我们取得最终的胜利。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3066', 'In Neptulon\'s service is a great frost lord by the name of Ahune. Even now he is marshalling his power to wage war against Ragnaros. All he requires is a gateway into our world; a gateway we will provide. We have the allies. We have the location. We have the strength and the will.\n\nFinal negotiations with our new faithful allies and guests will be conducted in a safe, out of the way location northwest of our primary location in Ashenvale.\n\nBefore this \"festival\" of the ignorant masses comes to a close, Ahune shall face Ragnaros in the shadow of Blackrock. The world will quake with the forces unleashed.', null, null, '猎潮者手下有一位名叫埃霍恩的冰霜领主。眼下他正集结力量，想与拉格纳罗斯抗衡。只要有一道通往火焰领主所在位面的传送门，好戏就能上演。就让我们来开启传送门，结交这位新盟友，占尽天时地利人和吧。\r\n\r\n为谨慎起见，我们决定在灰谷据点西北方一处偏僻的场所，与冰霜领主进行最后的协商。\r\n\r\n趁那帮愚昧无知的凡人欢庆“节日”时，埃霍恩将在黑石塔之底迎战拉格纳罗斯。大地会在两股巨大力量的冲击碰撞下颤抖。', '猎潮者手下有一位名叫埃霍恩的冰霜领主。眼下他正集结力量，想与拉格纳罗斯抗衡。只要有一道通往火焰领主所在位面的传送门，好戏就能上演。就让我们来开启传送门，结交这位新盟友，占尽天时地利人和吧。\r\n\r\n为谨慎起见，我们决定在灰谷据点西北方一处偏僻的场所，与冰霜领主进行最后的协商。\r\n\r\n趁那帮愚昧无知的凡人欢庆“节日”时，埃霍恩将在黑石塔之底迎战拉格纳罗斯。大地会在两股巨大力量的冲击碰撞下颤抖。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3072', 'All our toils have worked toward this moment. Our masters will rise against one another in one glorious battle that will tear Azeroth asunder.\n\nWe live in the end times, my brothers. Hold to your tasks. Strive on. We will soon be triumphant!\n', null, null, '我们付出的所有艰辛，只为迎接这一刻的到来。主人们将从黑暗中显身，艾泽拉斯世界将因这场旷古之战变得支离破碎。\r\n\r\n兄弟们，世界末日就要来临。牢记你们的使命，战斗吧。我们很快就会迎来胜利的曙光！', '我们付出的所有艰辛，只为迎接这一刻的到来。主人们将从黑暗中显身，艾泽拉斯世界将因这场旷古之战变得支离破碎。\r\n\r\n兄弟们，世界末日就要来临。牢记你们的使命，战斗吧。我们很快就会迎来胜利的曙光！', null, null, null);
INSERT INTO `locales_page_text` VALUES ('9999', 'Howdy! You have found a loot bug in UDB. We are terribly sorry about that, and we will try and fix it just as SOON as possible. However, You need to help us a little bit. If you will go to http://www.udbforums.org and fill out a loot bug report, with the location you found this, we will try and get it fixed ASAP. Thanks, the UDB team!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2945', '<HTML>\n<BODY>\n<BR/>\n<BR/>\n<H1 align=\"center\">THE GREEN HILLS OF STRANGLETHORN</H1>\n<BR/>\n<H3 align=\"center\">by Hemet Nesingwary</H3>\n</BODY>\n</HTML>', null, null, '荆棘谷的青山\r\n\r\n\r\n\r\n\r\n- 赫米特·奈辛瓦里', '荆棘谷的青山\r\n\r\n\r\n\r\n\r\n- 赫米特·奈辛瓦里', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2946', 'Our first day went as well as one can expect first days to go. Most of our time was preoccupied with making the necessary arrangements to establish a base camp. I located an ideal setting by a freshwater river inlet. Judging by the old, abandoned docks nearby, this site was inhabited sometime ago. As for the original inhabitants, only time can tell that tale.', null, null, '第一天和我们预想的完全一样。我们大部分时间都忙于为建立营地做一些必要安排。我在一条淡水河的入口处找到了一个理想的驻扎点。根据旁边被废弃的古老码头判断，这个地方曾经有人住过，不过只有时间才能说明这里究竟住过什么人。', '第一天和我们预想的完全一样。我们大部分时间都忙于为建立营地做一些必要安排。我在一条淡水河的入口处找到了一个理想的驻扎点。根据旁边被废弃的古老码头判断，这个地方曾经有人住过，不过只有时间才能说明这里究竟住过什么人。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2947', 'For this expedition I have assembled Ajeck Rouack and Sir S. J Erlgadin, along with my trusted servant, Barnil Stonepot. I fought alongside Ajeck\'s father\'s side in many battles in defense of the Alliance. Seeing her grown is quite special. Her father schooled her well in the ways of weaponry. Her skills with a bow make me wonder if there is elven blood running through those veins.', null, null, '我为这次探险召集了阿杰克·罗欧克、埃尔拉丁爵士和我忠实的仆人巴尼尔·石罐。我曾经在保卫联盟的战斗中与阿杰克的父亲并肩作战，看着阿杰克成长起来让我有种很特别的感觉，她父亲对她进行了严格的武器技能训练。看到她熟练地使用弓箭，我不禁怀疑她的血管里是否流淌着精灵的血。', '我为这次探险召集了阿杰克·罗欧克、埃尔拉丁爵士和我忠实的仆人巴尼尔·石罐。我曾经在保卫联盟的战斗中与阿杰克的父亲并肩作战，看着阿杰克成长起来让我有种很特别的感觉，她父亲对她进行了严格的武器技能训练。看到她熟练地使用弓箭，我不禁怀疑她的血管里是否流淌着精灵的血。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2948', 'Sir S. J. Erlgadin comes from human aristocracy. His father, Count Erlgadin, was renowned for his generosity. It was the Count who lobbied for improved working conditions for the Stonemasons\' Guild during the restoration of Stormwind after the Second Great War.', null, null, '埃尔拉丁爵士来自人类的贵族家庭。他的父亲埃尔拉丁伯爵以慷慨大方而著称。第二次兽人战争之后，正是因为他父亲的游说和疏通，石工协会才在重建暴风城时获得了更好的工作条件。', '埃尔拉丁爵士来自人类的贵族家庭。他的父亲埃尔拉丁伯爵以慷慨大方而著称。第二次兽人战争之后，正是因为他父亲的游说和疏通，石工协会才在重建暴风城时获得了更好的工作条件。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2949', 'In the years that followed, after Stormwind betrayed the Stonemasons\' Guild, Sir Erlgadin grew bitter about the role of the nobles within the Kingdom. He no longer wished to uphold the position that his father\'s bloodline had earned for him in the House of Nobles.\n\nBut I digress. The purpose of this story is not to act as a political treatise or a biography. This is the account of my experiences hunting big game in the green hills of Stranglethorn....', null, null, '接下来的几年，在暴风城背叛了石工协会后，埃尔加丁爵士开始厌恶他的贵族身份。他不愿因继承了他父亲的高贵血统而在贵族中获得崇高的地位。不过我跑题了。这个故事的主题不是政治文章或者人物传记，而是要叙述我在荆棘谷的青山中猎捕的经历。', '接下来的几年，在暴风城背叛了石工协会后，埃尔加丁爵士开始厌恶他的贵族身份。他不愿因继承了他父亲的高贵血统而在贵族中获得崇高的地位。不过我跑题了。这个故事的主题不是政治文章或者人物传记，而是要叙述我在荆棘谷的青山中猎捕的经历。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2950', 'We rose with the sun. Barnil began to prepare the morning meal. I noticed Ajeck\'s attention was somewhat distracted. The day\'s trek would be long and our hunt would bring us close to danger. A lack of focus could easily lead to an errant mishap. Yet Ajeck seemed unable to divert her gaze from Barnil who stood by the edge of the river rinsing out his mess kit.', null, null, '天一亮我们就起来了，巴尼尔开始准备早饭。我注意到阿杰克有点心烦意乱。今天的旅途将会漫长而危险，我们的狩猎行动也许会把我们带向危险。注意力不集中就很容易导致错误，甚至是灾难。阿杰克一直都在盯着在河边清洗餐具的巴尼尔。', '天一亮我们就起来了，巴尼尔开始准备早饭。我注意到阿杰克有点心烦意乱。今天的旅途将会漫长而危险，我们的狩猎行动也许会把我们带向危险。注意力不集中就很容易导致错误，甚至是灾难。阿杰克一直都在盯着在河边清洗餐具的巴尼尔。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2951', 'Just as I was about to question Ajeck\'s lack of interest in the day\'s hunting strategy, she reached for her quiver, drew an arrow and unleashed a shot right towards poor Barnil. But it was not Barnil that Ajeck was shooting at. For when Barnil stepped aside, mouth agape, a large river crocilisk floated to the surface with Ajeck\'s arrow perfectly placed between his two large eyes.', null, null, '正在我准备问阿杰克为什么一整天都无精打采的时候，她突然从箭囊中抽出一支箭，向可怜的巴尼尔射去——当然她的目标并非巴尼尔。瞠目结舌的巴尼尔向后退了几步，看着自己身后的一只巨大的鳄鱼浮出水面，阿杰克的箭正好射在了它的两只眼睛的中间。', '正在我准备问阿杰克为什么一整天都无精打采的时候，她突然从箭囊中抽出一支箭，向可怜的巴尼尔射去——当然她的目标并非巴尼尔。瞠目结舌的巴尼尔向后退了几步，看着自己身后的一只巨大的鳄鱼浮出水面，阿杰克的箭正好射在了它的两只眼睛的中间。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2952', 'We set out toward the west, through the thick overgrowth of the tangled jungle. Moving with slow, deliberate steps we paced through thick foliage in search of prey. The morning passed in frustrated silence. Nothing was stirring in the Vale, not even a breeze. By the afternoon, the expedition had grown restless. Barnil no longer strode with the cautious steps of a predator tracking prey. Rather he clumsily clomped along the path often stepping noisily on dried leaves or fallen branches.', null, null, '我们向西部进发，在繁茂的枝叶之间穿行。我们小心翼翼地走在厚厚的落叶上寻找猎物。整个早上都在令人沮丧的沉默中度过，山谷里没有任何激动人心的东西，连一阵微风都没有。到了下午，探险队开始变得不安，巴尼尔的脚步不再象一个追踪猎物的猎手那样谨慎，而是笨拙地踏着步，', '我们向西部进发，在繁茂的枝叶之间穿行。我们小心翼翼地走在厚厚的落叶上寻找猎物。整个早上都在令人沮丧的沉默中度过，山谷里没有任何激动人心的东西，连一阵微风都没有。到了下午，探险队开始变得不安，巴尼尔的脚步不再象一个追踪猎物的猎手那样谨慎，而是笨拙地踏着步，', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2953', 'During one such misstep, Erlgadin laid a heavy hand on Barnil\'s shoulder. Ajeck and I gave a casual glance, assuming the man was simply giving Barnil a much-needed scolding for his carelessness. Erlgadin, however, gestured slowly with his head toward a nearby fallen tree. Gazing back at us were two piercing black eyes just above a mouthful of razor sharp fangs.', null, null, '埃尔加丁爵士似乎被绊了一下，将手重重地拍在巴尼尔的肩膀上。我和阿杰克瞥了一眼，以为他会责备粗心大意的巴尼尔。然而埃尔加丁爵士却摆了摆头，示意我们观察旁边的一棵倒下的大树。那里正有一双炯炯有神的黑眼睛瞪着我们，眼睛下面还有一口如剃刀般锋利的牙齿。', '埃尔加丁爵士似乎被绊了一下，将手重重地拍在巴尼尔的肩膀上。我和阿杰克瞥了一眼，以为他会责备粗心大意的巴尼尔。然而埃尔加丁爵士却摆了摆头，示意我们观察旁边的一棵倒下的大树。那里正有一双炯炯有神的黑眼睛瞪着我们，眼睛下面还有一口如剃刀般锋利的牙齿。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2954', 'The beast was a male Stranglethorn Tiger. Before I could cock my rifle, Erlgadin raised his crossbow and fired upon the beast. The bolt missed its mark and caught the beast heavily in the left flank. The tiger made a futile attempt to flee but its wound was too grave. The beast stumbled for a few tragic seconds until Barnil finished the kill with a thrown axe.', null, null, '那是一只雄性的荆棘谷猛虎。我还没来得及拿起枪，埃尔拉丁已经拉开他的弩，向那头野兽射出了箭矢。这一下未能射中要害，不过还是重重地射入了猛虎的左腹。老虎想要逃跑，但是它伤得太重，只蹒跚走了几步就被巴尼尔的斧头击中，倒地身亡。', '那是一只雄性的荆棘谷猛虎。我还没来得及拿起枪，埃尔拉丁已经拉开他的弩，向那头野兽射出了箭矢。这一下未能射中要害，不过还是重重地射入了猛虎的左腹。老虎想要逃跑，但是它伤得太重，只蹒跚走了几步就被巴尼尔的斧头击中，倒地身亡。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2955', 'The kill brought about a festive mood amongst the expedition. Barnil poured mead for all to enjoy. But our festivities were short-lived. As we were preparing the corpse for transport back to base camp we were all caught off guard by a horrendous growl. In all my years I have never heard anything so blood curdling.', null, null, '这次成功的猎杀给探险队带来了一些愉快的心情，巴尼尔给大家倒了些蜜酒，但是这种喜悦只是暂时的。当我们准备把尸体运回营地时，丛林里传来了一阵可怕的嚎叫声。在我的一生中，我从来都没有听到过如此可怕的、几乎让人全身血液都凝固的声音。', '这次成功的猎杀给探险队带来了一些愉快的心情，巴尼尔给大家倒了些蜜酒，但是这种喜悦只是暂时的。当我们准备把尸体运回营地时，丛林里传来了一阵可怕的嚎叫声。在我的一生中，我从来都没有听到过如此可怕的、几乎让人全身血液都凝固的声音。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2956', 'On a rocky precipice above, silhouetted by the setting sun, I could make out the largest cat of prey I have ever laid eyes upon. I was able to loose one clumsy volley with my rifle, but the cat held his ground. He growled once again, this time louder than the first, and vanished.\n\nWe gathered our belongings and headed solemnly back to camp.', null, null, '在遍布岩石的悬崖上，一头迄今为止我见过的最巨大的豹子在晚霞的映衬中冒了出来。我笨拙地开了枪，但是那头豹子丝毫未动。它又咆哮了一声，比第一次的声音更大，然后就消失了。\r\n\r\n我们把东西收好，然后神色黯然地返回了营地。', '在遍布岩石的悬崖上，一头迄今为止我见过的最巨大的豹子在晚霞的映衬中冒了出来。我笨拙地开了枪，但是那头豹子丝毫未动。它又咆哮了一声，比第一次的声音更大，然后就消失了。\r\n\r\n我们把东西收好，然后神色黯然地返回了营地。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2957', 'I had promised the expedition that we would spend the next day hunting panthers, as their furs are in high demand throughout Azeroth. It only makes sense that such demand should exist with all of the able-bodied hunters, trappers and fur-traders off giving their lives so valiantly in the name of the Alliance.', null, null, '我向探险队员保证我们第二天将去狩猎黑豹，因为整个艾泽拉斯地区的豹皮都非常抢手而紧俏。正是因为有这种需求的存在，才有那么多的猎人、捕兽者和毛皮商人以联盟的名义勇敢地献出了他们的生命。', '我向探险队员保证我们第二天将去狩猎黑豹，因为整个艾泽拉斯地区的豹皮都非常抢手而紧俏。正是因为有这种需求的存在，才有那么多的猎人、捕兽者和毛皮商人以联盟的名义勇敢地献出了他们的生命。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2958', 'Ajeck and Sir Erlgadin were anxious to learn how to hunt effectively with a Dwarven Rifle. I had the two humans leave their primitive range weapons at base camp. Barnil and I outfitted them with some of Ironforge\'s finest firearms.', null, null, '阿杰克和埃尔加丁爵士很想知道如何有效地使用矮人火枪狩猎。所以我让这两个对此一无所知的人类把他们的弓箭留在营地中，然后让巴尼尔给他们装备了一些铁炉堡最好的武器。', '阿杰克和埃尔加丁爵士很想知道如何有效地使用矮人火枪狩猎。所以我让这两个对此一无所知的人类把他们的弓箭留在营地中，然后让巴尼尔给他们装备了一些铁炉堡最好的武器。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2959', 'On this day we ventured to the south, following some fresh panther tracks. Soon we reached a ravine spanned by a tremendous rope bridge. I could not help but to think of Brann\'s descriptive writings of this region when I saw the engineering marvel. So often it was assumed that the native Trolls were a primitive and uneducated race yet as I gazed upon the master craftsmanship of the bridge I was able to recognize the skill with which the Troll builders overcame the seemingly impossible feat.', null, null, '这一天，我们沿着一些新的黑豹足迹向南进发。很快我们就来到了一座架着巨大索桥的峡谷。看到这个工程奇迹的时候，我忍不住想起了布莱恩对这一带的描写。我们常常认为巨魔是原始而无知的族类，然而当我认识到这座索桥所体现的精湛技术时，我开始认为巨魔的建造技术已经达到令人难以想象的地步。', '这一天，我们沿着一些新的黑豹足迹向南进发。很快我们就来到了一座架着巨大索桥的峡谷。看到这个工程奇迹的时候，我忍不住想起了布莱恩对这一带的描写。我们常常认为巨魔是原始而无知的族类，然而当我认识到这座索桥所体现的精湛技术时，我开始认为巨魔的建造技术已经达到令人难以想象的地步。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2960', 'Before long, Ajeck tracked the panther to the southwest. We walked quietly, guns at the ready, in anticipation of our prey. A snapping of twigs from a nearby copse of trees drew our attention immediately. Something was in there. One stern glance at Barnil was enough to convey my thoughts. Barnil slowly lowered his rifle. This kill was not for us; it was for our Human companions. Countless panthers had lost their lives in front of our smoking barrels. This kill would be for the Humans.', null, null, '很快阿杰克就发现在西南方有猎豹出现。我们紧握住手中的枪，悄无声息地前进，随时准备对付突然冒出来的猎物。附近树丛里发出的树枝断裂的劈啪声立刻引起了我们注意，那里一定有什么东西！我瞥了巴尼尔一眼，他就明白了我的意思。这个猎物不是我们的，而是留给我们的人类同伴的。已经有无数猎豹死在我们的枪管下，这一次轮到人类伙伴们开开杀戒了。', '很快阿杰克就发现在西南方有猎豹出现。我们紧握住手中的枪，悄无声息地前进，随时准备对付突然冒出来的猎物。附近树丛里发出的树枝断裂的劈啪声立刻引起了我们注意，那里一定有什么东西！我瞥了巴尼尔一眼，他就明白了我的意思。这个猎物不是我们的，而是留给我们的人类同伴的。已经有无数猎豹死在我们的枪管下，这一次轮到人类伙伴们开开杀戒了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2961', 'Both Ajeck and Sir Erlgadin stood poised, guns leveled at the bristling overgrowth at the base of the swaying trees. The midday sun beat heavily upon us. A slow trickle of perspiration trailed down from Elrgadin\'s temple as he pulled the pin back. Upon the sound of the click, the thick flora parted and a large black panther -- a beautiful specimen -- darted out onto the plain.', null, null, '阿杰克和埃尔加丁爵士悄无声息地用枪瞄准着正在晃动的树丛。正午的强烈阳光照在我们身上，当埃尔加丁爵士终于扣动扳机的时候，他的额头流下了一丝汗水。一声枪响过后，浓密的树叶被打得乱飞，一头非常漂亮的大黑豹向离弦的箭一般冲向了平原。', '阿杰克和埃尔加丁爵士悄无声息地用枪瞄准着正在晃动的树丛。正午的强烈阳光照在我们身上，当埃尔加丁爵士终于扣动扳机的时候，他的额头流下了一丝汗水。一声枪响过后，浓密的树叶被打得乱飞，一头非常漂亮的大黑豹向离弦的箭一般冲向了平原。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2962', 'The Humans trained their sights on the panther as it ran along the edge of the tree line. The barrels of their guns moved in perfect parallel tandem. Barnil gave me an urging glance but I shook my head no. This hunt was for the Humans, not Barnil or me. Erlgadin fired a booming shot, missing the panther altogether. Apparently he was unprepared for the violent kickback of the rifle blast.', null, null, '当黑豹沿着林木边缘奔走时，有两个猎人一直瞄准着它。他们互相照应着一起行动，巴尼尔看看我，用眼光询问是否要开火，我摇了摇头。这次狩猎行动是要锻炼那些人类，而不是我或者巴尼尔。埃尔拉丁扣动了扳机，但没能打中黑豹。显然，他对猎枪所产生的强大后坐力还没有足够的认识和准备。', '当黑豹沿着林木边缘奔走时，有两个猎人一直瞄准着它。他们互相照应着一起行动，巴尼尔看看我，用眼光询问是否要开火，我摇了摇头。这次狩猎行动是要锻炼那些人类，而不是我或者巴尼尔。埃尔拉丁扣动了扳机，但没能打中黑豹。显然，他对猎枪所产生的强大后坐力还没有足够的认识和准备。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2963', 'His gun tossed back violently in his arms. The barrel swooped sideways and came up beneath Ajeck\'s rifle. Ajeck had chosen that exact moment to pull her trigger. The rifle, the aim now knocked clumsily toward the tree line, went off with a distinct boom. A flock of birds screamed out of the canopy, scattering in every direction. A plume of smoke rose from the tree. We watched in awe as a tremendous branch fell squarely on the fleeing panther, breaking its back', null, null, '他的枪重重地击在他的肩膀上，枪管歪向一边，正好落在在阿杰克的枪下面。阿杰克瞄准树冠适时地扣动了扳机，一声清晰的枪响过后，树冠上的一群鸟尖叫着四处飞散，树上冒出了一阵烟，我们惊异地看到一根巨大的树枝掉落下来，正好砸中了疾驰的黑豹。', '他的枪重重地击在他的肩膀上，枪管歪向一边，正好落在在阿杰克的枪下面。阿杰克瞄准树冠适时地扣动了扳机，一声清晰的枪响过后，树冠上的一群鸟尖叫着四处飞散，树上冒出了一阵烟，我们惊异地看到一根巨大的树枝掉落下来，正好砸中了疾驰的黑豹。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2964', 'As the weeks passed our stockpile of panther and tiger skins grew immense. I decided it was time for the expedition to shift our focus to a new challenge: Raptors.\n\nThe Humans, while appreciative of the training Barnil and I offered, decided to refrain from hunting with firearms. Ajeck was much more comfortable with a finely strung bow and Sir Erlgadin never left camp without his sturdy crossbow.', null, null, '过了几个星期，我们的豹皮和虎皮储备已经相当充足了。于是我决定将探险队的注意力转向一个新的挑战：迅猛龙。\r\n\r\n队伍中的两个人类虽然十分感激我和巴尼尔对他们进行的训练，不过他们还是决定不使用我们提供的火枪去打猎。阿杰克更喜欢她那调校完美的强弓，而埃尔拉丁爵士离开营地的时候总是拿着他的弩。', '过了几个星期，我们的豹皮和虎皮储备已经相当充足了。于是我决定将探险队的注意力转向一个新的挑战：迅猛龙。\r\n\r\n队伍中的两个人类虽然十分感激我和巴尼尔对他们进行的训练，不过他们还是决定不使用我们提供的火枪去打猎。阿杰克更喜欢她那调校完美的强弓，而埃尔拉丁爵士离开营地的时候总是拿着他的弩。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2965', 'We set out at first light, heading south past the Tkashi Ruins. Barnil voiced concern that we might encounter members of the Bloodscalp Tribe. I reminded Barnil that the Bloodscalps were more concerned with destroying their tribal enemy, the Skullsplitters. Needless to say, Barnil was not comforted in the least. I, however, had a loaded rifle, a satchel full of gunpowder and three deadly hunters with me to ease any concerns of an unfriendly ambush.', null, null, '我们在黎明时动身向南出发，一路穿过了伽什废墟。巴尼尔说他担心会遇到血顶部族的巨魔。我告诉巴尼尔，比起生吃我们这些冒险者来，血顶部族更关心的是如何摧毁他们的敌人，碎颅部族。不过巴尼尔显然没有因此而感到一丝的宽慰。但是，我有一把上好膛的火枪、满袋的黑火药，还有三个本领高强的猎人做伴，所以我一点也不担心会遭遇敌人的埋伏。', '我们在黎明时动身向南出发，一路穿过了伽什废墟。巴尼尔说他担心会遇到血顶部族的巨魔。我告诉巴尼尔，比起生吃我们这些冒险者来，血顶部族更关心的是如何摧毁他们的敌人，碎颅部族。不过巴尼尔显然没有因此而感到一丝的宽慰。但是，我有一把上好膛的火枪、满袋的黑火药，还有三个本领高强的猎人做伴，所以我一点也不担心会遭遇敌人的埋伏。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2966', 'I\'ve stood before a towering Infernal on the battlefield, the army of the Burning Legion advancing from all directions. An unruly band of Trolls seems as harmless as a jackrabbit in the hills of Dun Morogh.\n\nWe passed the Tkashi Ruins without event, much to Barnil\'s relief. The party proceeded to head westward, toward the Great Sea, skirting the Ruins of Zul\'Kunda just to the south. As we ascended the high sea bluffs we spotted our first Raptor.', null, null, '我曾经在战场上面对过巨大的地狱火，还有从四面八方冲过来的燃烧军团。如今对我而言，一群蛮横的巨魔就跟丹莫罗的长耳大野兔一样不值一提。\r\n\r\n我们平静地穿过伽什废墟，巴尼尔总算是放下了他那颗惴惴不安的心。我们继续向西边的无尽之海前进，并绕了南边的祖昆达废墟。当我们攀上海边的悬崖之后，第一只迅猛龙出现在我们面前。', '我曾经在战场上面对过巨大的地狱火，还有从四面八方冲过来的燃烧军团。如今对我而言，一群蛮横的巨魔就跟丹莫罗的长耳大野兔一样不值一提。\r\n\r\n我们平静地穿过伽什废墟，巴尼尔总算是放下了他那颗惴惴不安的心。我们继续向西边的无尽之海前进，并绕了南边的祖昆达废墟。当我们攀上海边的悬崖之后，第一只迅猛龙出现在我们面前。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2967', 'The beast never so much as detected our presence. In fact, the only greeting he received from the expedition was a bullet between the eyes.\n\nSir Erlgadin let out a hearty *hurrah* as Ajeck nodded toward me with keen approval. I sifted through my pack in search of my pipe, hoping to enjoy a celebratory smoke. Barnil began to scurry up the hillside to retrieve the Raptor\'s corpse. I stared at the fallen beast with the satisfaction that accompanies every big kill.', null, null, '这头野兽压根没有发现我们的存在，事实上，它从我们这儿收到的第一个问候就是射入它双眼间的子弹。\r\n\r\n埃尔拉丁爵士大声欢呼，阿杰克则赞许地冲我点了点头。我从包里拿出烟斗，想好好庆祝一番。巴尼尔奔上山坡去拖迅猛龙的尸体。我看着被击倒的野兽，心里就像以前每一次杀死猎物时那样充满着兴奋和满足。', '这头野兽压根没有发现我们的存在，事实上，它从我们这儿收到的第一个问候就是射入它双眼间的子弹。\r\n\r\n埃尔拉丁爵士大声欢呼，阿杰克则赞许地冲我点了点头。我从包里拿出烟斗，想好好庆祝一番。巴尼尔奔上山坡去拖迅猛龙的尸体。我看着被击倒的野兽，心里就像以前每一次杀死猎物时那样充满着兴奋和满足。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2968', 'But I could not bask in the glory of the kill for long. For when I turned my eyes toward the horizon, several silhouettes appeared cresting the hill, just above poor Barnil.\n\n*Flee, Barnil!* I shouted. Ajeck, Sir Erlgadin and myself loosed a volley of bullets, arrows and bolts over Barnil and toward the pursuing raptors. One of us landed a kill amidst the confusion.', null, null, '但是我还没有时间享受这次猎杀的愉悦，就赫然发现山顶上出现了几个影子——就在可怜的巴尼尔的正上方。\r\n\r\n但是我还没有时间享受这次猎杀的愉悦，就赫然发现山顶上出现了几个影子——就在可怜的巴尼尔的正上方。\r\n\r\n“快跑，巴尼尔！”我大叫道。阿杰克、埃尔拉丁和我立刻向追逐巴尼尔的迅猛龙射击，一时间子弹和箭矢乱飞，混乱之中，我们当中的某人还射杀了一只迅猛龙。', '但是我还没有时间享受这次猎杀的愉悦，就赫然发现山顶上出现了几个影子——就在可怜的巴尼尔的正上方。\r\n\r\n但是我还没有时间享受这次猎杀的愉悦，就赫然发现山顶上出现了几个影子——就在可怜的巴尼尔的正上方。\r\n\r\n“快跑，巴尼尔！”我大叫道。阿杰克、埃尔拉丁和我立刻向追逐巴尼尔的迅猛龙射击，一时间子弹和箭矢乱飞，混乱之中，我们当中的某人还射杀了一只迅猛龙。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2969', 'Our hastily aimed shots were enough to buy Barnil\'s escape. Barnil clamored back down the hill and rejoined the party. We scurried off into the jungle; a pack of ferocious Lashtail Raptors stalking our every move. \n\nThe hunters were now the hunted.', null, null, '我们匆忙射出的子弹为巴尼尔赢得了足够的逃跑时间。他大声叫嚷着跑下山丘，重新回到队伍之中。我们急忙离开了丛林，因为有一群凶残的鞭尾迅猛龙正在追寻着我们的足迹。\r\n\r\n现在攻守易位，猎手变成了猎物。', '我们匆忙射出的子弹为巴尼尔赢得了足够的逃跑时间。他大声叫嚷着跑下山丘，重新回到队伍之中。我们急忙离开了丛林，因为有一群凶残的鞭尾迅猛龙正在追寻着我们的足迹。\r\n\r\n现在攻守易位，猎手变成了猎物。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2970', 'I led the party toward the sea, hoping the shoreline would provide refuge from the Raptors. In our haste we had drifted too far north, to a precariously high elevation. The mistake was made. The fault was mine. We stopped just short of a sheer cliff, the Raptors just a few paces behind.', null, null, '我带着队伍继续往海边走去，希望在那里的海岸线旁找到某个可以避开迅猛龙的地方。但是在匆忙之中，我们跑到了一处危险的高地，这是个可怕的错误，全都是我的错。我们停在了一个陡峭的悬崖边，而那些迅猛龙距离我们仅有几步之遥。', '我带着队伍继续往海边走去，希望在那里的海岸线旁找到某个可以避开迅猛龙的地方。但是在匆忙之中，我们跑到了一处危险的高地，这是个可怕的错误，全都是我的错。我们停在了一个陡峭的悬崖边，而那些迅猛龙距离我们仅有几步之遥。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2971', 'I stepped slowly forward, gun raised. I had led these brave hunters to their death. I would die defending them. Lashtail Raptors are particularly fierce, known for their unrelenting blood-thirst. They far outnumbered us. But I would be damned if I let them kill me and my comrades without shedding some of their own blood first.', null, null, '我举着枪缓缓地前进。是我将这些勇敢的猎人带向了死亡的边缘，我至死都要保护他们！鞭尾迅猛龙异常凶残，它们一向都以残酷嗜血而闻名，而且现在它们的数量远远多于我们。但是，我决不能就让这些野兽轻易地杀了我和我的同伴。', '我举着枪缓缓地前进。是我将这些勇敢的猎人带向了死亡的边缘，我至死都要保护他们！鞭尾迅猛龙异常凶残，它们一向都以残酷嗜血而闻名，而且现在它们的数量远远多于我们。但是，我决不能就让这些野兽轻易地杀了我和我的同伴。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2972', 'Ajeck and Sir Erlgadin readied their weapons, flanking me on either side, our backs to the sea. Barnil let out a defeated sigh and drew his axe. The Lashtails were almost upon us. Their steady stride had slowed. They were stalking their prey now for they knew they had us trapped.', null, null, '阿杰克和埃尔加丁爵士拿起他们的武器，守在我的两侧，我们的背后就是大海。巴尼尔沮丧地叹了一口长气，然后拿起了他的斧子。迅猛龙在慢慢地逼近，它们高傲地注视着我们，因为它们知道我们已经无路可逃了。', '阿杰克和埃尔加丁爵士拿起他们的武器，守在我的两侧，我们的背后就是大海。巴尼尔沮丧地叹了一口长气，然后拿起了他的斧子。迅猛龙在慢慢地逼近，它们高傲地注视着我们，因为它们知道我们已经无路可逃了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2973', 'And then something miraculous happened. From off to our side we heard the distinct and terrifying roar of the great white tiger. Despite their numbers, the Raptors turned and scattered in all directions. We saw but a brief white flash as the tiger darted past us and pounced on one of the Raptors. No command needed to be given. All four members of our party knew it was time to run.', null, null, '突然之间，奇迹发生了。我们听见一头白虎清晰而凄厉的吼声从对面传来。虽然迅猛龙数量众多，但它们一听到叫声便立刻四散逃窜。那头老虎像一道白色闪电般从我们身前跑过，扑向一只迅猛龙。不需要任何命令和沟通，我们四个人都明白：这正是逃跑的最佳时机。', '突然之间，奇迹发生了。我们听见一头白虎清晰而凄厉的吼声从对面传来。虽然迅猛龙数量众多，但它们一听到叫声便立刻四散逃窜。那头老虎像一道白色闪电般从我们身前跑过，扑向一只迅猛龙。不需要任何命令和沟通，我们四个人都明白：这正是逃跑的最佳时机。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('2974', 'We sprinted all the way back to base camp, never slowing. Later that night we sat quietly around the campfire, knowing our lives had been saved by a bizarre twist of fate. Such are the risks of the big game hunter. We toy with fate by delivering it. Yet each of us, at some point, will face fate\'s razor sharp teeth. This Dwarf is just glad that moment did not come upon the green hills of Stranglethorn.\n\n--The End--', null, null, '我们一路狂奔回到了营地，丝毫不敢放慢速度。深夜，我们围着篝火静静地坐在一起，大家心里都明白，我们是被奇异的命运所救，这就是狩猎高手活动所带来的风险：我们在和命运做游戏。然而，我们中的每一个人都可能会在未来的某一时刻面对命运严峻的考验。我这个老矮人感到高兴的是，这一时刻并非发生在荆棘谷的青山中。\r\n\r\n- 完 -', '我们一路狂奔回到了营地，丝毫不敢放慢速度。深夜，我们围着篝火静静地坐在一起，大家心里都明白，我们是被奇异的命运所救，这就是狩猎高手活动所带来的风险：我们在和命运做游戏。然而，我们中的每一个人都可能会在未来的某一时刻面对命运严峻的考验。我这个老矮人感到高兴的是，这一时刻并非发生在荆棘谷的青山中。\r\n\r\n- 完 -', null, null, null);
INSERT INTO `locales_page_text` VALUES ('80000', 'WAIT $c! Your search for the next boss isn\'t this way. Try another way.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3064', 'The cauldrons, protected by the loyal cultists, would then act as plague-generators, sending the plague seeping out across the unsuspecting farmlands and cities of northern Lordaeron.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3063', 'With Kel\'Thuzad\'s success in Lordaeron, the Lich King made the final preparations for his assault against human civilization. Placing his plague-energies into a number of portable artifacts called plague-cauldrons, Ner\'zhul ordered Kel\'Thuzad to transport the cauldrons to Lordaeron, where they would be hidden within various cult-controlled villages.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('912', 'Get a Bolt Charged Bramble. To get one of those, gather 10 Witherbark Totem Sticks from the Witherbark Trolls of the Arathi Highlands. Bring the sticks to the Circle of Outer Binding, a circle of stones in those same highlands. Place the sticks on the center stone in the circle and wait for lightning to strike it. When it does, the Bolt Charged Bramble will be formed.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1', 'Here is a jar of ashes. These are the ashes of my sanity, my passion, and my drive. All, utterly destroyed by themselves. May all those who look upon these desolated lands of Hellfire remember this fallen peon. He shed blood for the Alliance, and sacrificed for the Horde only to be driven utterly mad by the wicked and soulless ones who devoured what he held most dear. As they feast from his toils, may they suffer his wrath. Maybe not in this world, but in every world hereafter. It is my declaration, my solem oath, and my everlasting promise. I will avenge my suffering.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('8345', 'You have discovered the location of the shrine! Upon further examination, you sense a stronger pulse of the strange power that has gripped the isle. You feel a bit uncomfortable standing by the shrine... and perhaps a little disturbed...$B$BThe bronze placard along the side of the shrine reads:$B$B\"Here stands the Shrine of Dath\'Remar, a fitting tribute to a noble elf. Let all who gaze on this monument remember his sacrifice against the Scourge and his dedication to the cause of our continued survival. All who prosper in Quel\'Thalas do so thanks to him.\"', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3040', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Okay, $N, this is a list of all the stuff that I\'m going to need for our new super-secret, er..., super weapon. For now, let\'s just call it \"Crafty\'s Ultra-Advanced Proto-Typical Shortening Blaster\".</P>\r\n<BR/>\r\n<P>Trust me, it\'s going to be far more revolutionary than that old, hokey world enlarger!</P>\r\n<BR/>\r\n<P>Anyway, don\'t think too hard about it right now. Just get me this stuff and we\'ll be golden!</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3041', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">My Tools</H1>\r\n<BR/>\r\n<P>First things first... I need my tools. For a project this intricate no others will do!</P>\r\n<BR/>\r\n<P>When we had to run for our lives to escape from the pumping station -- that\'s a whole different story -- I didn\'t have time to pick them up. I mean, come on, I was running for my life! But I do remember that I left them under the pumping station to the east.</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3042', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">An Overcharged Capacitor</H1>\r\n<BR/>\r\n<P>Any engineering hack worth their salt should be able to whip one of these up for you if you can\'t create it yourself. Hell, I\'d make one for you, but I\'m missing my tools at the moment, remember?</P>\r\n<BR/>\r\n<P>So, do whatever it takes, but get me one. The project\'s going nowhere without it. If you have to, head back to the southern continents and scour the auction house!</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3043', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">A Handful of Rocknar\'s Grit</H1>\r\n<BR/>\r\n<P>This last bit might be a little tricky to get, but it\'s necessary to fashion the blaster\'s lens from only the finest sand. That means obtaining it from a top-notch elemental.</P>\r\n<BR/>\r\n<P>Rocknar will do nicely. He was spotted to the southeast in the Frozen Reach between Unu\'pe and the Wailing Ziggurat. Go break down his icy exterior to get to his rocky core and bring me back a handful.</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3110', 'To High General Abbendis\r\nNew Avalon, Scarlet Lands\r\n\r\nHigh General Abbendis,\r\n\r\nI hope this letter finds you in good health. My name is Dansel Adams, hunter by trade, Scarlet Crusader by blood. I am writing this letter to inform you that I have witnessed a miracle.\r\n\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3111', 'During a routine purging of heathens in the Scourge infested Plaguewood, my party and I were interrupted by a deafening screech coming from the sky. When we looked to the heavens, towards Naxxramas, our jaws dropped to the floor. I tell you this now, High General, \'twas a sight not to be believed! The dread citadel was moving. Slowly at first and then with a thunderous roar - poof - it was gone!\r\n\r\nThat\'s right, High General, Naxxramas is gone! No more! Could it be anything other than a miracle of the Light? I say not!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3112', 'May it be that the impure, unkempt barbarians, are judged and sentenced next. Praise be the Light!\r\n\r\nPlease let our brothers and sisters know of this miracle, High General.\r\n\r\nMay the Light preserve you for all eternity so that you may spread the good word until the end of days.\r\n\r\nRespectfully,\r\n\r\nD. Adams\r\n\"The Closer\"', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3113', '1. Cultivate a fulsome beard, for it conceals the imperfections of the face from the Light. \r\n\r\n2. Purify your hands in the milk of the mare, that they be soft and supple when handling the offertory. \r\n\r\n3. Bathe in the rivers, allowing the glorious natural gifts of the Light to cleanse your skin. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3114', '4. Replace regularly your holy raiment, that your appearance and odours not offend the servants of the Light. \r\n\r\n5. Maintain at all times an upright posture - let not fatigue nor sloth curl the spine. \r\n\r\n<The list continues for several more pages of minutiae>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3120', 'High General,\r\n\r\nThe armies of Hearthglen and Tirisfal are less than a day\'s ride from New Avalon. We ride with the Light shining upon our backs and the wind at our heels. Soon the Scourge will have to contend with the full might of the Scarlet Crusade!\r\n\r\nLight Bless You,\r\n\r\nHigh Commander Galvar Pureblood', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3103', 'Day 1: The human scum seem blissfully unaware of my presence here, as intended. They\'re ripe for bombing, and it should only be a matter of time before I\'ve charted out their position.\r\n\r\nDay 2: Charting complete - a trivial task for such an accomplished fellow as myself. Why I got stuck with this job is beyond me - they should have J-Y C. do it. That ridiculous accent should have been justification enough. \r\n\r\nDay 3: First breath of air is finally getting a bit stale. Perhaps I should surface momentarily for another in a few days. Still bored to tears - where is that blasted signal?\r\n\r\n< The ranting continues ... > ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3104', 'Day 12: Ah, sweet relief. The second breath tasted like the purest orphan tears I\'ve ever had. The signal remains puzzlingly absent, and I could swear that one of the crabs is attempting to play drums on my big toe. \r\n\r\n< The rants grow more disassociated ... > ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3105', 'Day 36: Third breath of air finally expired, and the veins in my left elbow filled with bubbles again on the way up. The drumming of the crabs is a constant companion now, a percussive backdrop to my blackened little corner of hell. Clearly, I\'ve been forgotten by those wretches. If J-Y were down here, they wouldn\'t have forgotten him! They wouldn\'t have left Hans or Sylvia behind! Why me? \r\n\r\n< The entries trail off in a despondent scrawl ... >', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3119', 'Purify the body with labor. Purify the soul with prayer. Purify your enemies with fire. Pain is not an enemy, but an ally to be embraced, held close, and delivered to unbeliever and believer alike. \r\n\r\nResistance to the ways of the Light is a sign of impurity, and should be excised from the flesh of the community. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3117', 'The topic of indulgences is a difficult one, but at times in our lives, it becomes a necessary evil. A lapse of judgment, a harsh word too quickly spoken, a punch or kick thrown in anger - all these things are failings of mortal men, and the Church must acknowledge that. $B$BHowever, such acknowledgement does not come without a price. For sins of harsh words, a mere handful of silver will cleanse the soul. For sins of physical force, a handful of gold will expurgate the failings of mortal flesh. For those other sins, a full confession and a more generous contribution will be necessary. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3118', 'Punching: $B Punch to the Face : 2 gold$B Punch to the Groin: 3 gold$B Punch in the Chest: 1 gold 45 silver$B $B<A detailed list of prices and sins continues for pages>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3121', 'The Path of Redemption', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3122', 'The voice whispered, \"Come to me.\" From the very beginning I knew that it was the Holy Light speaking to me in dreams. At last! After all of my years of prayers and good deeds, the cleansing of the blight of the unliving from the face of Azeroth. After all of the failures and resurrections.$B$BFinally!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3123', 'It happened again. \"Come to me...\", the Light commanded.$B$BThis time I woke up freezing, but it wasn\'t cold in my chambers. I\'m going to redouble my efforts! I\'ll tell the high abbot tomorrow that I want prayers increased. No more half-measures!$B$BThe Holy Light has taken notice of our good work. I can feel it!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3124', 'This time I was awake! It was very vivid, and yet for over a minute, in the middle of the warm, sunny day, my breath came out misted and chill. One of the priests noticed and dropped to his knees in prayer.$B$BNo one else heard the voice, though. At least the witness proves that I\'m not going insane. Maybe I should ask Landgren to pray on the matter?$B$BI\'ll get Jordan and Street to scrutinize their recruiting efforts tomorrow. We\'ve grown bloated with ranks of unbelievers who yearn only to destroy the undead. That\'s not enough!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3125', 'The commander and the bishop were both receptive. Not that they had any choice. Bishop Street in particular seemed very enthusiastic. He spoke of a new crusade and swore to ferret out the weak of faith within our ranks.$B$BI told him to go easy. We\'re not destroying the Crusade so much as forming an elite cadre of the most faithful to do the Light\'s bidding in Northrend. I fear the man\'s friendship with LeCraft is slowly twisting him. They both have their uses, though.$B$BStreet\'s talk of a new crusade, however, has me thinking. I believe that when this new force is assembled, I will give us a new name. It\'ll have to be evocative of our new mission, yet still provide a tie to the past. I will pray with the high abbot on this.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3126', 'Our prayers have been answered. The high abbot was particularly moved by the force of the Light\'s voice, its clarity, and sense of purpose.$B$BThose of us going north - the most faithful - shall be known as the Scarlet Onslaught.$B$BAnd an onslaught upon Northrend we shall be! The cancer of the Scourge threatens to overflow in the crown of the world and drown the rest of us. The time has come to take the fight directly to the Lich King\'s doorstep.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3127', 'It is with trepidation that I gaze upon New Avalon, I somehow suspect for the last time. The fate of our cause lies in Northrend. I am filled with a sense of ominous foreboding for some reason. The mission ahead should shake away these concerns. I will put them out of my mind.$B$BThe Crimson Dawn is upon us.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3536', 'Majesty,\r\n\r\nO\' merciful king, may the Light stay your hand and steady your heart. Hold your judgment upon this fallen champion - if only for a moment - and hear my words.\r\n\r\nThe bearer of this letter is a former hero of the Alliance. Though $g he:she; carries with $g him:her; the stench of death and the look of the Scourge, $g he:she; has the soul of a champion. A soul that has only recently been reunited with the body.\r\n\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3537', '$N represents a new, united front against the Lich King known as the Knights of the Ebon Blade. Yes, majesty, death knights turned against their former master. They are led by the son of Lord Alexandros Mograine - the Ashbringer. They seek what we seek, what we all seek: the end of the Scourge.\r\n\r\nI ask not that you accept $N and the Knights of the Ebon Blade into the Alliance, but only that you show tolerance.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3538', 'Remember, sire, by blood and honor we all serve.\r\n\r\nRespectfully,\r\n\r\nHighlord Tirion Fordring\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3539', 'Warchief,$B$BMighty Warchief, may the Light stay your hand and steady your heart. Hold your judgment upon this fallen champion - if only for a moment - and hear my words.$B$BThe bearer of this letter is a former hero of the Horde. Though $g he:she carries with $g him:her the stench of death and the look of the Scourge, $g he:she has the soul of a champion. A soul that has only recently been reunited with the body.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3540', '$n represents a new, united front against the Lich King known as the Knights of the Ebon Blade. Yes, Warchief, death knights turned against their former master. They are led by the son of Lord Alexandros Mograine - the Ashbringer. They seek what we seek, what we all seek: the end of the Scourge.$B$BI ask not that you accept $n and the Knights of the Ebon Blade into the Horde, but only that you show tolerance.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3541', 'Remember, Thrall, by blood and honor we all serve.$B$BRespectfully,$B$BHighlord Tirion Fordring$B$BP.S. Give Eitrigg my regards and please inform him that I could use a good orc by my side in Northrend - with your permission, of course.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3073', 'My queen,\r\n\r\nIt has been discovered that the ley line intersection at Moonrest Gardens was unfettered by the blue dragonflight. Their forces used a surge needle to destroy the capstone, but instead of flowing the power back to the Nexus, they utilized a series of foci to clumsily run and augment the power under the land to the Azure Dragonshrine.\r\n\r\nNeedless to say, this has had dire consequences wherever the magic shot up through the ground. The Ancients of the woodlands, the Kalu\'ak, and other creatures in the region have been adversely affected. Luckily for us, $N, the $r who stands before you, was instrumental in tracking down and dealing with all of these problems.\r\n\r\nIn my opinion, $N could be a great asset to Wyrmrest in helping to deal with the problems that the temple currently faces. I humbly ask that you consider $g him : her; a resource, one which I found to be invaluable.\r\n\r\nYours ever respectfully,\r\n\r\nModera\r\nArchmage and Member of the Six', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3512', 'The Old Wizard\'s Almanac: The Alliance\r\n\r\nAs a historically Alliance-friendly organization, one would think that the Kirin Tor might not need a guidebook outlining the Alliance races.  I feel that the opposite is true - that we can only understand the other races after truly looking inward.  - O.W.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3513', 'Human - As adaptable a race as you will ever find, humans can be some of the most highly skilled magic-users.  Any list of the most powerful mages in history will be dominated by humans.  Perhaps most tellingly, the Old Wizard himself is a human.\r\n\r\nDwarf - Dwarves possess a moderate magical acuity, but tend to have difficulty at the higher echelons of wizardry.  While they tend to focus more on priestly endeavors, a rare few have been known to dabble in arcane or necromantic activities.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3514', 'Gnome - Gnomes are remarkably adept arcanists.  Pound for pound, they are some of the most potent casters the world has ever known, and have always had a presence in the Kirin Tor.\r\n\r\nNight Elf - While night elves tend to avoid arcane energy like it was the Scourge, their druidic powers are second to none.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3515', 'Draenei - This ancient race has brought the Alliance both powerful wizards and masterful wielders of the Light.  More recently, the Draenei have adopted shamanism after their arrival in Kalimdor.\r\n\r\nHigh Elf - The magic of the high elves is extremely powerful.  High elves possess a magical acumen that rivals that of any - even the humans.  Some of the greatest heroes in the Battle of Mount Hyjal were the High Elf priests.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3516', 'The Old Wizard\'s Almanac: The Horde\r\n\r\nUp until the last few years, most of my information on the Horde has been anecdotal, or inferred from my few experiences in battle against them.  However, as the Kirin Tor has recently adopted a more neutral stance in Horde-Alliance relations, the amount of information on the Horde and their magic has exploded. - O.W.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3517', 'Orc - Despite their obvious physical strength, they display a decent magical ability.  Orcs tend to make poor arcanists, but culturally have a heritage of peaceful shamanism, combined with a more recent focus on the darker magic of warlocks and necromancy.\r\n\r\nTauren - Few experiment with the arcane, but the bull-men are highly attuned to the magics of the earth.  As is the case with many nature-worshipping tribes, many follow the path of the shaman.  Furthermore, their most senior druids can rival the archdruids of Darnassus in power.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3518', 'Troll - A surprising affinity for the magic arts belies the trolls\' barbaric reputation and history.  My Zul\'tour - an expedition into the troll empires of Zul\'Aman, Zul\'Farrak, and Zul\'Drak - has revealed a startling variety in troll casters, ranging from witch doctors to shadow priests to a bizarre animal worship that most closely resembles druidism.\r\n\r\nUndead - The undead have as much magical skill as can be expected of any living human... if not more.  Sylvanas\' Forsaken, in particular, fling spells with the frightening levels of zeal.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3519', 'Blood Elf - A history of prolonged exposure to magical energy has left an indelible mark on the Blood Elves.  These lithe, magic-addicted creatures seem to fare well in any magical school they study.  Most recently, the blood elves have taken up the path of the \"Blood Knight\", which can be loosely described as a form of paladinism.\r\n\r\nTaunka - Much like their tauren cousins, taunka tend to focus on magic stemming from the earth.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3520', 'The Old Wizard\'s Almanac: Other Arcanist-Friendly Races\r\n\r\nMagic is not limited simply to the races of the Horde and the Alliance.  Many of the creatures that make Azeroth their home are capable of thaumaturgical pursuits. - O.W.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3521', 'Naga - While males are more suited for melee combat, female naga are incredible sorcerers, commanding the power of water and ice at their fingertips.  It should be noted that their ancient queen Azshara was reputedly one of the most powerful magic-users in Azeroth\'s history.\r\n\r\nNerubian - Only the most powerful Nerubian leaders have the capacity for magic.  However, following the onslaught of the Scourge, many plagued Nerubians have become necromancers.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3522', 'Ogre - Most of these expatriates from Draenor focus on the path of the warrior or the ranger, but a small percentage choose a life of sorcery as ogre-magi.  Cho\'gall, one of the first ogre-magi, was an immensely powerful sorcerer (in addition to simply being immense).\r\n\r\nSatyr - These dangerous creatures have features similar to those of night elves, but twisted demonic bodies. Masters of demonic magic, satyrs are incredibly dangerous. Extreme caution is advised when approaching them.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3523', 'Blue Dragons - The ancient children of Malygos are among the foremost experts on arcane magic. Some, such as the adventurous Kalecgos, are friendly to the mortal wizard and potentially could make excellent research partners. Unfortunately, recent rumors from the north indicate that some blue dragons have grown extremely hostile to other spellcasters. The reasons for this aggression are as-yet unknown.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3524', 'The Old Wizard\'s Almanac: The Shamanic Races\r\n\r\nMy travels have introduced me to numerous societies across Azeroth, many of whom practice shamanism.  Many of these peoples live off the land, shunning advanced society for a more nature-centric existence. - O.W.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3525', 'Quilboar - The few quilboar that remain in Kalimdor are known to practice geomancy, a crude form of shamanism.  Some plagued quilboar practice this form of magic even in undeath.\r\n\r\nGnoll - Gnolls are a strange race.  I once managed to gain the favor of a clan leader in Redridge by performing a basic cantrip - the kind taught to Dalaran\'s schoolchildren.  For a day, he was my best friend, until he forgot my trick and sicced his hunters on me.  Anyhow, when they can be bothered to pay attention long enough, gnolls tend to focus on elemental shamanism.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3526', 'Centaur - Centaur females tend to use a dark, perverted form of shamanism.  Magic use among centaur males, on the other hand, is quite rare.\r\n\r\nWolvar - This wolverine-like group may appear primitive upon first glance.  However, their tribal leaders are often very powerful shaman.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3527', 'Oracles - Though they are the sworn enemies of the wolvar, the oracles seem to have a nearly identical social system.  Grunt work is done by warriors, but their cultural leaders are nearly always great shamanic practitioners.\r\n\r\nFurbolg - These days, it seems few furbolg ever become shaman.  Shamanism is much more common among the free-willed furbolg tribes.  Furthermore, some free-willed furbolg are even capable of basic shapeshifting, much like a trained druid.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3528', 'The Old Wizard\'s Almanac: Peoples of the Land\r\n\r\nShamanism isn\'t the only nature-centric magic.  Many of Azeroth\'s simpler races perform spells that stem from the the earth\'s energies, but which cannot be called shamanism.  Whether their magic powers come from the same source as the shaman\'s magic or not is a constant point of debate in the halls of the Violet Citadel. - O.W.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3529', 'Tuskarr - The walrus-men practice a form of magic that, on its surface, is astonishingly similar to shamanism.  Our scholars have not yet deciphered the source of the tuskarr\'s magic.\r\n\r\nKobold - These simple creatures are only capable of simple earth magic.  My exploits as a youth taught me that a few of them have perfected a rather nasty fireball, but that\'s about it.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3530', 'Wildkin - These feathered creatures go by many names - moonkin, owlbear, and owlkin, to name a few.  These gentle giants possess a natural, druidic energy similar to that of the night elves.\r\n\r\nHarpy - The cackling bird-women are capable of powerful elemental storm magic.  Little is known about the source of their power, as 1) they attack invaders on sight, 2) few can discern any meaning behind their shrill cries, and 3) none can tolerate the smell of their nests.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3531', 'Murloc - For many years these enigmatic creatures appeared to be capable of only the most basic magic.  Do not be deceived by their unintelligent fae.  The fish-men are fully capable of casting potent earth and water spells, and their numbers on land increase by the day.\r\n\r\nMakrura - The lobster-like race of makrura, while widely considered to be primitive, has an innate capacity for water magic.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3532', 'The Old Wizard\'s Almanac: Non-Magical Creatures\r\n\r\nMagic isn\'t for everyone.  The non-intelligent beasts of the world have survived for millennia on their wits alone, as have several of the simpler races.  Some races, while socially advanced, simply prefer to avoid magic in favor of other pursuits.  As Kirin Tor, it is critically important that we understand the way that these races thrive without any magic at all. - O.W.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3533', 'Goblin - Goblins tend to be distrustful of magic, preferring to focus mainly on technological ventures.  Unlike their gnomish counterparts, they lack the capacity to understand and appreciate the arcane.  That being said, there are some that have combined their technology with magic, yielding interesting results.\r\n\r\nTrogg - These primitive creatures simply lack the intelligence necessary for magic.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3534', 'Magnataur - The enormous magnataur have little to no magical affinity.  Magnataur casters are very rare, and tend to be follow a shamanic route if they practice magic at all.  I can tell you first-hand, however, that they won\'t go down without a fight, and their thick hides can withstand several of the most powerful frostbolts.\r\n\r\nVrykul - While Northrend\'s half-giants naturally know little to no magic, some have been trained in necromancy by the Lich King. I have also heard rumors of �rune magic', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3535', 'Highlord,\r\n\r\n$N has successfully led the campaign against the Scourge of Scourgeholme and positioned us to march upon Icecrown.\r\n\r\nThe masons are equipped with the supplies that they need to begin construction of Justice Keep - thanks once again to $N for $g his:her; assistance in Crystalsong Forest.\r\n\r\nThere remains only one last challenge: the taking of Crusader\'s Pinnacle. For it is there, at the place where we have anguished most, that I wish to begin this final chapter.\r\n\r\nWe await your command, sir.\r\n\r\nLight protect you.\r\n\r\n-Gustav', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3067', 'Goramosh,$B$BI am sending a representative of the Ethereum to you.  Ambassador Duyheen tells me that there are more of his kind who would like to join the cause.  Feel him out, see if you can find a use for him.$B$BIf he proves helpful, I may consider accepting more of his kind into the fold.$B$BDon\'t be an idiot; burn this letter once you\'ve read it!$B$BM', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3075', 'Hi!\r\n\r\nI\'m not good at letters and so the matron helped me write this down for you.  I just want to say THANKS because you\'re a hero and maybe someday I can save people like you do.\r\n\r\nOk, I have to go play so THANKS AGAIN for being good and strong and and you\'re $ghandsome:pretty; too!\r\n\r\n--Your secret admirer', null, null, '你好！\r\n\r\n我不是很擅长写信所以让阿姨帮我写了这封信。我想对你说谢谢，因为你是一个大英雄！希望有一天我也能像你一样保护其他人。\r\n\r\n好啦，我要去玩啦，再次感谢你！\r\n\r\n- 你的神秘仰慕者', '你好！\r\n\r\n我不是很擅长写信所以让阿姨帮我写了这封信。我想对你说谢谢，因为你是一个大英雄！希望有一天我也能像你一样保护其他人。\r\n\r\n好啦，我要去玩啦，再次感谢你！\r\n\r\n- 你的神秘仰慕者', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3106', '1. If you don\'t like the potency of a poison, double the ingredients or halve them.$B2. If a poison doesn\'t work the way you want, you\'re always more wrong than you think you are.$B3. Prototype, Prototype, Prototype (gnomes make great test subjects).$B4. Poisons should be easy to start using, hard to stop using.$B5. Simple ingredients work together to create interesting complexity.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3279', 'Minions be hearin\' da call of your masta! Da Lich King be givin\' Drakuru full control of da armies of da Scourge in Zul\'Drak. We be commanded ta kill everyting.$B$BEVERYTING MUST DIE!$B$BWe be processin\' all da corpses in da Dead Fields and sendin\' em up ta Prince Navarius\' crew in  Zeramas for Scourgin\'! Notin\' goes ta waste on Drakuru\'s watch!$B$BSoon we be drinkin\' da blood of da prophets ta become stronger dan eva before! Zul\'Drak and Gundrak gonna fall to da Scourge!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3280', 'Long ago, when the North Wind ruled these peaks alone, a powerful Taunka Chieftain\'s brother coveted these lands for himself.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3281', 'The Chieftain\'s brother, Stormhoof gathered all his strength and set out to conquer the Storm Peaks from its rightful ruler, the North Wind.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3282', 'Stormhoof ambushed the North Wind in the heart of it\'s domain, and nearly succeeded in his plot. But the North Wind survived Stormhoof\'s treachery and executed the Taunka as punishment for his invasion.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('264', 'Miner OrwellDebt outstanding.  Payment due upon next delivery of ore from Azureload.Miner FitzgeraldDebt outstanding.  Payment due upon next delivery of ore from Azureload.Citizen NetherandAll debts paid.Citizen MayAll debts paid.Foreman BondsDebt outstanding.  Payment due upon next delivery of ore from Azureload.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('384', 'It should be noted here that this report bearer was instrumental in not only the success of our mission, but also in the defeat of Ivar the Foul, the rescue of Erland, and the deliverance of this report.We extend our gratitude, and recommend that command acknowledges this individual with awards befitting such meritorious conduct as was displayed in the field.-Deathstalker Rane Yorick,Mission Leader', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2177', 'Valea Twinblades  Warrior of the Scarlet CrusadeCitizen of Alterac  Last seen deep in the Eastern Plaguelands', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2295', 'Before the Age of Memory, the gentle Earthmother breathed upon the golden mists of dawn. Where the amber clouds came to rest, there were endless fields of flowing wheat and barley. This was the basin of her works - the great basket of life and hope.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2296', 'The Earthmother\'s eyes shone down upon the lands she had breathed into creation. Her right eye, An\'she (the sun), gave warmth and light to the land. Her left eye, Mu\'sha (the moon), gave peace and sleep to the stirring creatures of the dawning. Such was the power of her gaze that the Earthmother closed one dreaming eye for every turning of the sky. Thus, her loving gaze turned day into night for the first dawning of the world.   ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2297', 'While the right eye shone down upon the golden dawn, the Earthmother\'s gentle hands spread out across the golden plains. Wherever the shadow of her arms passed, a noble people arose from the rich soil. The Shu\'halo (the tauren) arose to give thanks and prayer to their loving mother. There, in the endless fields of dawn, the children of the earth swore themselves to her grace and vowed to bless her name until the final darkening of the world.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2298', 'As the children of the earth roamed the fields of dawn, they harkened to dark whispers from deep beneath the world.  The whispers told the children of the arts of war and deceit. Many of the Shu\'halo fell under the shadow\'s sway and embraced the ways of malice and wickedness. They turned upon their pure brethren and left their innocence to drift upon the plains.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2299', 'The Earthmother, her heart heavy with her children\'s\' plight, could not bear to watch them fall from grace. In her grief, she tore out her eyes and set them spinning across the endless, starry skies. An\'she and Mu\'sha, seeking to ease the other\'s sorrow, could only chase each other\'s faint glow across the sky. The twins still chase one another with every turning of the world.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2300', 'Though sightless, the Earthmother could not long stray from the world of her heart. She kept her ear to the winds and listened to all that transpired across the fields of the dawn. Her great heart was always with her children - and her loving wisdom never fled from them.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2301', 'Into the brave hearts of her pure children, the Earthmother placed the love of the hunt. For the creatures of the first dawn were savage and fierce. They hid from the Earthmother, finding solace in the shadows and the wild places of the land. The Shu\'halo hunted these beasts wherever they could be found and tamed them with the Earthmother\'s blessing. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2302', 'One great spirit eluded them, however. Apa\'ro (known as Malorne to the night elves), was a proud stag of snow white fur. His antlers scraped the roof of the heavens and his mighty hooves stamped out the deep places of the world. The Shu\'halo hunted Apa\'ro to the corners of the dawning world - and closed in to snare the proud stag.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2303', 'Seeking to escape, the great stag leapt into the sky. Yet, as his escape seemed assured, his mighty antlers tangled in the stars which held him fast. Though he kicked and struggled, Apa\'ro could not loose himself from the heavens. It was then that Mu\'sha found him as she chased her brother, An\'she, towards the dawn. Mu\'sha saw the mighty stag as he struggled and fell in love with him immediately. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2308', 'As the mists of dawn faded and the Age of Memory advanced, the demigod, Cenarius, went his own way through the fields of the world. The Shu\'halo (tauren) were sorrowful at his passing and forgot much of the druidism he had taught them. As the generations passed, they forgot how to speak with the trees and the wild things of the land. The dark whispers from the deeps of the world drifted up to their ears once again.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2309', 'Though the children of the earth closed out the evil whisperings, a terrible curse befell their roaming tribes. Out of the black lands of the west came a horde of murderous creatures - the centaur. Cannibals and ravagers, the centaur fell upon the Shu\'halo like a plague. Though the braves and hunters fought with the Earthmother\'s blessing in their hearts, the centaur could not be defeated.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2711', 'Completing the RitualYou can track your progress by the magic runes along the border of the circle.  When nine runes appear then the ritual is complete, and you will see energy rise from the newly empowered Circle.From there, you may invoke the Xorothian Glyphs and open a portal into Xoroth and pull a dreadsteed through it.Defeat the dreadsteed and release his spirit.  Confront the spirit and it will be enthralled, and you will be rewarded with the secret of its summoning.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2927', 'PS - Our grand magister has received word back from Outland regarding Thrall\'s question.  The answer is, with great certainty, yes.$B$B<This letter bears the additional seal of Lady Sylvanas Windrunner>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3036', 'The mighty creature\'s maw opened wide, tossing bolts of icy water toward our vessel. Those terrible jaws clamped down upon the bow of our ship, tearing it from the hull. Sailors and cargo were thrown violently into the sea as the remnants of the ship disappeared beneath the waves.The last thing I remember was flying from the broken hull, hitting the inky waters, and plunging beneath the waves. The world went dark and I thought for certain I\'d drawn my last breath. When I awoke in Theramore\'s infirmary, I knew someone had to tell our story.', null, null, '这头巨兽张开血盆大口，冰冷的水柱朝我们的船只倾泻而来。它锋利的牙齿瞬间将船头撕成了碎片。船员和货物纷纷被抛到海中，船只的残骸也渐渐被海浪吞没。\r\n\r\n我只记得自己站立不稳，从破裂的船舱跌入海中后便失去了知觉。眼前的世界一片漆黑，我以为此次难逃一劫，注定要葬身鱼腹。待我苏醒后，发现自己躺在塞拉摩岛的医护室。看来，这段恐怖的经历必须得由我这个唯一的幸存者来讲述了。', '这头巨兽张开血盆大口，冰冷的水柱朝我们的船只倾泻而来。它锋利的牙齿瞬间将船头撕成了碎片。船员和货物纷纷被抛到海中，船只的残骸也渐渐被海浪吞没。\r\n\r\n我只记得自己站立不稳，从破裂的船舱跌入海中后便失去了知觉。眼前的世界一片漆黑，我以为此次难逃一劫，注定要葬身鱼腹。待我苏醒后，发现自己躺在塞拉摩岛的医护室。看来，这段恐怖的经历必须得由我这个唯一的幸存者来讲述了。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3045', 'First Watch, Eight Bells$BWatches changed.  All is well.', null, null, '早班，第八次报钟\r\n换岗。一切正常。', '早班，第八次报钟\r\n换岗。一切正常。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3046', 'Middle Watch, One Bell$BCedric found attempting to break into Captain\'s wine cabinet.  Disciplined.', null, null, '夜班，第一次报钟\r\n发现塞德瑞克想要闯入船长的酒窖。惩罚之。', '夜班，第一次报钟\r\n发现塞德瑞克想要闯入船长的酒窖。惩罚之。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3047', 'Middle Watch, Two Bells$BCedric climbed rigging, became tangled and fell.  Attended by ship\'s surgeon.  Sent below to sober up.', null, null, '夜班，第二次报钟\r\n塞德瑞克攀爬绳索，失足摔落。外科医生对其进行救治。关入船舱醒酒。', '夜班，第二次报钟\r\n塞德瑞克攀爬绳索，失足摔落。外科医生对其进行救治。关入船舱醒酒。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3049', 'Middle Watch, Four Bells$BBottle of rum found in Cedric\'s pants.  Disciplined.', null, null, '夜班，第四次报钟\r\n在塞德瑞克的裤子里发现朗姆酒瓶。惩罚之。', '夜班，第四次报钟\r\n在塞德瑞克的裤子里发现朗姆酒瓶。惩罚之。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3050', 'Middle Watch, Five Bells$BCedric\'s clothes found.  Cedric found separately.  Cedric disciplined.', null, null, '夜班，第五次报钟\r\n找到塞德瑞克的衣物。发现塞德瑞克身首异处。惩罚之。', '夜班，第五次报钟\r\n找到塞德瑞克的衣物。发现塞德瑞克身首异处。惩罚之。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3051', 'Middle Watch, Six Bells$BCedric singing loudly.  Woke Captain.  Cedric disciplined by Captain.', null, null, '夜班，第六次报钟\r\n塞德瑞克大声唱歌。船长被吵醒。船长亲自惩罚塞德瑞克。', '夜班，第六次报钟\r\n塞德瑞克大声唱歌。船长被吵醒。船长亲自惩罚塞德瑞克。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3052', 'Middle Watch, Eight Bells$BWatches changed.  All quiet.  Two men sent to locate Cedric.', null, null, '夜班，第八次报钟\r\n换岗。一切正常。派出两人寻找塞德瑞克。', '夜班，第八次报钟\r\n换岗。一切正常。派出两人寻找塞德瑞克。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3053', 'Morning Watch, One Bell$BFire in Captain\'s quarters.  All hands roused.  Throwing powder overboard.', null, null, '早班，第一次报钟\r\n船长的房间失火。所有人出动。丢弃火药。', '早班，第一次报钟\r\n船长的房间失火。所有人出动。丢弃火药。', null, null, null);
INSERT INTO `locales_page_text` VALUES ('3115', 'From the desk of High Abbot Landgren: \nIt may seem that in dark times of strife and turmoil that one must turn inward, protecting family and loved ones to the exclusion of all else. But these feelings are false - without the beacon of hope that is the Church, despair will quickly inundate the very pores of your family\'s soul. All the full stomachs, warm toes, and happy smiles that worldly goods may purchase will not save your family from the creeping inner emptiness that shirking your fiduciary responsibilities to the Light will induce. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3116', 'Give to the church. The hardships endured through lack of worldly goods act as a forge for the soul, burning out impurities and raising you up to a higher plane of being. Be not ashamed at the rumbling of an empty stomach, the bite of a sharp rock upon your bare foot, or the chill of the winter wind. These are but means by which the Light communicates its inspiration to the soul. These sacrifices ensure that the arching, gold-appointed paeans to the Light which so inspire you at worship are well-maintained. \r\n\r\nEschew the comforts of the flesh, and come ever closer to the Light.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3129', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>It is with trepidation that I gaze upon New Avalon, I somehow suspect for the last time. The fate of our cause lies in Northrend. I am filled with a sense of ominous foreboding for some reason. The mission ahead should shake away these concerns. I will put them out of my mind.</P>\r\n<BR/>\r\n<P>The Crimson Dawn is upon us.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3163', 'Tartek the Enraged.  One of the most powerful abominations the Scourge has ever created.  Assembled by lich Amnennar the Coldbringer to assist in setting up a base at Razorfen Downs.  Tartek single-handedly felled more than 1200 quilboar before finally being overcome.  His sacrifice provided the corpses for Amnennar to quickly raise an undead quilboar army of his own, ultimately assembling the first major Scourge outpost on Kalimdor.  Some of Tartek\'s remains were reanimated and used to create Glutton, Amnennar\'s current lieutenant.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3164', 'Obrahiim, the Traveler.  Brilliant architect, pivotal in the conception of Naxxramas, and mastermind behind the creation of Acherus.  Quickly rose up the ranks from lowly skeletal peon to one of Kel\'Thuzad\'s most trusted advisors.\r\n\r\nAlways the scholar, Obrahiim traveled the world in search of knowledge, notably taking most of his early inspiration from the architecture of the Nerubians and the magic of the orc warlocks.  He was last seen several years ago, headed west to study the Ruins of Zin-Azshari in Kalimdor.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3165', 'Diodor the Damned.  Human necromancer, one of the first members of the Cult of the Damned, and decorated hero of the First War.  Successfully led a contingent of Scourge to victory during the First and Second Summertide Assaults upon Scarlet Monastery.  Killed in action during the Third (and final) Summertide Assault, which was successfully repelled by Scarlet Crusade operatives that had finally figured out his modus operandi.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3166', 'Kirkessen the Zealous.  Powerful lich who reported directly to the Lich King himself.  A decorated veteran of countless battles, Kirkessen designed and led the Siege of the Sanguine, one of the largest organized assaults on Light\'s Hope Chapel.  Despite overwhelming odds, his attack was quickly countered, and his forces suffered enormous damages.  Kirkessen himself received a critical blow from Lord Maxwell Tyrosus, and is no longer able to assume a corporeal form.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3167', 'WESTERN NORTHREND - Reports from the Aerial Surveillance Squadron in Icecrown Glacier show that communication with the lich-lord, Chillwinter, has been cut sharply cut off.  `Chillwinter was last seen piloting the necropolis, Talramas, over Borean Tundra on a confidential mission,` reports Scourge Engineer Karomon, the Squadron\'s chief information officer.  `Our diagnostics show that his communication crystals are fully operational... it\'s just... dead silent.`\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3169', 'DEATH\'S BREACH, EASTERN KINGDOMS - Instructor Razuvious spoke out earlier this week, reiterating his policy on mutiny and insubordination.  `Any death knight who dares desert the Scourge will meet a fate far worse than death.  Their suffering shall see no end - I will see to it personally.`\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3171', 'DRAGONBLIGHT - Prince Valanar is looking for an exceptional death knight to travel into Dragonblight, Northrend\'s frozen central plain, and lead an elite cadre of soldiers in a strike against the red dragonflight.  \"Your training is nearly complete,\" he said in a recent public address.  \"The time has come to strike against those who would protect the living! We shall show them the true meaning of Dragonblight!\"\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3172', 'Alexstrasza, red dragonqueen and arguably the Scourge\'s most powerful enemy in Northrend, has been seen building up her own army as of late, though it is assumed that her efforts are aimed at the rogue blue dragonflight.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3173', 'UNDERCITY - Our sources inside the Undercity report that the Horde is mobilizing to stage an assault on Northrend. \"The Horde\'s troops, most of whom are returning from deployment in Outland, are now gathering and preparing to strike at Icecrown.\"\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3174', 'Furthermore, covert agents report that the Forsaken\'s Royal Apothecary Society has ramped up activity lately.  \"While nothing is confirmed at this point, rumors fly that the Society has had a breakthrough in their plague research.  Again, this is only hearsay, but all forces are advised to give Forsaken alchemists a wide berth.\"\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3175', 'The Death Knights of Acherus\r\n\r\nName: Jayde\r\nStatus: Alive\r\nComment: Bloodthirsty', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3176', 'Name: Sixen\r\nStatus: Alive\r\nComment: Chatty\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3177', 'Name: Munch\r\nStatus: Alive\r\nComment: Destructive\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3178', 'Name: Cobalt\r\nStatus: Deceased\r\nComment: Fell to his death. Not much upstairs. Probably saved him a lifetime of suffering.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3179', 'Name: Harmony\r\nStatus: Deceased\r\nComment: The name of this death knight was found in violation of common Scourge decency. Struck down by the Lich King.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3180', 'Name: Melt\r\nStatus: Alive*\r\nComment: Turned into a ghoul.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3181', 'Name: Milton\r\nStatus: Alive\r\nComment: Rambles\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3182', 'Name: Minitalis\r\nStatus: Alive\r\nComment: Sees things that do not exist.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3183', 'Name: $N\r\nStatus: Alive\r\nComment: Needs more cowbell.\r\n\r\n<The book goes on for hundreds of pages.>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3211', 'Corpulous great cook of Acherus who make good food for you. He proud to feed mighty Scourge and make stronger. No make Corpulous mad, he no feed you.\r\n\r\nRules:\r\n\r\nDeath Knight eat first, ghoul wait turn.\r\n\r\nNo eat ghoul next to you. Need clean plate first.\r\n\r\nOk add own maggots and lice to food. Corpulous not offended.\r\n\r\nGot vermin? Bring to Corpulous!\r\n\r\nOk eat human or elf. Elf other white meat.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3212', 'Mess part of meal. Corpulous see napkin, Corpulous hang head in shame.\r\n\r\nOk bring back food not rotten enough. Corpulous got three free armpits.\r\n\r\nOk eat with hands or swords. No eat hands or swords.\r\n\r\nZombie need try more food. Think outside the brains.\r\n\r\nNo eat self.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3213', 'Account of the Raising of a Frost Wyrm\r\n\r\nThe hulking bones were nearly swallowed by the snow, but there was no mistaking the grim claw that jutted upward, twisted in solemn agony. The necromancers assembled without a word, forming a ring around the dragon\'s corpse. They stood still for a moment as the wind encircled them with gusts of snow. Then the ritual began.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3214', 'Spokes of unholy light emanated from the center of the frozen bones, cracking through the snow and ice until at last the immense remains were fully exposed. With a gesture from Grand Necrolord Antiok, the bones shuddered and lurched above the ground, rotating slowly into place.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3215', 'The necromancers\' incantations swelled as they began forcing sentience into the creature\'s remains. Violent contortions racked the body as the wyrm\'s conscience fought against its corrupt reanimation. A thin shriek pierced the air and the beast was subdued. An icy glow ignited within its gaping ribs, spreading along its limbs and lending an eerie cognizance to the sockets of its eyes.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3216', 'The Grand Necrlord came forward, and spoke. \"The Lich King has seen fit to raise you to serve the Scourge. You will be our loftiest instrument of death, raining torment upon the villages of our enemies, feasting upon the living, and bearing our finest death knights.\"\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3217', 'The frost wyrm regarded them and dipped its head slightly in accord. The conversion was complete. Rearing back, it flared its wings like a fan of knives across the sky.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3239', 'I warned the lot of you that the next campaign on which I spent more time cleaning my boots than kicking infants, heads would roll! Dung cleanup shifts are henceforth doubled in length and number for all geists!\r\n\r\n--Instructor Razuvious\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3240', 'Behold the finest of ironies. The living ascribe unto undeath all the most sickly attributes of their own lives--hopeless servitude, savagery, stillness of the spirit. We serve! The living serve, too, though they serve kings, warlords, druids, priests, gods, men, and beasts. We serve only the mighty, unifying will of the Lich King, which compels us unto prosperity and unity!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3241', 'For all the endeavours of men, there exist only endless records of tragedy, cruelty, betrayal, and selfishness. For the Scourge, there is only efficiency and totality. Serve the Lich King in life, or serve him in death. His is a way of choice whose roads lead only to fulfilment!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3242', 'Cold in flesh, but warmed by unity. The mighty Scourge are one nation, one mind, one being. True cold dwells in the heartless gaze of the guards who turn away sickly refugees from their neighboring country in a time of war! Are not all men of one kind? Nay, divided in their icy hearts.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3243', 'Raise high our ideals, and bring low our foes! Fold their broken into our number until all serve the Frozen Throne!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3244', 'I have earned the chapter\'s favor at last and been granted entry to the Sholomance! Today my final ascent into glorious undeath begins!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3245', 'Things within the Scholomance are far more rustic than I\'d expected. Piles of research materials are strewn in awkward piles, some sealed together with wax dripped from the odd untended candle. Constructs, spirits, and demons roam the halls untended. I must say that I expected more from such practiced necromancers as these!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3246', 'Today a troupe of brigands broke into the school and began slashing their way through to the crypts. As they cut down a study group in the foyer, the nearby students continued their practices, oblivious! The forces within that hall would overwhelm a small army, yet they yielded piece-meal. Fools!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3247', 'As I read with Darkmaster Gandling today, invaders broke through the viewing room doors and reached the crypts! The crazy sod hid behind a bookcase, giggling, as he watched them slay each of the school\'s administrators within the crypts! Then, he burst forth, shouting `School is in session!` Is he off his rocker? I\'m recommending myself for transfer to Stratholme!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3252', 'I went to work within a war factory of the mighty Scourge today! Naxxramas bristles with activity. I was saddened to learn that they were using more foolish puzzle mechanisms to power mission-critical teleport devices, but otherwise it seemed to be a tight ship. My thoughts turned toward gainful research, until I ran astray of the perverse nerubian...', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3253', 'The Nerubian\'s overtures are without number. Always calling to me in that creepy, droning voice of its. Invitations to study in his \'parlor\'. I\'ve taken to sequestering myself in the broken pipe behind Gluth\'s chambers, as it\'s the only place he won\'t follow me. My mind is wearing thin!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3254', 'How does this lackadaisical cadre of misfits and rotting corpses intend to conquer all of Azeroth? Their self-defeating disingenuity falls short of any reasonable appearance of effort at self-preservation! I can make no effort to effect change with the detestable nerubian hounding my footsteps and blackening my dreams!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3255', 'Though this may seem a trend in my recent ventures with employment in the Scourge, adventurers have broken into the necropolis and are killing my colleagues. Alas, even the hot redhead in Faerlina\'s cult, slain, before the creepy spider would let me talk with her! I hate this place, as soon as I turn undead I\'m SO out of here. It\'s time to fight!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3256', 'After repeated failures at serving within the Scourge, I found that I held myself to much higher standards than even those within the upper echelons of the organization. Given the inherent inflexibility of social power structures, it is difficult to balance attempts to effect change with extant social challenges.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3257', 'It is fortunate, then, that this organization offers low-skill employment opportunities for disaffected individuals or those inconvienced by some malady (such as, in my case, death).', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3258', 'I have come to embrace one truth. The arrival of death is cold and unfulfilling, and gives no answers. I am freed of earthly obligation, but I feel no relief for I feel no heart. I simply am. I may only hope to fall again before the blazing wrath of a noble and merciful heart, in glorious battle!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3259', 'The necropolis has been called back to Icecrown. We have taken on supplies and personnel, and the city is alive once again! I am different. I feel whole. I feel alive. I feel His perfect will directing me. I know exactly where I will stand, and when the warriors come, I will test them.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3269', 'The Decree of the Scourge\r\nChapter 1\r\nBy Kel\'Thuzad\r\n\r\nThe roots of the Scourge are sewn in the folly of all races. Our rise to power is the product of the sins of those who mean our end. The so called \'heroes\' of the land fall before us by resorting to a measure of our ideals to bring forth their envisioned victory. Their victory is a vehicle to the realization that without resorting to what they define as deplorability to achieve it, we would have overcome them instead. In both outcomes, win or lose, the Scourge invariably wins. When this is recognized, submission to our will is inevitable.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3270', 'The Master, our Lich King, was born under this very same doctrine.\r\n\r\nTo conquer is to corrupt. To corrupt is to take what it is to be righteous and hopeful, to be living, and invert it through any means in your arsenal. The attributes of the living all have synonyms with fatal flaws that are their undoing: hope is dogma, righteousness is zealotry, living is empathy. Recognize what makes that which lives desire to live, and turn it upon itself.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3271', 'Knowing the most direct path of corruption is the pinnacle of wisdom. A simple weakening of the heart, dismemberment of vital organs, or direct disease will work for most. However the conquer of most is not the decree of the Master, but the conquer of all. It is foolish to spend excessive resources in warping the mind of a simple peasant where common plague will suffice, but it is equally unwise to try the same common plague against the seasoned and hearty. Regimented study of any formidability will reveal useable weaknesses. \r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3272', 'The Decree of the Scourge\r\nChapter 2\r\n\r\nThe largest misconception the living have of the Scourge is our penchant for what is considered \'evil.\' For the sake of demoralization we allow this error to perpetuate, and indeed spread it on our own. The true nature of The Scourge is our transcendence of the shackles that keep any of the living races from becoming as prosperous as we. \r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3273', 'For example: What benefits do emotion and honor really hold? They serve to validate the living, to make them feel \'good.\' Now, what flaws do they hold? Emotion is directly correlated to the ignorance of logic and reason. Honor is, at most times, the sole perpetuator of able resources fighting a losing battle. It is baffling even to me that the living can be so unaware of how eager we are to play the part of their villain, to nurture their greatest flaws by becoming the embodiment of all that incites irrational behavior.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3274', 'Moral ambiguity cannot be \'cleansed.\' Antipathy cannot be \'healed\' or \'assuaged.\' Greed cannot be \'dispelled\' and wrath cannot be \'cured.\' We choose to be the embodiment of all these things, and yet there are some still foolish enough to believe that the Scourge can be defeated and removed completely. So long as the most attractive sins of the living remain our banner flag, there will always be those eager to flock to our will.  \r\n\r\nI myself was born under this doctrine.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3275', 'The Decree of the Scourge\r\nChapter 3\r\n\r\nThe Scourge is infinite in potential. We are not, however, infallible by any means. Always be well aware of your limits, and the resources at your disposal. Always have an exit strategy. Sacrifice of eager minions to your ends is a cornerstone of Scourge philosophy. All manner of bravery, martyrdom, or compatriotism for anyone but the Master is imprudent and severely punishable.\r\n\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3276', 'Another fallacy outsiders hold of the Scourge is that our power resides in the residual will of the living, that we are an advanced form of parasite, and without a host of regenerating life would simply wither away. Again we do not deny such ignorant thought for our benefit, but this could not be further from the truth. We are nothing but an ever-improving form of adaptation to a land once riddled with endless struggle. We are immune to the tribulations of dissention, over-population, individuality, selfish opinion, short-sightedness, and even mortality. We hear the will of the Master, and the Master brings us all that we will ever desire. \r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3277', 'It is true that we take architecture, technology, and physical prowess from assimilated cultures. This, like the so many \'evil\' acts of the Scourge, is done entirely out of pragmatism. The Scourge has neither the time nor the desire to generate a culture of its own. A member of the Scourge that creates without influence will hold an inherent attachment to it, a right to see it viewed and used. This leads to individuality, which leads to dissention. Creativity is a flaw of culture, but a minor obligation when dealing with simple needs like buildings and equipment. It is not your duty to question or care of the origins of your resources, only in how usable they are.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3278', 'The Scourge is the will of the land. All manner of life kneel to our will with an ease that yields a single conclusion: That they need us, they yearn for our salvation. There is no resilience to our practices, no immunity to our commandments. We are every bit the inevitability that a natural life holds: Death. Death comes with every life, and undeath comes with every death. The sooner the living recognizes this immutable fact, the easier their transition will be.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3479', 'Thule Ravenclaw\r\n\r\nThough accounts vary wildly, many recent  events transpiring in Tirisfal Glades and Silverpine Forest seem to indicate that Thule Ravenclaw had willingly sided with the Scourge. The husk of Fenris Keep on Lordamere Lake may yield clues, but our attention must remain elsewhere at present.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3480', 'Gunther Arcanus\r\n\r\nPresumably killed during the events of the third war and lost to the Scourge.\r\n\r\nGunther was last in possession of my favorite pipe. Should his remains turn up, I would be most gracious for its return!\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3481', 'Bethor Iceshard\r\n\r\nKilled during the events of the third war. Bethor is known to have risen as a member of the Forsaken and should be treated as an agent of the Horde in all dealings.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3542', 'Archmage Antonidas, Grand Magus of the Kirin Tor\r\n\r\nThe great city of Dalaran stands once again - a testament to the tenacity and will of its greatest son.\r\n\r\nYour sacrifices will not have been in vain, dearest friend.\r\n\r\n\r\nWith Love and Honor,\r\n\r\nJaina Proudmoore', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('60000', 'Years and years I have been locked away in this magical prison.  Tonight, I make my escape.  I have everything I need to make my break.\r\n\r\n<Water smeared ink has ruined this section of the letter>\r\n\r\nWhy have I been here so long?  What did I do to deserve this?  Days and days of these thoughts running through my head.  What could someone do deserve this?\r\n\r\n<Water smeared ink has ruined this section of the letter>\r\n\r\nPerhaps I will find out in another life.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3068', 'Lok\'tar ogar! Victory or death - it is these words that bind me to the Horde. For they are the most sacred and fundamental of truths to any warrior of the Horde.\r\n\r\nI give my flesh and blood freely to the Warchief. I am the instrument of my Warchief\'s desire. I am a weapon of my Warchief\'s command.\r\n\r\nFrom this moment until the end of days I live and die - For the Horde!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3074', 'My queen,\r\n\r\nIt has been discovered that the ley line intersection at Moonrest Gardens was unfettered by the blue dragonflight. Their forces used a surge needle to destroy the capstone, but instead of flowing the power back to the Nexus, they utilized a series of foci to clumsily run and augment the power under the land to the Azure Dragonshrine.\r\n\r\nNeedless to say, this has had dire consequences wherever the magic shot up through the ground. The Ancients of the woodlands, the Kalu\'ak, and other creatures in the region have been adversely affected. <name>, the <race> who stands before you, was very helpful in tracking down and dealing with these issues.\r\n\r\nIn my opinion, <name> could be useful for Wyrmrest in dealing with the problems that the temple currently faces. I humbly ask that you consider <him/her> an asset, one which I found to be quite acceptable.\r\n\r\nYours ever respectfully,\r\n\r\nAethas Sunreaver\r\nArchmage and Member of the Six', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3077', 'Ah\'tusa crept silently through the massive underground tunnel, nervously looking over her shoulder in anticipation of the fast moving tram.  \"Where is he...\" she muttered angrily, stopping suddenly as something moved behind her.  Her eyes narrowed dangerously as her dagger slid from its sheath.\r\n\r\nA husky voice echoed in the hollow chamber, \"I thought rogues liked to be behind their target.\"  She spun quickly, driving her blade into a shield of impenetrable force.  \"And I thought paladins were supposed to be chaste.\" she replied with a confident smirk.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3078', 'Leaning forward, she counted out loud until the invisible bubble dissipated with an audible pop.  \"Twelve seconds?  I know you have abilities that last longer than that.\"\r\n\r\nBony fingers worked effortlessly beneath his armor, unlatching his breastplate and exposing him to the cold air.  \"Don\'t worry my sweet Marcus, that\'s not a finishing move tonight... I\'m just getting started.\"  He wrapped his arms around what remained of her waist, forcing an excited giggle as he nibbled at her neck vertebrae.\r\n\r\n<The rest of the book has several sections that are apparently worn out from repeated readings.>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3079', 'The Diary of High General Abbendis', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3080', 'The voice whispered, \"Come to me.\" From the very beginning I knew that it was the Holy Light speaking to me in dreams. At last! After all of my years of prayers and good deeds, the cleansing of the blight of the unliving from the face of Azeroth. After all of the failures and resurrections.\r\n\r\nFinally!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3081', 'It happened again. \"Come to me...\", the Light commanded.\r\n\r\nI woke up freezing, but it wasn\'t cold in my chambers. I\'m going to redouble my efforts! I\'ll tell the high abbot tomorrow that I want prayers increased. No more half-measures!\r\n\r\nThe Holy Light has taken notice of our good work. I can feel it!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3082', 'This time I was awake! It was very vivid, and yet for over a minute, in the middle of the warm, sunny day, my breath came out misted and chill. One of the priests noticed and dropped to his knees in prayer.\r\n\r\nNo one else heard the voice, though. At least the witness proves that I\'m not going insane. Maybe I should ask Landgren to pray on the matter?\r\n\r\nI\'ll get Jordan and Street to scrutinize their recruiting efforts tomorrow. We\'ve grown bloated with ranks of unbelievers who yearn only to destroy the undead. That\'s not enough!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3083', 'The commander and the bishop were both receptive. Not that they had any choice. Bishop Street in particular seemed very enthusiastic. He spoke of a revival for the crusade and swore to ferret out the weak of faith within our ranks.\r\n\r\nI told him to go easy. I\'ve no intention of destroying the Crusade. However, I do like the sound of forming an elite cadre of the most faithful to do the Light\'s bidding in Northrend. I fear the man\'s friendship with LeCraft is slowly twisting him. They both have their uses, though.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3084', 'I will leave most of the Crusade here to continue mopping-up operations on the undead in our backyard. I imagine that once they have finished what we began, most will disband and go back to their homes to live in peace.\r\n\r\nThat somehow seems right. At our finest, we have always been the salt of the earth, rising up to take back our homes from the filth of the Scourge corruption, to return our Lordaeron to its former glory. To a time before the Scourge, before Arthas and regicide... before the Lich King.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3085', 'There have been whispers amongst the men about a day soon to come that will change everything for the Scarlet Crusade. Bishop Street has put a name to it, calling it the Crimson Dawn.\r\n\r\nI will put some thought to this, though I can feel the truth of it in my bones. I pray that it will bring weal for us rather than woe.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3086', 'The Light has spoken again with a great deal of urgency. I came away from the dream with a sense of impatience. I will not disappoint. There must be no more delays. We must soon be underway!\r\n\r\nWhat a great coincidence then - and I had to control myself from showing my relief - this afternoon Captain Shely procured for us a number of new ships for the voyage. Perhaps it was no coincidence at all? The Holy Light expresses its will in ways that we are not meant to understand.\r\n\r\nI will take the Sinner\'s Folly as my flag. I think the name is most fitting.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3087', 'Now I know why the Light has been pushing me to be underway. In the dead of night a Scourge necropolis appeared in the sky over us and out poured the minions of hell!\r\n\r\nA new breed of death knight leads the assault. Already the casualties we\'ve suffered have been catastrophic. With the Scourge able to strike at us anywhere and at will from above, there seems no way to mount a proper defense.\r\n\r\nI fear that our intended expedition to Northrend is over before it has begun.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3088', 'I\'ve been informed that Hearthglen and the surrounding area have already begun to assemble a host. High Commander Galvar Pureblood himself intends to lead them to save us. His efforts will be for naught.\r\n\r\nI must see to it that my best couriers get through the enemy lines and warn him off. Hearthglen must prepare to dig in and rally the rest of the Crusade.\r\n\r\nWith any luck they\'ll get through before nightfall.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3089', 'There\'s been no word from any of my couriers this morning. It\'s clear that none of them made it through to Hearthglen. The Plaguelands are lost. Pureblood will come with his forces and they\'ll be annihilated out in the open.\r\n\r\nThis afternoon I received a vision from the Light. In it, I saw the utter destruction of everything that we\'ve built here. The message was clear - I was being told to take the remainder of the most faithful and abandon the Crusade to their doom.\r\n\r\nLandgren later told me that he\'d received the same vision. I cannot comprehend how the Light would tell us to take such a dishonorable action, but it is not my place to question - I am to obey, and obey I shall.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3090', 'It was with trepidation that I gazed upon New Avalon, I suspect for the last time. Our fate lies in Northrend. I am filled with a sense of ominous foreboding for some reason. The mission ahead should shake away these concerns. I will put them out of my mind.\r\n\r\nPerhaps with luck, High Commander Pureblood will somehow endure and marshal the survivors. I\'m a coward - a dog running away with my tail tucked between my legs!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3091', 'Two months they tell me that this journey is going to take. The other ships aren\'t built for speed like the Folly. They\'re carrying most of our forces and equipment, and they\'re not much more than single sail freighters, but they\'ll get there safely.\r\n\r\nI\'m not looking forward to this, but for the Light I will endure the sea sickness. I simply mustn\'t let the others see.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3092', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<H1 align=\"center\">The Diary of High General Abbendis</H1>\r\n<BR/>\r\n<IMG src=\"InterfaceFlavorImagesScarletCrusadeLogo\"/>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3093', 'I\'ve not written in a while. It\'s as much as I can do to stand upright and not get sick. The men are beginning to wonder why I keep myself holed up in my cabin most of the time. It wouldn\'t be good for morale for them to see me this way.\r\n\r\nIt shouldn\'t be long now. I pray that it won\'t be. Six weeks already and every day it seems that the weather gets worse. I can only hope that Northrend itself does not have such horrible weather. I wasn\'t built for the cold.\r\n\r\nThe Holy Light has been silent for a very long time.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3094', 'We were attacked with no warning whatsoever! Giants in massive oared longships came out of the mist like ghosts! They were silent as the dead.\r\n\r\nI lost a ship and all of the men on it. We fought with great courage and skill considering that we have only the most rudimentary naval combat training.\r\n\r\nAfterward, the screams of our men who\'d been taken captive by the giants echoed across the water. After a while it grew silent again. Bishop Street led the men in prayers.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3095', 'I awoke this morning in the middle of making adjustments to my maps. The Light was guiding my hand, showing me exactly where we must go.\r\n\r\nWe are close!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3096', 'Landfall!\r\n\r\nExiting the rowboat upon the beach, I planted our banner and was overcome by the Holy Light, which spoke through me. Today is the Crimson Dawn - the great day that we\'ve waited for. This is to be the site of New Hearthglen. We are no longer the Scarlet Crusade. We are now the Scarlet Onslaught!\r\n\r\nAnd an onslaught upon Northrend we shall be! The cancer of the Scourge threatens to overflow in the crown of the world and drown the rest of us. The time has come to take the fight directly to the Lich King\'s doorstep!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3097', 'It\'s been almost a month now and construction has proceeded apace. I\'ve been too busy to write. My scouts tell me that this land is full of dragons and other strange beasts. We\'ll keep to ourselves until we\'re ready.\r\n\r\nIn the middle of services today, the high abbot proclaimed that a visitor would arrive soon - one that the Light was sending to lead us to victory.\r\n\r\nI don\'t know how I feel about that. Why didn\'t the Light tell me? Have I not served faithfully? Now I\'m to be replaced by some outsider?', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3098', 'Admiral Barean Westwind showed up on my doorstep tonight! By all accounts he\'d died upon these shores an age ago.\r\n\r\nHe didn\'t look old enough and yet I knew that it was him. He claimed that he was the only survivor of his doomed fleet and that he\'d survived only through the good graces of the Light.\r\n\r\nWe stayed up speaking through the night and into the morning. He assured me that he had no intention of taking my place, but that the Light had instructed him to travel across the Great Dragonblight to serve as my advisor and commander in title only. He claimed that great change was coming to Northrend. The Alliance and the Horde were going to come en masse in response to a great plague that the Lich King was about to unleash upon them.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3099', 'The men have taken to the grand admiral with great zeal, especially High Abbot Landgren and Bishop Street. Apparently the Holy Light whispered a new blessing to the admiral in his sleep, which he passed on to Landgren. Some of the men have converted to the priesthood and are now being called \"raven priests\".\r\n\r\nOnly Jordan seems unimpressed. I suppose that\'s understandable. He probably feels his position is threatened if mine is.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3100', 'Something doesn\'t seem right. I can\'t put my finger on it, but I cannot make myself fully trust the admiral. He\'s done nothing wrong. Quite the contrary! And yet, I have to trust my gut.\r\n\r\nI will pray for understanding. The Light delivered the admiral to lead us to victory and it is not my place to question its decisions. I will continue to obey. I am faithful.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3101', 'Another couple of months have passed. We\'ve made great strides in the construction of New Hearthglen. The wall is almost complete as is the barracks. Kaleiki\'s men are miracle workers.\r\n\r\nMy heart hasn\'t been into writing. I\'ve been avoiding putting my thoughts here for some reason. The Light hasn\'t visited me of late, though the admiral assures me that this is nothing to worry about.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3102', 'My men report that a small group of Forsaken have broken ground on a camp along the hillside to the north. On the other side of the hill, the Alliance have apparently begun the construction of a much larger base.\r\n\r\nThe admiral says that we should leave them be. There are other Horde forces gathered to the west that would surely come to their aid if we were to attack. I don\'t feel good about it, but I see the logic in his reasoning.\r\n\r\nThe first phase of construction is done. Admiral Westwind has ordered a group of my men to establish a toehold further to the north. He wouldn\'t go into detail, claiming that he was being \"guided\" to do so.\r\n\r\nWe caught four spies from the Forsaken town, Venomspite, this afternoon. I\'m going to have LeCraft torture them for information. If we only caught four, how many more have slipped in amongst us?\r\n\r\nWhy do I feel as if things are beginning to unravel?', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3284', '<The majority of the book\'s pages are missing or soiled beyond recovery. A few of the early entries and the name \'Darius Orion\' are still legible.>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3285', 'Dec. 2 of the Year 20\r\n\r\nThe cold this year has been particularly cruel. I find that no matter how many layers I wear under my robes, the frost cuts deep into my bones. The masters say that it will pass but I have trouble believing that at times.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3286', 'A small group of workers from an outlying farm near Andorhal were brought in this morning and taken to the infirmary within the Violet Citadel. I do not know much about what ails them but I overheard one of them complaining about weakness and nausea. I hope the masters choose me to aid them in working to treat the farmers.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3287', '2 hours later\r\n\r\nWhat luck! One of the masters informed me that I will be working with the men who were brought in from Andorhal earlier. I intend to make good use of this opportunity to show how far I have come in my studies.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3288', '6 hours later\r\n\r\nThis is not turning out to be quite as glorious as I had planned. The farmers\' conditions have deteriorated quickly in their short time here. One has developed irritated boils covering large portions of his skin. I have treated the most seriously afflicted with some of the most powerful herbs and poultices available in the citadel. There should be some noticeable improvement within the next few hours.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3289', '10 hours later\r\n\r\nThe situation has quickly gotten out of control. All of the subjects are now covered in lesions which leak yellow-green fluid. All but a few are unconscious at this point. The few that are awake alternate between complaints of paralyzing numbness and burning aches in their joints. All of the subjects are showing a milky coloring in their eyes.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3290', 'Our attempts to slow the progress of the illness have been unsuccessful. Word has spread among the apprentices that this disease may be magical in nature; the masters were quick to quash the rumors. Many of the other apprentices that I had been working with on this project have excused themselves for fear that the disease is contagious and capable of penetrating our personal wards.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3291', 'I excused myself to take a walk out on one of the terraces to clear my thoughts. I could not have been outside for more than a couple minutes when I had to run back inside. My hands and feet had gone numb and my nose and ears were showing symptoms of mild exposure to frostbite. I will have to be more careful next time.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3292', '13 hours later\r\n\r\nLight save us.\r\n\r\nThe subjects have begun severe hemorrhaging from the eyes and ears. The masters believe they will all be dead within the hour. All attempts to halt the progress of the disease have failed. I have done what I can to ease their pain. I only hope that we can learn what we need from them after their deaths. No one should suffer as these men have suffered. I plan to stay with them until the end.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3293', 'To make matters worse one of the guards found the body of a first year apprentice in the gardens. It has not been confirmed yet but the rumors are that he froze to death. I do not think it has been this cold here in Dalaran in decades, if ever.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3294', '18 hours later\r\n\r\nThe farmers from Andorhal are dead. I thought they would finally be at peace but I was wrong. Only a few minutes after the last one breathed his last breath something horrifying happened. Their disease riddled corpses somehow animated and began clawing at myself and the other mages in the infirmary. Once the initial confusion passed one of the masters organized a force to deal with the animated bodies. The undead quickly fell to our combined arcane might.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3295', 'There were minimal casualties. A few of the apprentices had been scratched; one had a nasty bite that required stitches. Shortly afterward guards arrived and escorted myself and the other apprentices to our rooms. I tried to learn what was going on but they would not answer me. As we were being led through the halls to our rooms we passed by several troupes of guards with weapons drawn and readied. Is this what war feels like?\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3296', 'After I was brought to my room I was told to lock the door and not come out until I was told by a master personally that it was safe to leave. What is going on? Has the world gone mad?\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3297', '24 hours later\r\n\r\nI have been awake for over twenty-four hours now. I am exhausted but my room is too cold to sleep. I managed to complete a sending and contact a couple of the other apprentices who are trapped in their rooms. Through them I was able to learn what has the citadel on high alert. The masters are organizing search parties to locate the necromancer who was responsible for animating the corpses of the diseased farmers.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3298', 'There has not been a practicing necromancer in Dalaran since Kel\'Thuzad. I hope the monster responsible for the horrors I have seen today is brought to justice soon.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3299', '38 hours later\r\n\r\nThe quarantine has been lifted and we apprentices are finally allowed to leave our rooms. The necromancer responsible for animating the dead farmers was never caught but the masters are confident that he is not within the citadel or anywhere near Dalaran.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3300', 'We have resumed our efforts to learn more about the disease. Work has been slow due to most of the bodies having been obliterated. The masters have called Lady Jaina Proudmoore to aid in our research. She will teleport here by the end of the day. I hear she is quite beautiful; hopefully working with Lady Proudmoore will help me keep my mind off this damned cold.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3301', '<The journal is too badly damaged to read beyond this point.>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3302', 'Ask The Mage!\r\n\r\nQ: My friends tell me that studying magic and becoming a mage will make me frail, weak, and unpopular with the ladies. Is it true?\r\n-Jimmy \"Insecure\" Townsend', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3303', 'A: Don\'t worry, Jimmy. Your friends are referring to an unfair stereotype that started as a result of a few lazy mages with poor eating habits. It is entirely possible to maintain peak physical condition while still finding time to master manipulating the fabric of the universe. Even members of the Kirin Tor, through strict diet and a robust exercise regime, keep themselves in great shape despite their busy schedules. And what woman does not like Polymorph?\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3304', 'Q: My parents are not supportive of my decision to become a mage. They say that arcane magic is dangerous and leads to other \"harder\" magics like demonology. I do not want to sell my soul. Are they right?\r\n-Joseph Winters\r\n\r\nA: Your parents\' concern is unfounded. While there are stories of mages turning to demon worship or necromancy there has been little to no evidence supporting these claims. Reassure your family that absolutely nothing can possibly go wrong.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3305', 'Q: I just recently started studying the arcane and so far my friends just laugh at me. All they seem to want me around for is to conjure food and drinks and teleport them around. When will people start respecting me?\r\n-Gregory \"Doormat\" Adams\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3069', '$N,\r\n\r\nIf you are reading this letter then you are alive and in one piece - or at least you still have your eyes.\r\n\r\nI must apologize for the secrecy. Agmar demands that all mail is read before delivery - too many traitors and thieves there, as the wanted poster no doubt displays. There are delicate matters herein that could easily be misconstrued by the new guard.\r\n\r\nFor a soldier of the Horde, loss is absolute. Loss means death and there is no negotiation or interpretation with death. One can only hope that the manner of their death was honorable.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3070', 'But victory... Victory can mean many things. As you have probably noticed, the Kor\'kron are there in full force. The Warchief has sent his elite guard to help secure victory in Northrend. They, along with you and other heroes, are pushing the Lich King and his forces towards an inevitable conclusion. With each challenge you overcome, we are one step closer to ridding our world of Arthas and the Scourge.\r\n\r\nAnd therein lays the dilemma. For you see, our forces in Northrend work under the auspices of young Hellscream. Each victory bolsters the morale of the Horde forces here, which carries through to the rest of Azeroth.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3071', 'It is unfortunate, then, that Hellscream employs such savage tactics. As victory approaches, Hellscream gains further justification for his methods, which in turn brings us closer to a place we have not been in many years: a dark place.\r\n\r\nI have sent my son to command our forces at the Wrathgate. I know that he will battle with honor and I remain hopeful that his courage and tenacity will be noticed and emulated by our forces. He is my heart and strength in a place that I cannot be... You will be my eyes and ears. Together, we will make it right.\r\n\r\nBlood and Thunder... May your arrival bring them both.\r\n\r\nSaurfang', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3109', 'Pa set me taking care of Ol\' Bess again. I think he likes watching her try to kick me to pieces. It\'s not my fault that she doesn\'t like me as much as the others - it was only the once that I ate the apple intended for her. I don\'t even know how she knew.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3543', 'Years and years I have been locked away in this magical prison.  Tonight, I make my escape.  I have everything I need to make my break.\r\n\r\n<Water smeared ink has ruined this section of the letter>\r\n\r\nWhy have I been here so long?  What did I do to deserve this?  Days and days of these thoughts running through my head.  What could someone do deserve this?\r\n\r\n<Water smeared ink has ruined this section of the letter>\r\n\r\nPerhaps I will find out in another life.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3054', 'Long had the tale of the Horn of Elemental Fury been told at the campfires of the taunka. Generations of warriors grew up hearing the stories and sought the horn\'s hiding place in vain. Like the others, mighty Stormhoof vowed to seek the horn, leaving behind his village and his family.\r\n\r\nBraving the biting cold and fierce elementals of Frostblade Peak, Stormhoof climbed the mountain alone. Upon reaching the summit, Stormhoof was greeted by the North Wind itself. Knowing the wind would not give up the horn without a fight, the mighty warrior issued his challenge.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3055', 'For five days, Stormhoof struggled against the wind. As the sixth day dawned, he banished the wind from Frostblade Peak and took possession of the horn. Battered and injured, Stormhoof departed the mountain for his village.\r\n\r\nThe North Wind gathered a host of lesser elementals and attacked Stormhoof as he neared home. The weakened warrior could not defend himself against the onslaught and the North Wind took its revenge. It broke the horn into two pieces, encased them in shiny baubles, and gave them to two powerful gorloc chieftains as tokens of their authority.\r\n\r\n<The original text ends abruptly, but someone, perhaps Windtamer Barah, seems to have made an addendum below.>\r\n\r\nAs they were instructed by the North Wind, the gorloc chieftains passed down these precious relics to their sons and grandsons. To this day, the horn\'s pieces remain with Chieftain Burblegobble and Chieftain Gurgleboggle.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3038', 'Forboding runes are carved on the side of this crate...', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3143', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>There\'s been no word from any of my couriers this morning. It\'s clear that none of them made it through to Hearthglen. The Plaguelands are lost. Pureblood will come with his forces and they\'ll be annihilated out in the open.</P>\r\n<BR/>\r\n<P>This afternoon I received a vision from the Light. In it, I saw the utter destruction of everything that we\'ve built here. The message was clear - I was being told to take the remainder of the most faithful and abandon the Crusade to their doom.</P>\r\n<BR/>\r\n<P>Landgren later told me that he\'d received the same vision. I cannot comprehend how the Light would tell us to take such a dishonorable action, but it is not my place to question - I am to obey, and obey I shall.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3144', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>It was with trepidation that I gazed upon New Avalon, I suspect for the last time. Our fate lies in Northrend. I am filled with a sense of ominous foreboding for some reason. The mission ahead should shake away these concerns. I will put them out of my mind.</P>\r\n<BR/>\r\n<P>Perhaps with luck, High Commander Pureblood will somehow endure and marshal the survivors. I\'m a coward - a dog running away with my tail tucked between my legs!</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3145', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Two months they tell me that this journey is going to take. The other ships aren\'t built for speed like the Folly. They\'re carrying most of our forces and equipment, and they\'re not much more than single sail freighters, but they\'ll get there safely.</P>\r\n<BR/>\r\n<P>I\'m not looking forward to this, but for the Light I will endure the sea sickness. I simply mustn\'t let the others see.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3146', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>I\'ve not written in a while. It\'s as much as I can do to stand upright and not get sick. The men are beginning to wonder why I keep myself holed up in my cabin most of the time. It wouldn\'t be good for morale for them to see me this way.</P>\r\n<BR/>\r\n<P>It shouldn\'t be long now. I pray that it won\'t be. Six weeks already and every day it seems that the weather gets worse. I can only hope that Northrend itself does not have such horrible weather. I wasn\'t built for the cold.</P>\r\n<BR/>\r\n<P>The Holy Light has been silent for a very long time.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3147', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>We were attacked with no warning whatsoever! Giants in massive oared longships came out of the mist like ghosts! They were silent as the dead.</P>\r\n<BR/>\r\n<P>I lost a ship and all of the men on it. We fought with great courage and skill considering that we have only the most rudimentary naval combat training.</P>\r\n<BR/>\r\n<P>Afterward, the screams of our men who\'d been taken captive by the giants echoed across the water. After a while it grew silent again. Bishop Street led the men in prayers.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3148', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>I awoke this morning in the middle of making adjustments to my maps. The Light was guiding my hand, showing me exactly where we must go.</P>\r\n<BR/>\r\n<P>We are close!</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3149', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Landfall!</P>\r\n<BR/>\r\n<P>Exiting the rowboat upon the beach, I planted our banner and was overcome by the Holy Light, which spoke through me. Today is the Crimson Dawn - the great day that we\'ve waited for. This is to be the site of New Hearthglen. We are no longer the Scarlet Crusade. We are now the Scarlet Onslaught!</P>\r\n<BR/>\r\n<P>And an onslaught upon Northrend we shall be! The cancer of the Scourge threatens to overflow in the crown of the world and drown the rest of us. The time has come to take the fight directly to the Lich King\'s doorstep!</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3150', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>It\'s been almost a month now and construction has proceeded apace. I\'ve been too busy to write. My scouts tell me that this land is full of dragons and other strange beasts. We\'ll keep to ourselves until we\'re ready.</P>\r\n<BR/>\r\n<P>In the middle of services today, the high abbot proclaimed that a visitor would arrive soon - one that the Light was sending to lead us to victory.</P>\r\n<BR/>\r\n<P>I don\'t know how I feel about that. Why didn\'t the Light tell me? Have I not served faithfully? Now I\'m to be replaced by some outsider?</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3151', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Admiral Barean Westwind showed up on my doorstep tonight! By all accounts he\'d died upon these shores an age ago.</P>\r\n<BR/>\r\n<P>He didn\'t look old enough and yet I knew that it was him. He claimed that he was the only survivor of his doomed fleet and that he\'d survived only through the good graces of the Light.</P>\r\n<BR/>\r\n<P>We stayed up speaking through the night and into the morning. He assured me that he had no intention of taking my place, but that the Light had instructed him to travel across the Great Dragonblight to serve as my advisor and commander in title only. He claimed that great change was coming to Northrend. The Alliance and the Horde were going to come en masse in response to a great plague that the Lich King was about to unleash upon them.</P>\r\n</BODY>\r\n</HTML>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3152', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>The men have taken to the grand admiral with great zeal, especially High Abbot Landgren and Bishop Street. Apparently the Holy Light whispered a new blessing to the admiral in his sleep, which he passed on to Landgren. Some of the men have converted to the priesthood and are now being called \"raven priests\".</P>\r\n<BR/>\r\n<P>Only Jordan seems unimpressed. I suppose that\'s understandable. He probably feels his position is threatened if mine is.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3153', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Something doesn\'t seem right. I can\'t put my finger on it, but I cannot make myself fully trust the admiral. He\'s done nothing wrong. Quite the contrary! And yet, I have to trust my gut.</P>\r\n<BR/>\r\n<P>I will pray for understanding. The Light delivered the admiral to lead us to victory and it is not my place to question its decisions. I will continue to obey. I am faithful.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3154', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>Another couple of months have passed. We\'ve made great strides in the construction of New Hearthglen. The wall is almost complete as is the barracks. Kaleiki\'s men are miracle workers.</P>\r\n<BR/>\r\n<P>My heart hasn\'t been into writing. I\'ve been avoiding putting my thoughts here for some reason. The Light hasn\'t visited me of late, though the admiral assures me that this is nothing to worry about.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3155', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>My men report that a small group of Forsaken have broken ground on a camp along the hillside to the north. On the other side of the hill, the Alliance have apparently begun the construction of a much larger base.</P>\r\n<BR/>\r\n<P>The admiral says that we should leave them be. There are other Horde forces gathered to the west that would surely come to their aid if we were to attack. I don\'t feel good about it, but I see the logic in his reasoning.</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3156', '<HTML>\r\n<BODY>\r\n<BR/>\r\n<P>The first phase of construction is done. Admiral Westwind has ordered a group of my men to establish a toehold further to the north. He wouldn\'t go into detail, claiming that he was being \"guided\" to do so.</P>\r\n<BR/>\r\n<P>We caught four spies from the Forsaken town, Venomspite, this afternoon. I\'m going to have LeCraft torture them for information. If we only caught four, how many more have slipped in amongst us?</P>\r\n<BR/>\r\n<P>Why do I feel as if things are beginning to unravel?</P>\r\n</BODY>\r\n</HTML>\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3226', 'Welcome to the Scourge. Now that you have entered your true service, you may find immense relief in your liberation from various mortal inconveniences. No bothersome sleeping to take up your time anymore, nor any great need for shelter from the elements, and you are even free from the mild irritation of cutting hair and nails! How delightful!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3227', 'However, there are a few, almost insignificant, details to being undead:\r\nKeep an eye on your rotting. Some is all very well to scare your foes, but an arm falling off mid-stab is just embarrassing.\r\nLearn to love your stench. Some say foul, we say rawrr.\r\nAvoid living animals, especially dogs.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3228', 'Heed your unintelligibility. Should you misplace your lower jaw, you may be reassigned to a position where your gurgling is not amiss. And few of those hold any prestige.\r\nAlcohol is simply not the same. My apologies.\r\nA bloodbath a day keeps the living away (although adding bubbles is frowned upon). \r\nAvoid pastels. They simply do not work with our coloring.\r\nThose pants will no longer make you look fat.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3306', 'Some have accused mages of being an insular, self-centered group. We would like you to know that this could not be further from the truth. Mages are caring individuals whose relentless pursuit of knowledge and power is centered on your well-being! How do mages serve you? I\'m glad you asked!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3307', 'Nervous at a public speaking engagement? Imagining your audience in their underwear not helping? What if you imagine them as sheep? Still nothing? We will turn your audience into sheep.\r\n\r\nAsk your mage friend to cast a slowing charm on you and take the time to stop and smell the roses. Also fun at races!\r\n\r\nTeleportation is the chicest way to get around. Nothing ruins your second date chances faster than an acute case of N.O. (Netherdrake Odor).\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3308', 'A friend of a mage never hungers or thirsts. Summoned food has no transmuted fats!\r\n\r\nThink a frosted mug is the best way to keep your ale cool? You should see a frostbolted mug.\r\n\r\nStudies show that most master works of literature have been written while the author was under the influence of arcane intellect.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3309', 'Have a white Winter Veil every year when mages summon blizzards for you. (Warning: standing outside during an arcane blizzard may cause slight tingling in the fingers and painful death.)\r\n\r\nAnd always remember: If anything should go astray, a mage is just a blink away.\r\n\r\nThis pamphlet was printed on summoned paper.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3358', '<Penned by the skilled hand of Archmage Ansirem Runeweaver>', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3359', 'The wondrous gift of magic can be bent to many purposes. We the learned have found that it is easiest to divide the eldritch arts into eight different categories in order to further understand the fundamentals of magic itself. Each of these categories is known as a 锟絪chool锟?of magic, for they are often learned separately and mages frequently choose to specialize in one type or another. Other mages attempt to master all forms of magic, but few have achieved this lofty goal. Within this tome we will discuss each individual school of magic and some of their notable practitioners.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3360', 'All schools of magic can be combined to create formidable spells that utilize multiple sources of power. Some of these potent spells are discussed later in this series of books, but beware, combining multiple types of magic can be extremely dangerous - especially for beginners.\r\n\r\n	Here you will learn the basics of all schools of magic. Please remember that magic is not to be abused. Do not practice magic without the appropriate supervision.', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3393', 'By Ilustriel Greenleaf\r\n\r\nStop! Think about what you\'re doing. Is arcane magic really right for you? \r\n\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3394', 'Facts:\r\n- Magic is a dangerous force that leads to the deaths of millions each year.\r\n- Arcane magic is highly addictive. Once you start practicing magic, it\'s almost impossible to stop.\r\n- Learning simple arcane spells can seem innocent at first, but it\'s often a gateway to harder and harder spells. In the worst cases, you could even end up addicted to fel magic.\r\n- Casting magic during childbirth can cause significant risks to the health of your children.\r\n- Magic abuse can lead to violence and necromancy.\r\n- Arcane magic is not cool. ', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3395', 'That\'s why we\'ve started the Magic Resistance Abuse Education (M.A.R.E.) program to educate people everywhere about the dangers of magic abuse. Our mission is to show the world that it\'s okay to live without magic. You can find fun elsewhere, like with flowers or basketweaving. Doesn\'t that sound exciting?', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3396', 'In addition, our program helps recovering magic addicts re-integrate themselves into society. In the nearly 6 days of our existence, our program has established a very strong relationship with the Priestess of Elune and the Cenarion Circle. These organizations are capable and willing to help you get on the path to a speedy recovery. Remember, it\'s never too late to learn to love yourself!', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3397', 'Contemporary Azeroth is full of unique challenges that make it very difficult to maintain a safe and magic-free lifestyle. We\'re here to help you find that safe, comfortable path. Dozens of people have already benefited from our lifestyle enriching magic-alternatives. Won\'t you be the next?', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3497', 'So You Think You Can Cast: Frequently Asked Questions from First-Time Mages:\r\n\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3502', 'Q: I\'m having trouble casting any spells at all.  What can I do?\r\n\r\nA: You may be suffering from dehydration.  If you\'re having trouble casting a spell that you know you know, try drinking a glass of water!  Proper hydration is critical to effective spellcasting.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3503', 'Q: I can\'t cast fireblast!  I just cast it a couple seconds ago and now it\'s not working.  Help!\r\n\r\nA: Having trouble casting a spell?  Did you just cast it a few seconds ago?  Some spells just won\'t work twice in quick succession.  Try mixing in a few different spells while you\'re waiting.\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3504', 'Q: I peeked at my friend\'s spellbook in the locker room, and he already has way more spells than I do!  What\'s wrong with me?\r\n\r\nA: First of all, this is COMPLETELY NORMAL.  Be sure to talk to your trainer regularly.  He or she may be able to teach you some new incantations.  If not, you probably just need more practice with the spells you already know.  Be patient, you\'ll get your spells!\r\n', null, null, null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3505', 'Q: My co-workers all seem to be doing more damage than I do.  How can I catch up with them without looking like a dufus?\r\n\r\nA: It\'s not all about damage.  You can aid your group in other ways - Arcane Refreshment spells are a good example.  If that doesn\'t pan out, perhaps a career in the arcane arts isn\'t the right career path for you.  We hear that hunting is easy!\r\n', null, null, null, null, null, null, null);
